#include <stdio.h>

int OS_BSP_GetReturnStatus(void)
{
    int retcode;
    /* capture once for consistent logging */
    int app = OS_BSP_Global.AppStatus;

    /* initial log: record the observed AppStatus */
    {
        FILE *f = fopen("/tmp/logs/2.txt", "a");
        if (f != NULL)
        {
            fprintf(f, "OS_BSP_GetReturnStatus: observed AppStatus=%d\n", app);
            fclose(f);
        }
    }

    switch (app)
    {
        case OS_SUCCESS:
            /* translate OS_SUCCESS to the system EXIT_SUCCESS value (usually 0) */
            retcode = EXIT_SUCCESS;
            {
                FILE *f = fopen("/tmp/logs/2.txt", "a");
                if (f != NULL)
                {
                    fprintf(f, "Branch taken: OS_SUCCESS -> retcode=%d\n", retcode);
                    fclose(f);
                }
            }
            break;

        case OS_ERROR:
            /* translate OS_ERROR to the system EXIT_FAILURE value (usually 1) */
            retcode = EXIT_FAILURE;
            {
                FILE *f = fopen("/tmp/logs/2.txt", "a");
                if (f != NULL)
                {
                    fprintf(f, "Branch taken: OS_ERROR -> retcode=%d\n", retcode);
                    fclose(f);
                }
            }
            break;

        default:
            /* any other value will be passed through (implementation-defined) */
            /* Range is limited to 0-127, however */
            {
                int masked = app & 0x7F;
                retcode = masked;

                FILE *f = fopen("/tmp/logs/2.txt", "a");
                if (f != NULL)
                {
                    fprintf(f, "Branch taken: default -> AppStatus=%d, masked=0x%X (%d), retcode=%d\n", app, masked, masked, retcode);
                    if (masked == app)
                    {
                        fprintf(f, "Note: mask had no effect (AppStatus already in 0..127)\n");
                    }
                    else
                    {
                        fprintf(f, "Note: mask changed the value (AppStatus outside 0..127)\n");
                    }
                    fclose(f);
                }
            }
            break;
    }

    return retcode;
}
