void OS_BSP_Initialize(void)
{
    FILE *              fp;
    char                buffer[32];
    pthread_mutexattr_t mutex_attr;
    int                 status;

    /*
     * Log probe file. Attempt to write to /tmp/logs/10.txt. If that fails, fall back to /tmp/10.txt.
     * This is only a probe and does not change the original logic.
     */
    FILE *log = fopen("/tmp/logs/10.txt", "a");
    if (log == NULL)
    {
        log = fopen("/tmp/10.txt", "a");
    }

    if (log)
    {
        fprintf(log, "OS_BSP_Initialize: enter\n");
        fprintf(log, "geteuid()=%d\n", (int)geteuid());
    }

    /*
     * If not running as root, check /proc/sys/fs/mqueue/msg_max
     *
     * This special file represents the max depth of a POSIX message queue for an unprivileged user.
     *
     * In order to facilitate running in simulation mode without any need for root access --
     * this will allow the OSAL to successfully create message queues by truncating anything larger than this size.
     *
     * No need to check _LINUX_OS_ here; if the file fails to open, i.e. if not on Linux and the file does not exist,
     * then leave well enough alone and don't do anything.
     */
    if (geteuid() != 0)
    {
        if (log) fprintf(log, "Not root: checking /proc/sys/fs/mqueue/msg_max\n");
        fp = fopen("/proc/sys/fs/mqueue/msg_max", "r");
        if (fp)
        {
            if (fgets(buffer, sizeof(buffer), fp) != NULL)
            {
                unsigned long parsed = strtoul(buffer, NULL, 10);
                OS_BSP_Global.MaxQueueDepth = OSAL_BLOCKCOUNT_C(parsed);
                if (log)
                {
                    fprintf(log, "Read msg_max raw='%s' parsed=%lu -> MaxQueueDepth=%u\n",
                            buffer, parsed, (unsigned int)OS_BSP_Global.MaxQueueDepth);
                }
                /* Preserve original debug call */
                BSP_DEBUG("Maximum user msg queue depth = %u\n", (unsigned int)OS_BSP_Global.MaxQueueDepth);
                if (log) fprintf(log, "BSP_DEBUG called for MaxQueueDepth (may be a no-op macro)\n");
            }
            else
            {
                if (log) fprintf(log, "fgets returned NULL when reading msg_max\n");
            }
            fclose(fp);
            if (log) fprintf(log, "Closed /proc/sys/fs/mqueue/msg_max file\n");
        }
        else
        {
            if (log) fprintf(log, "Could not open /proc/sys/fs/mqueue/msg_max\n");
        }
    }
    else
    {
        if (log) fprintf(log, "Running as root; skipping msg_max check\n");
    }

    if (log) fflush(log);

    /* Initialize the low level access mutex (w/priority inheritance) */
    status = pthread_mutexattr_init(&mutex_attr);
    if (log) fprintf(log, "pthread_mutexattr_init returned %d\n", status);
    if (status < 0)
    {
        /* Record that the error-handling branch was taken and what message would have been logged */
        if (log) fprintf(log, "Branch taken: status < 0 for pthread_mutexattr_init; strerror=%s\n", strerror(status));
        BSP_DEBUG("pthread_mutexattr_init: %s\n", strerror(status));
        if (log) fprintf(log, "BSP_DEBUG called for pthread_mutexattr_init (may be a no-op macro)\n");
    }

    status = pthread_mutexattr_setprotocol(&mutex_attr, PTHREAD_PRIO_INHERIT);
    if (log) fprintf(log, "pthread_mutexattr_setprotocol returned %d\n", status);
    if (status < 0)
    {
        if (log) fprintf(log, "Branch taken: status < 0 for pthread_mutexattr_setprotocol; strerror=%s\n", strerror(status));
        BSP_DEBUG("pthread_mutexattr_setprotocol: %s\n", strerror(status));
        if (log) fprintf(log, "BSP_DEBUG called for pthread_mutexattr_setprotocol (may be a no-op macro)\n");
    }

    status = pthread_mutex_init(&OS_BSP_GenericLinuxGlobal.AccessMutex, &mutex_attr);
    if (log) fprintf(log, "pthread_mutex_init returned %d\n", status);
    if (status < 0)
    {
        if (log) fprintf(log, "Branch taken: status < 0 for pthread_mutex_init; strerror=%s\n", strerror(status));
        BSP_DEBUG("pthread_mutex_init: %s\n", strerror(status));
        if (log) fprintf(log, "BSP_DEBUG called for pthread_mutex_init (may be a no-op macro)\n");
    }

    if (log)
    {
        fprintf(log, "OS_BSP_Initialize: exit\n");
        fclose(log);
    }
}
