#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <dirent.h>

/* Probe helper to log messages to /tmp/logs/747.txt */
static void os_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/747.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fputc('\n', f);
    va_end(ap);

    fclose(f);
}

int32 OS_DirRead_Impl(const OS_object_token_t *token, os_dirent_t *dirent)
{
    struct dirent *                de;
    OS_impl_dir_internal_record_t *impl;

    os_log_probe("OS_DirRead_Impl: entry token=%p dirent=%p", (const void *)token, (void *)dirent);

    impl = OS_OBJECT_TABLE_GET(OS_impl_dir_table, *token);
    os_log_probe("OS_DirRead_Impl: resolved impl=%p", (void *)impl);

    /* NOTE - the readdir() call is non-reentrant ....
     * However, this is performed while the global dir table lock is taken.
     * Therefore this ensures that only one such call can occur at any given time.
     *
     * Static analysis tools may warn about this because they do not know
     * this function is externally serialized via the global lock.
     */
    /* cppcheck-suppress readdirCalled */
    /* cppcheck-suppress nonreentrantFunctionsreaddir */

    os_log_probe("OS_DirRead_Impl: calling readdir() with dp=%p", (void *)(impl ? impl->dp : NULL));
    de = readdir(impl->dp);
    os_log_probe("OS_DirRead_Impl: readdir() returned de=%p", (void *)de);

    if (de == NULL)
    {
        os_log_probe("OS_DirRead_Impl: readdir returned NULL, returning OS_ERROR");
        return OS_ERROR;
    }

    os_log_probe("OS_DirRead_Impl: de->d_name='%s'", de->d_name);

    strncpy(dirent->FileName, de->d_name, sizeof(dirent->FileName) - 1);
    dirent->FileName[sizeof(dirent->FileName) - 1] = 0;

    os_log_probe("OS_DirRead_Impl: copied FileName='%s', returning OS_SUCCESS", dirent->FileName);

    return OS_SUCCESS;
}
