#include <stdio.h>
#include <string.h>

int32 OS_DirRead_Impl(const OS_object_token_t *token, os_dirent_t *dirent)
{
    struct dirent *                de;
    OS_impl_dir_internal_record_t *impl;
    FILE *log = fopen("/tmp/logs/747.txt", "a");

    if (log != NULL)
    {
        fprintf(log, "OS_DirRead_Impl: entry token=%p, dirent=%p\n", (const void *)token, (void *)dirent);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_dir_table, *token);

    if (log != NULL)
    {
        /* Log impl and its DIR* handle (impl may be NULL in some failure scenarios) */
        fprintf(log, "OS_DirRead_Impl: impl=%p\n", (void *)impl);
        if (impl != NULL)
        {
            fprintf(log, "OS_DirRead_Impl: impl->dp=%p\n", (void *)impl->dp);
        }
    }

    /* NOTE - the readdir() call is non-reentrant ....
     * However, this is performed while the global dir table lock is taken.
     * Therefore this ensures that only one such call can occur at any given time.
     *
     * Static analysis tools may warn about this because they do not know
     * this function is externally serialized via the global lock.
     */
    /* cppcheck-suppress readdirCalled */
    /* cppcheck-suppress nonreentrantFunctionsreaddir */
    de = readdir(impl->dp);

    if (log != NULL)
    {
        fprintf(log, "OS_DirRead_Impl: readdir returned de=%p\n", (void *)de);
    }

    if (de == NULL)
    {
        if (log != NULL)
        {
            fprintf(log, "OS_DirRead_Impl: de is NULL -> returning OS_ERROR\n");
            fclose(log);
        }

        return OS_ERROR;
    }

    if (log != NULL)
    {
        /* Capture the name read from the dirent returned by readdir */
        fprintf(log, "OS_DirRead_Impl: de->d_name=\"%s\"\n", de->d_name);
    }

    strncpy(dirent->FileName, de->d_name, sizeof(dirent->FileName) - 1);
    dirent->FileName[sizeof(dirent->FileName) - 1] = 0;

    if (log != NULL)
    {
        fprintf(log, "OS_DirRead_Impl: copied FileName=\"%s\" -> returning OS_SUCCESS\n", dirent->FileName);
        fclose(log);
    }

    return OS_SUCCESS;
}
