#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe logging helper - append-only to avoid altering program behavior */
static void os_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1386.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);

    fclose(f);
}

int32 OS_FileSysAddFixedMap(osal_id_t *filesys_id, const char *phys_path, const char *virt_path)
{
    OS_filesys_internal_record_t *filesys;
    int32                         return_code;
    OS_object_token_t             token;
    const char *                  dev_name;

    /* Log entry and input state (print pointers and strings when safe) */
    os_log_probe("OS_FileSysAddFixedMap ENTER: filesys_id=%p, phys_path=%p, virt_path=%p",
                 (void *)filesys_id, (void *)phys_path, (void *)virt_path);
    if (phys_path != NULL)
    {
        os_log_probe("  phys_path='%s'", phys_path);
    }
    if (virt_path != NULL)
    {
        os_log_probe("  virt_path='%s'", virt_path);
    }

    /*
     * Validate inputs
     */
    OS_CHECK_POINTER(filesys_id);
    OS_CHECK_STRING(phys_path, sizeof(filesys->system_mountpt), OS_FS_ERR_PATH_TOO_LONG);
    OS_CHECK_PATHNAME(virt_path);

    /*
     * Generate a dev name by taking the basename of the phys_path.
     */
    dev_name = strrchr(phys_path, '/');
    if (dev_name == NULL)
    {
        dev_name = phys_path;
    }
    else
    {
        ++dev_name;
    }

    os_log_probe("  derived dev_name pointer=%p, name='%s'", (void *)dev_name, dev_name ? dev_name : "(null)");

    if (memchr(dev_name, 0, sizeof(filesys->volume_name)) == NULL)
    {
        os_log_probe("  memchr check failed: name too long -> returning OS_ERR_NAME_TOO_LONG");
        return OS_ERR_NAME_TOO_LONG;
    }

    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, dev_name, &token);
    os_log_probe("  OS_ObjectIdAllocateNew returned %ld, token=%ld", (long)return_code, (long)token);
    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, filesys, device_name, dev_name);

        strncpy(filesys->volume_name, dev_name, sizeof(filesys->volume_name) - 1);
        strncpy(filesys->system_mountpt, phys_path, sizeof(filesys->system_mountpt) - 1);
        strncpy(filesys->virtual_mountpt, virt_path, sizeof(filesys->virtual_mountpt) - 1);

        /* Log the record contents after initial fills (strings may not be NUL-terminated explicitly) */
        filesys->volume_name[sizeof(filesys->volume_name) - 1] = '\0';
        filesys->system_mountpt[sizeof(filesys->system_mountpt) - 1] = '\0';
        filesys->virtual_mountpt[sizeof(filesys->virtual_mountpt) - 1] = '\0';
        os_log_probe("  filesys record after strncpy: volume_name='%s', system_mountpt='%s', virtual_mountpt='%s'",
                     filesys->volume_name, filesys->system_mountpt, filesys->virtual_mountpt);

        /*
         * mark the entry that it is a fixed disk
         */
        filesys->fstype = OS_FILESYS_TYPE_FS_BASED;
        filesys->flags  = OS_FILESYS_FLAG_IS_FIXED;

        os_log_probe("  after marking fixed: fstype=%d, flags=0x%08x", (int)filesys->fstype, (unsigned)filesys->flags);

        /*
         * The "mount" implementation is required as it will
         * create the mountpoint if it does not already exist
         */
        return_code = OS_FileSysStartVolume_Impl(&token);
        os_log_probe("  OS_FileSysStartVolume_Impl returned %ld", (long)return_code);

        if (return_code == OS_SUCCESS)
        {
            filesys->flags |= OS_FILESYS_FLAG_IS_READY;
            os_log_probe("  marked IS_READY: flags=0x%08x", (unsigned)filesys->flags);
            return_code = OS_FileSysMountVolume_Impl(&token);
            os_log_probe("  OS_FileSysMountVolume_Impl returned %ld", (long)return_code);
        }

        if (return_code == OS_SUCCESS)
        {
            /*
             * mark the entry that it is a fixed disk
             */
            filesys->flags |= OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL;
            os_log_probe("  marked MOUNTED_SYSTEM|MOUNTED_VIRTUAL: flags=0x%08x", (unsigned)filesys->flags);
        }

        /* Check result, finalize record, and unlock global table. */
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, filesys_id);
        os_log_probe("  OS_ObjectIdFinalizeNew returned %ld, final filesys_id=%p", (long)return_code, (void *)(filesys_id ? *filesys_id : 0));
    }

    os_log_probe("OS_FileSysAddFixedMap EXIT: return_code=%ld", (long)return_code);

    return return_code;
}
