#include <stdio.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <errno.h>
#include <time.h>
#include <unistd.h>

int32 OS_FileSysStopVolume_Impl(const OS_object_token_t *token)
{
    /*
     * Preserve the original behaviour (no-op) but add non-intrusive logging
     * to help reproduce and gather evidence for the reported violation.
     * Any failure in logging is ignored so that the function remains a no-op
     * from the caller's perspective and still returns OS_SUCCESS.
     */

    /* Save errno and restore later to avoid changing caller-visible errno */
    int saved_errno = errno;

    /* Try to ensure the log directory exists. Ignore errors. */
    (void)mkdir("/tmp/logs", 0700);

    FILE *fp = fopen("/tmp/logs/330.txt", "a");
    if (fp != NULL)
    {
        /* Timestamp */
        time_t now = time(NULL);
        if (now != (time_t)-1)
        {
            struct tm *tm_info = localtime(&now);
            if (tm_info != NULL)
            {
                char buf[64];
                if (strftime(buf, sizeof(buf), "%Y-%m-%d %H:%M:%S", tm_info) > 0)
                {
                    fprintf(fp, "[%s] ", buf);
                }
                else
                {
                    fprintf(fp, "[time-error] ");
                }
            }
            else
            {
                fprintf(fp, "[time-error] ");
            }
        }
        else
        {
            fprintf(fp, "[time-error] ");
        }

        /* Log control path and minimal state */
        fprintf(fp, "OS_FileSysStopVolume_Impl invoked (no-op)\n");
        fprintf(fp, "    token pointer: %p\n", (const void *)token);
        fprintf(fp, "    pid=%ld uid=%ld gid=%ld\n",
                (long)getpid(), (long)getuid(), (long)getgid());

        /* Flush and close the log. Any errors here are ignored. */
        fclose(fp);
    }

    /* Restore errno to avoid affecting caller behaviour */
    errno = saved_errno;

    /* Original behaviour: unconditional success (no-op) */
    return OS_SUCCESS;
}
