#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <inttypes.h>
#include <string.h>

/* Probe logging helper - appends human-readable trace messages to the logfile. */
static void OS_FileSys_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1410.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_FileSys_Initialize(char *address, const char *fsdevname, const char *fsvolname, size_t blocksize,
                            osal_blockcount_t numblocks, bool should_format)
{
    OS_filesys_internal_record_t *filesys;
    int32                         return_code;
    OS_object_token_t             token;

    /* Log entry and parameter values */
    OS_FileSys_Log("OS_FileSys_Initialize ENTRY: fsdevname=\"%s\", fsvolname=\"%s\", address=%p, blocksize=%" PRIuPTR ", numblocks=%" PRIuPTR ", should_format=%d",
                   fsdevname ? fsdevname : "(null)", fsvolname ? fsvolname : "(null)", (void *)address,
                   (uintptr_t)blocksize, (uintptr_t)numblocks, (int)should_format);

    /*
     * Check parameters
     *
     * Note "address" is not checked, because in certain configurations it can be validly null.
     */
    OS_CHECK_STRING(fsdevname, sizeof(filesys->device_name), OS_FS_ERR_PATH_TOO_LONG);
    OS_CHECK_STRING(fsvolname, sizeof(filesys->volume_name), OS_FS_ERR_PATH_TOO_LONG);

    /* check names are not empty strings */
    if (fsdevname[0] == 0 || fsvolname[0] == 0)
    {
        OS_FileSys_Log("OS_FileSys_Initialize EXIT early: invalid path (empty string)");
        return OS_FS_ERR_PATH_INVALID;
    }

    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, fsdevname, &token);
    OS_FileSys_Log("OS_ObjectIdAllocateNew returned %d, token=%" PRIuPTR, return_code, (uintptr_t)token);

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, filesys, device_name, fsdevname);

        /* populate the VolumeName and BlockSize ahead of the Impl call,
         * so the implementation can reference this info if necessary */
        filesys->blocksize = blocksize;
        filesys->numblocks = numblocks;
        filesys->address   = address;
        strncpy(filesys->volume_name, fsvolname, sizeof(filesys->volume_name) - 1);

        /* Log internal state before determining fstype */
        OS_FileSys_Log("Post-init state: filesys=%p, device_name=\"%s\", volume_name=\"%s\", blocksize=%" PRIuPTR ", numblocks=%" PRIuPTR ", address=%p, fstype=%d",
                       (void *)filesys, filesys->device_name, filesys->volume_name,
                       (uintptr_t)filesys->blocksize, (uintptr_t)filesys->numblocks, (void *)filesys->address,
                       (int)filesys->fstype);

        /*
         * Determine basic type of filesystem, if not already known
         *
         * if either an address was supplied, or if the volume name
         * contains the string "RAM" then it is a RAM disk. Otherwise
         * leave the type as UNKNOWN and let the implementation decide.
         */
        if (filesys->fstype == OS_FILESYS_TYPE_UNKNOWN &&
            (filesys->address != NULL || strncmp(filesys->volume_name, OS_FILESYS_RAMDISK_VOLNAME_PREFIX,
                                                 sizeof(OS_FILESYS_RAMDISK_VOLNAME_PREFIX) - 1) == 0))
        {
            filesys->fstype = OS_FILESYS_TYPE_VOLATILE_DISK;
            OS_FileSys_Log("fstype inferred as VOLATILE_DISK for filesys=%p", (void *)filesys);
        }

        return_code = OS_FileSysStartVolume_Impl(&token);
        OS_FileSys_Log("OS_FileSysStartVolume_Impl returned %d for token=%" PRIuPTR, return_code, (uintptr_t)token);

        if (return_code == OS_SUCCESS)
        {
            /*
             * The "mkfs" call also formats the device.
             * this is the primary difference between mkfs and initfs.
             */
            if (should_format)
            {
                OS_FileSys_Log("should_format is true; calling OS_FileSysFormatVolume_Impl for token=%" PRIuPTR, (uintptr_t)token);
                return_code = OS_FileSysFormatVolume_Impl(&token);
                OS_FileSys_Log("OS_FileSysFormatVolume_Impl returned %d for token=%" PRIuPTR, return_code, (uintptr_t)token);
            }

            if (return_code == OS_SUCCESS)
            {
                filesys->flags |= OS_FILESYS_FLAG_IS_READY;
                OS_FileSys_Log("filesys marked READY: filesys=%p, flags=0x%08x", (void *)filesys, (unsigned)filesys->flags);
            }
            else
            {
                /*
                 * To avoid leaving in an intermediate state,
                 * this also stops the volume if formatting failed.
                 * Cast to void to repress analysis warnings for
                 * ignored return value.
                 */
                OS_FileSys_Log("Format failed (code=%d); calling OS_FileSysStopVolume_Impl for token=%" PRIuPTR, return_code, (uintptr_t)token);

                /* Log state immediately before calling StopVolume_Impl */
                OS_FileSys_Log("State BEFORE StopVolume: filesys=%p, device_name=\"%s\", volume_name=\"%s\", flags=0x%08x, address=%p",
                               (void *)filesys, filesys->device_name, filesys->volume_name, (unsigned)filesys->flags, (void *)filesys->address);

                (void)OS_FileSysStopVolume_Impl(&token);

                /* Log that StopVolume was invoked (even if it is a no-op) */
                OS_FileSys_Log("OS_FileSysStopVolume_Impl invoked for token=%" PRIuPTR, (uintptr_t)token);
            }
        }

        /* Check result, finalize record, and unlock global table. */
        OS_FileSys_Log("Finalizing new object with return_code=%d for token=%" PRIuPTR, return_code, (uintptr_t)token);
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, NULL);
        OS_FileSys_Log("OS_ObjectIdFinalizeNew returned %d for token=%" PRIuPTR, return_code, (uintptr_t)token);
    }

    OS_FileSys_Log("OS_FileSys_Initialize EXIT: return_code=%d", return_code);
    return return_code;
}
