#include <stdio.h>
#include <stdarg.h>
#include <errno.h>
#include <string.h>
#include <unistd.h>
#include <stddef.h>

/* Probe helper: append log lines to /tmp/logs/674.txt. This preserves errno. */
static void OS_GenericRead_Log(const char *fmt, ...)
{
    int saved_errno = errno;
    FILE *f = fopen("/tmp/logs/674.txt", "a");
    if (f != NULL)
    {
        va_list ap;
        va_start(ap, fmt);
        vfprintf(f, fmt, ap);
        va_end(ap);
        fclose(f);
    }
    errno = saved_errno;
}

int32 OS_GenericRead_Impl(const OS_object_token_t *token, void *buffer, size_t nbytes, int32 timeout)
{
    int32                           return_code;
    ssize_t                         os_result;
    uint32                          operation;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /* Log entry and key state so the control flow can be reproduced */
    OS_GenericRead_Log("ENTRY: OS_GenericRead_Impl token=%p impl=%p fd=%d selectable=%d nbytes=%zu timeout=%d\n",
                       (const void *)token, (const void *)impl, (impl ? impl->fd : -1), (impl ? (int)impl->selectable : -1), nbytes, timeout);

    return_code = OS_SUCCESS;

    if (nbytes > 0)
    {
        operation = OS_STREAM_STATE_READABLE;

        /*
         * If filehandle is set with O_NONBLOCK, then must call select() here.
         *
         * The "selectable" field should be set false for those file handles
         * which the underlying OS does not support select() on.
         *
         * Note that a timeout will not work unless selectable is true.
         */
        if (impl->selectable)
        {
            /* Log before calling select so the pre-select state is captured */
            OS_GenericRead_Log("BEFORE_SELECT: token=%p fd=%d operation=%u timeout=%d\n",
                               (const void *)token, impl->fd, operation, timeout);

            return_code = OS_SelectSingle_Impl(token, &operation, timeout);

            /* Log result of select */
            OS_GenericRead_Log("AFTER_SELECT: token=%p return_code=%d operation=%u\n",
                               (const void *)token, return_code, operation);
        }

        if (return_code == OS_SUCCESS && (operation & OS_STREAM_STATE_READABLE) != 0)
        {
            /* Log just before the read so the attempted operation is visible */
            OS_GenericRead_Log("BEFORE_READ: token=%p fd=%d nbytes=%zu\n",
                               (const void *)token, impl->fd, nbytes);

            os_result = read(impl->fd, buffer, nbytes);
            if (os_result < 0)
            {
                /* Capture errno immediately before any further calls */
                int saved_err = errno;

                /* Original code had: OS_DEBUG("read: %s\n", strerror(errno));
                 * That macro may be empty in some builds, leaving an empty statement.
                 * To provide observable evidence of the runtime state, log details here.
                 */
                OS_GenericRead_Log("READ_FAIL: token=%p fd=%d nbytes=%zu errno=%d strerror=%s\n",
                                   (const void *)token, impl->fd, nbytes, saved_err, strerror(saved_err));

                /* Note about the OS_DEBUG invocation location so reviewers can correlate the log line */
                OS_GenericRead_Log("NOTE: OS_DEBUG(""read: <strerror>"") was invoked at this point in the source. If OS_DEBUG is defined empty it will be preprocessed away.\n");

                /* Restore errno (logging may have changed it) and preserve original behavior */
                errno = saved_err;

                return_code = OS_ERROR;
            }
            else
            {
                /* type conversion from ssize_t to int32 for return */
                return_code = (int32)os_result;

                /* Log successful read details */
                OS_GenericRead_Log("READ_OK: token=%p fd=%d read_bytes=%zd return_code=%d\n",
                                   (const void *)token, impl->fd, os_result, return_code);
            }
        }
    }

    /* Log exit state */
    OS_GenericRead_Log("EXIT: token=%p final_return=%d\n", (const void *)token, return_code);

    return return_code;
}
