#include <stdio.h>
#include <stdint.h>
#include <stddef.h>

/* Minimal typedefs so this file can be compiled standalone for testing. */
typedef int32_t int32;
typedef uintptr_t cpuaddr;

/*
 * Probe/logging helper that appends a single line of state to the log file.
 * This preserves the original logic of OS_GenericSymbolLookup_Impl and only
 * records control flow and state observations to "/tmp/logs/778.txt".
 */
static void OS_GenericSymbolLookup_Log_State(const char *location,
                                             void *dl_handle,
                                             void *Function,
                                             const char *SymbolName,
                                             const char *dlError,
                                             int32 status)
{
    FILE *f = fopen("/tmp/logs/778.txt", "a");
    if (f != NULL)
    {
        fprintf(f, "%s: dl_handle=%p Function=%p SymbolName=%s dlError=%s status=%d\n",
                (location != NULL) ? location : "<null>",
                dl_handle,
                Function,
                (SymbolName != NULL) ? SymbolName : "<NULL>",
                (dlError != NULL) ? dlError : "<NULL>",
                (int)status);
        fclose(f);
    }
}

int32 OS_GenericSymbolLookup_Impl(void *dl_handle, cpuaddr *SymbolAddress, const char *SymbolName)
{
    const char *dlError; /*  Pointer to error string   */
    void *      Function;
    int32       status;

    status = (int32)(-1); /* OS_ERROR */

    /*
     * call dlerror() to clear any prior error that might have occurred.
     */
    dlerror();

    /* Log that the prior error was cleared (Function not set yet) */
    OS_GenericSymbolLookup_Log_State("after_clear_dlerror", dl_handle, NULL, SymbolName, NULL, status);

    Function = dlsym(dl_handle, SymbolName);
    dlError  = dlerror();

    /* Log result of dlsym and dlerror */
    OS_GenericSymbolLookup_Log_State("after_dlsym", dl_handle, Function, SymbolName, dlError, status);

    /*
     * For the POSIX DL implementation, if the symbol does not exist
     * then dlerror() is supposed to return non-null.  This is intended
     * to differentiate between valid symbols which are actually 0/NULL
     * and invalid symbols that do not exist.
     *
     * Some implementations do _not_ implement this detail, and dlerror()
     * still returns NULL after looking up an invalid symbol name.
     *
     * In practice, this is expected to be used for looking up functions
     * and as such all valid symbols should be non-NULL, so NULL is considered
     * an error even if the C library doesn't consider this an error.
     */
    if (dlError != NULL)
    {
        /* Original debug call preserved (may be a no-op in the build). */
        OS_DEBUG("Error: %s: %s\n", SymbolName, dlError);

        /* Additional probe logging to capture the error branch. */
        OS_GenericSymbolLookup_Log_State("branch_dlError_nonnull", dl_handle, Function, SymbolName, dlError, status);
    }
    else if (Function == NULL)
    {
        /* technically not an error per POSIX, but in practice should not happen */
        OS_DEBUG("Error: %s: dlsym() returned NULL\n", SymbolName);

        /* Probe logging to capture the case where Function is NULL but dlError is NULL. */
        OS_GenericSymbolLookup_Log_State("branch_function_null", dl_handle, Function, SymbolName, dlError, status);
    }
    else
    {
        status = (int32)0; /* OS_SUCCESS */

        /* Log success branch and updated status */
        OS_GenericSymbolLookup_Log_State("branch_success", dl_handle, Function, SymbolName, dlError, status);
    }

    /* Log about to write out the symbol address */
    OS_GenericSymbolLookup_Log_State("before_assign_symboladdress", dl_handle, Function, SymbolName, dlError, status);

    *SymbolAddress = (cpuaddr)Function;

    /* Final log before returning */
    OS_GenericSymbolLookup_Log_State("return", dl_handle, Function, SymbolName, dlError, status);

    return status;
}
