#include <stdio.h>
#include <stdarg.h>
#include <dlfcn.h>

/*
 * Probe logging helper: append human-readable trace lines to /tmp/logs/779.txt
 */
static void OS_GenericSymbolLookup_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/779.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);

    fclose(f);
}

int32 OS_GenericSymbolLookup_Impl(void *dl_handle, cpuaddr *SymbolAddress, const char *SymbolName)
{
    const char *dlError; /*  Pointer to error string   */
    void *      Function;
    int32       status;

    /* Save initial status for logging */
    status = OS_ERROR;

    OS_GenericSymbolLookup_Log("ENTER OS_GenericSymbolLookup_Impl: dl_handle=%p, SymbolName=%s",
                               dl_handle, (SymbolName != NULL) ? SymbolName : "(null)");

    /*
     * call dlerror() to clear any prior error that might have occurred.
     */
    OS_GenericSymbolLookup_Log("Calling dlerror() to clear prior errors");
    dlerror();

    Function = dlsym(dl_handle, SymbolName);
    dlError  = dlerror();

    /* Log the immediate results of dlsym/dlerror */
    OS_GenericSymbolLookup_Log("dlsym returned Function=%p, dlerror=%s",
                               Function, (dlError != NULL) ? dlError : "(null)");

    /*
     * For the POSIX DL implementation, if the symbol does not exist
     * then dlerror() is supposed to return non-null.  This is intended
     * to differentiate between valid symbols which are actually 0/NULL
     * and invalid symbols that do not exist.
     *
     * Some implementations do _not_ implement this detail, and dlerror()
     * still returns NULL after looking up an invalid symbol name.
     *
     * In practice, this is expected to be used for looking up functions
     * and as such all valid symbols should be non-NULL, so NULL is considered
     * an error even if the C library doesn't consider this an error.
     */
    if (dlError != NULL)
    {
        OS_GenericSymbolLookup_Log("BRANCH: dlError non-NULL for symbol '%s' -> %s", SymbolName ? SymbolName : "(null)", dlError);
        OS_DEBUG("Error: %s: %s\n", SymbolName, dlError);
    }
    else if (Function == NULL)
    {
        /* technically not an error per POSIX, but in practice should not happen */
        OS_GenericSymbolLookup_Log("BRANCH: dlsym returned NULL for symbol '%s'", SymbolName ? SymbolName : "(null)");
        OS_DEBUG("Error: %s: dlsym() returned NULL\n", SymbolName);
    }
    else
    {
        OS_GenericSymbolLookup_Log("BRANCH: success - non-NULL Function=%p for symbol '%s'", Function, SymbolName ? SymbolName : "(null)");
        status = OS_SUCCESS;
    }

    /*
     * Additional probe: show that the local variable 'Function' was only used
     * to hold the result of dlsym() and then copied to *SymbolAddress.  This
     * log captures the value immediately before assignment and confirms it was
     * not modified within this function after the lookup.
     */
    {
        void *saved_Function = Function;
        /* No modification of Function happens between lookup and assignment. */
        if (saved_Function == Function)
        {
            OS_GenericSymbolLookup_Log("PROBE: Function value unchanged after lookup: %p", Function);
        }
        else
        {
            OS_GenericSymbolLookup_Log("PROBE: Function value changed after lookup: before=%p, after=%p", saved_Function, Function);
        }
    }

    *SymbolAddress = (cpuaddr)Function;
    OS_GenericSymbolLookup_Log("ASSIGN: *SymbolAddress set to %p (Function=%p), returning status=%d", (void *)(uintptr_t)*SymbolAddress, Function, status);

    OS_GenericSymbolLookup_Log("EXIT OS_GenericSymbolLookup_Impl: status=%d", status);

    return status;
}
