int32 OS_GenericWrite_Impl(const OS_object_token_t *token, const void *buffer, size_t nbytes, int32 timeout)
{
    int32                           return_code;
    ssize_t                         os_result;
    uint32                          operation;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /* Probe: log entry and initial state */
    {
        int saved_errno = errno;
        FILE *log = fopen("/tmp/logs/690.txt", "a");
        if (log)
        {
            fprintf(log, "ENTER OS_GenericWrite_Impl: token=%p impl=%p nbytes=%zu timeout=%d\n",
                    (void *)token, (void *)impl, nbytes, timeout);
            if (impl != NULL)
            {
                fprintf(log, "  impl->fd=%d impl->selectable=%d\n", impl->fd, impl->selectable ? 1 : 0);
            }
            fclose(log);
        }
        errno = saved_errno;
    }

    return_code = OS_SUCCESS;

    if (nbytes > 0)
    {
        operation = OS_STREAM_STATE_WRITABLE;

        /*
         * If filehandle is set with O_NONBLOCK, then must call select() here.
         *
         * The "selectable" field should be set false for those file handles
         * which the underlying OS does not support select() on.
         *
         * Note that a timeout will not work unless selectable is true.
         */
        if (impl->selectable)
        {
            /* Log before select */
            {
                int saved_errno = errno;
                FILE *log = fopen("/tmp/logs/690.txt", "a");
                if (log)
                {
                    fprintf(log, "  calling OS_SelectSingle_Impl: operation=0x%08x timeout=%d\n", (unsigned)operation, timeout);
                    fclose(log);
                }
                errno = saved_errno;
            }

            return_code = OS_SelectSingle_Impl(token, &operation, timeout);

            /* Log after select */
            {
                int saved_errno = errno;
                FILE *log = fopen("/tmp/logs/690.txt", "a");
                if (log)
                {
                    fprintf(log, "  returned from OS_SelectSingle_Impl: return_code=%d operation=0x%08x\n", return_code, (unsigned)operation);
                    fclose(log);
                }
                errno = saved_errno;
            }
        }

        if (return_code == OS_SUCCESS && (operation & OS_STREAM_STATE_WRITABLE) != 0)
        {
            /* Log before write */
            {
                int saved_errno = errno;
                FILE *log = fopen("/tmp/logs/690.txt", "a");
                if (log)
                {
                    fprintf(log, "  about to write: fd=%d nbytes=%zu buffer=%p\n", impl->fd, nbytes, buffer);
                    fclose(log);
                }
                errno = saved_errno;
            }

            /* on some system libraries for which the write() argument is not
             * qualified correctly, it needs to be case to a void* here */
            os_result = write(impl->fd, GENERIC_IO_CONST_DATA_CAST buffer, nbytes);
            if (os_result < 0)
            {
                /* Log error from write with errno */
                int saved_errno = errno;
                FILE *log = fopen("/tmp/logs/690.txt", "a");
                if (log)
                {
                    fprintf(log, "  write failed: fd=%d errno=%d (%s)\n", impl->fd, saved_errno, strerror(saved_errno));
                    fclose(log);
                }
                errno = saved_errno;

                OS_DEBUG("write: %s\n", strerror(errno));
                return_code = OS_ERROR;
            }
            else
            {
                /* type conversion from ssize_t to int32 for return */
                return_code = (int32)os_result;

                /* Log successful write */
                int saved_errno = errno;
                FILE *log = fopen("/tmp/logs/690.txt", "a");
                if (log)
                {
                    fprintf(log, "  write succeeded: fd=%d written=%zd return_code=%d\n", impl->fd, os_result, return_code);
                    fclose(log);
                }
                errno = saved_errno;
            }
        }
    }
    else
    {
        /* Log the nbytes == 0 case */
        int saved_errno = errno;
        FILE *log = fopen("/tmp/logs/690.txt", "a");
        if (log)
        {
            fprintf(log, "  nbytes <= 0 branch taken: nbytes=%zu timeout=%d\n", nbytes, timeout);
            fclose(log);
        }
        errno = saved_errno;
    }

    /* Log exit and final return code */
    {
        int saved_errno = errno;
        FILE *log = fopen("/tmp/logs/690.txt", "a");
        if (log)
        {
            fprintf(log, "EXIT OS_GenericWrite_Impl: final return_code=%d\n", return_code);
            fclose(log);
        }
        errno = saved_errno;
    }

    return return_code;
}
