#include <stdio.h>
#include <stdarg.h>
#include <errno.h>
#include <string.h>
#include <unistd.h>

/* Probe logger: append human-readable traces to /tmp/logs/691.txt */
static void OS_GenericWrite_Impl_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/691.txt", "a");
    if (f == NULL)
    {
        return; /* best-effort logging only */
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fclose(f);
}

int32 OS_GenericWrite_Impl(const OS_object_token_t *token, const void *buffer, size_t nbytes, int32 timeout)
{
    int32                           return_code;
    ssize_t                         os_result;
    uint32                          operation;
    OS_impl_file_internal_record_t *impl;

    OS_GenericWrite_Impl_log("OS_GenericWrite_Impl START: token=%p, buffer=%p, nbytes=%zu, timeout=%d\n",
                             (const void *)token, buffer, nbytes, timeout);

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /* Log resolved implementation record pointer and key fields used below. */
    if (impl != NULL)
    {
        OS_GenericWrite_Impl_log("Resolved impl=%p, fd=%d, selectable=%d\n",
                                 (const void *)impl, impl->fd, impl->selectable ? 1 : 0);
    }
    else
    {
        OS_GenericWrite_Impl_log("Resolved impl=NULL\n");
    }

    return_code = OS_SUCCESS;

    if (nbytes > 0)
    {
        operation = OS_STREAM_STATE_WRITABLE;

        OS_GenericWrite_Impl_log("nbytes>0: initial operation=0x%08x\n", operation);

        /*
         * If filehandle is set with O_NONBLOCK, then must call select() here.
         *
         * The "selectable" field should be set false for those file handles
         * which the underlying OS does not support select() on.
         *
         * Note that a timeout will not work unless selectable is true.
         */
        if (impl != NULL && impl->selectable)
        {
            OS_GenericWrite_Impl_log("Calling OS_SelectSingle_Impl: operation before=0x%08x, timeout=%d\n",
                                     operation, timeout);

            return_code = OS_SelectSingle_Impl(token, &operation, timeout);

            OS_GenericWrite_Impl_log("OS_SelectSingle_Impl returned %d, operation after=0x%08x\n",
                                     return_code, operation);
        }
        else
        {
            OS_GenericWrite_Impl_log("Skipping select: impl=%p selectable=%d\n",
                                     (const void *)impl, (impl != NULL) ? (int)impl->selectable : -1);
        }

        if (return_code == OS_SUCCESS && (operation & OS_STREAM_STATE_WRITABLE) != 0)
        {
            /* on some system libraries for which the write() argument is not
             * qualified correctly, it needs to be case to a void* here */
            OS_GenericWrite_Impl_log("Performing write: fd=%d, nbytes=%zu\n", (impl != NULL) ? impl->fd : -1, nbytes);
            os_result = write(impl->fd, GENERIC_IO_CONST_DATA_CAST buffer, nbytes);
            if (os_result < 0)
            {
                int err = errno; /* capture errno immediately */
                OS_GenericWrite_Impl_log("write failed: fd=%d, errno=%d (%s)\n",
                                         (impl != NULL) ? impl->fd : -1, err, strerror(err));
                OS_DEBUG("write: %s\n", strerror(errno));
                return_code = OS_ERROR;
            }
            else
            {
                /* type conversion from ssize_t to int32 for return */
                OS_GenericWrite_Impl_log("write succeeded: fd=%d, bytes_written=%zd\n", impl->fd, os_result);
                return_code = (int32)os_result;
            }
        }
        else
        {
            OS_GenericWrite_Impl_log("Not writable or select failed: return_code=%d, operation=0x%08x\n",
                                     return_code, operation);
        }
    }
    else
    {
        OS_GenericWrite_Impl_log("nbytes==0: nothing to write\n");
    }

    OS_GenericWrite_Impl_log("OS_GenericWrite_Impl END: return_code=%d\n", return_code);

    return return_code;
}
