int32 OS_ModuleLoad(osal_id_t *module_id, const char *module_name, const char *filename, uint32 flags)
{
    char                         translated_path[OS_MAX_LOCAL_PATH_LEN];
    int32                        return_code;
    int32                        filename_status;
    OS_object_token_t            token;
    OS_module_internal_record_t *module;

    /* Log file handle used for probe logging */
    FILE *log = fopen("/tmp/logs/1687.txt", "a");
    if (log)
    {
        fprintf(log, "\n--- OS_ModuleLoad ENTRY ---\n");
        fprintf(log, "module_id=%p module_name='%s' filename='%s' flags=0x%08x\n",
                (void *)module_id,
                (module_name != NULL) ? module_name : "(null)",
                (filename != NULL) ? filename : "(null)",
                (unsigned)flags);
        fflush(log);
    }

    /*
     * Check parameters
     *
     * Note "filename" is not checked, because in certain configurations it can be validly
     * null.  filename is checked for NULL-ness by the OS_TranslatePath() later.
     */
    OS_CHECK_POINTER(module_id);
    OS_CHECK_APINAME(module_name);

    /*
     * Preemptively translate the filename, and hold it in a temporary buffer.
     *
     * This should be done before allocating a new object ID because it also
     * locks the global table, and this prevents double-locking.
     *
     * The status of this operation is stored separately, because it may or
     * may not be relevant, depending on whether the static module table is enabled.
     */
    if (log)
    {
        fprintf(log, "Calling OS_TranslatePath(filename='%s', translated_path)\n",
                (filename != NULL) ? filename : "(null)");
        fflush(log);
    }

    filename_status = OS_TranslatePath(filename, translated_path);

    /* Safely compute the length of translated_path (bounded by buffer size) for logging */
    if (log)
    {
        size_t translated_len = 0;
        while (translated_len < OS_MAX_LOCAL_PATH_LEN && translated_path[translated_len] != '\0')
        {
            translated_len++;
        }
        fprintf(log, "OS_TranslatePath returned %d translated_len=%u translated_path='%.*s'\n",
                (int)filename_status,
                (unsigned)translated_len,
                (int)translated_len,
                translated_path);
        fflush(log);
    }

    /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, module_name, &token);

    if (log)
    {
        fprintf(log, "OS_ObjectIdAllocateNew returned %d (token at %p)\n",
                (int)return_code,
                (void *)&token);
        fflush(log);
    }

    if (return_code == OS_SUCCESS)
    {
        module = OS_OBJECT_TABLE_GET(OS_module_table, token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, module, module_name, module_name);

        module->flags = flags; /* save user-supplied flags */

        if (log)
        {
            fprintf(log, "Module table entry initialized. module=%p flags=0x%08x\n",
                    (void *)module, (unsigned)module->flags);
            fflush(log);
        }

        /*
         * Check the statically-linked module list.
         * If a matching entry is found, this means its
         * already effectively "loaded" through static linkage.
         * Return success without any more action.
         *
         * If the OSAL_CONFIG_INCLUDE_STATIC_LOADER feature is disabled,
         * then the list of static modules is empty and this always
         * returns OS_ERR_NAME_NOT_FOUND.
         */
        return_code = OS_ModuleLoad_Static(module_name);
        if (log)
        {
            fprintf(log, "OS_ModuleLoad_Static('%s') returned %d\n",
                    (module_name != NULL) ? module_name : "(null)", (int)return_code);
            fflush(log);
        }

        if (return_code == OS_SUCCESS)
        {
            /* mark this as a statically loaded module */
            module->module_type = OS_MODULE_TYPE_STATIC;

            if (log)
            {
                fprintf(log, "Marked as static module.\n");
                fflush(log);
            }
        }
        else
        {
            /*
             * If this is NOT a static module, then the module file must be loaded by normal
             * means using the dynamic loader, if available.  This also means the filename
             * must be valid, so this is when the "filename_status" is checked/enforced.
             */
            if (filename_status != OS_SUCCESS)
            {
                /* supplied filename was not valid */
                return_code = filename_status;

                if (log)
                {
                    fprintf(log, "Filename not valid: filename_status=%d, set return_code=%d\n",
                            (int)filename_status, (int)return_code);
                    fflush(log);
                }
            }
            else
            {
                /* supplied filename was valid, so store a copy for future reference */
                /* Log details about the filename copy operation */
                if (log)
                {
                    size_t buf_limit = sizeof(module->file_name) - 1;
                    size_t fname_len = 0;
                    if (filename != NULL)
                    {
                        while (fname_len < buf_limit && filename[fname_len] != '\0')
                        {
                            fname_len++;
                        }
                    }
                    fprintf(log, "Copying filename into module->file_name, src_len=%u buf_limit=%u\n",
                            (unsigned)fname_len, (unsigned)buf_limit);
                    fflush(log);
                }

                strncpy(module->file_name, filename, sizeof(module->file_name) - 1);
                module->file_name[sizeof(module->file_name) - 1] = '\0';
                module->module_type = OS_MODULE_TYPE_DYNAMIC;

                /* Log the stored filename safely (bounded) */
                if (log)
                {
                    size_t stored_len = 0;
                    size_t max_store = sizeof(module->file_name) - 1;
                    while (stored_len < max_store && module->file_name[stored_len] != '\0')
                    {
                        stored_len++;
                    }
                    fprintf(log, "Stored module->file_name (len=%u)='%.*s'\n",
                            (unsigned)stored_len, (int)stored_len, module->file_name);
                    fflush(log);
                }

                /* Now call the OS-specific implementation.  This reads info from the module table. */
                if (log)
                {
                    fprintf(log, "Calling OS_ModuleLoad_Impl(&token, translated_path)\n");
                    fflush(log);
                }

                return_code = OS_ModuleLoad_Impl(&token, translated_path);

                if (log)
                {
                    fprintf(log, "OS_ModuleLoad_Impl returned %d\n", (int)return_code);
                    fflush(log);
                }
            }
        }

        /* Check result, finalize record, and unlock global table. */
        if (log)
        {
            fprintf(log, "Finalizing new object with return_code=%d\n", (int)return_code);
            fflush(log);
        }
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, module_id);

        if (log)
        {
            fprintf(log, "OS_ObjectIdFinalizeNew returned %d, final module_id=%p\n",
                    (int)return_code, (void *)module_id);
            fflush(log);
        }
    }

    if (log)
    {
        fprintf(log, "--- OS_ModuleLoad EXIT return_code=%d ---\n", (int)return_code);
        fclose(log);
    }

    return return_code;
}
