#include <stdio.h>
#include <stdint.h>

/* Probe logging helper for OS_ObjectIdConvertToken.  This is intentionally
 * read-only with respect to the token structure (takes a const pointer)
 * so that the logs reflect that the token is only inspected.  The file
 * "/tmp/logs/1539.txt" is appended to so multiple runs accumulate evidence.
 */
static void OS_ObjectIdConvertToken_Log(const OS_object_token_t *token,
                                       const OS_common_record_t *obj,
                                       osal_id_t expected_id,
                                       int32 return_code,
                                       uint32 attempts,
                                       const char *stage)
{
    FILE *f = fopen("/tmp/logs/1539.txt", "a");
    if (f == NULL)
    {
        return; /* best-effort logging only */
    }

    /* Record key state items useful to reproduce the violation scenario */
    fprintf(f, "Stage: %s\n", stage);
    fprintf(f, "  token ptr: %p\n", (const void *)token);
    /* Use the accessor to get the ID associated with the token (may be read-only) */
    fprintf(f, "  token id: 0x%08x\n", (unsigned)OS_ObjectIdFromToken((OS_object_token_t *)token));
    fprintf(f, "  token lock_mode: %u\n", (unsigned)token->lock_mode);
    fprintf(f, "  expected_id: 0x%08x\n", (unsigned)expected_id);
    fprintf(f, "  obj ptr: %p\n", (const void *)obj);
    if (obj != NULL)
    {
        fprintf(f, "  obj->active_id: 0x%08x\n", (unsigned)obj->active_id);
        fprintf(f, "  obj->refcount: %u\n", (unsigned)obj->refcount);
    }
    fprintf(f, "  attempts: %u\n", (unsigned)attempts);
    fprintf(f, "  return_code (incoming): %d\n", (int)return_code);
    fprintf(f, "\n");

    fclose(f);
}

int32 OS_ObjectIdConvertToken(OS_object_token_t *token)
{
    int32               return_code = OS_ERROR;
    uint32              attempts    = 0;
    OS_common_record_t *obj;
    osal_id_t           expected_id;

    obj         = OS_ObjectIdGlobalFromToken(token);
    expected_id = OS_ObjectIdFromToken(token);

    /* Log initial state */
    OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "entry");

    /*
     * Upon entry the ID from the token must be valid
     */
    if (!OS_ObjectIdIsValid(expected_id))
    {
        /* Log invalid ID case */
        OS_ObjectIdConvertToken_Log(token, obj, expected_id, OS_ERR_INCORRECT_OBJ_STATE, attempts, "invalid_token_id");
        return OS_ERR_INCORRECT_OBJ_STATE;
    }

    /*
     * If lock mode is RESERVED, then the ID in the record should
     * already be set to OS_OBJECT_ID_RESERVED.  This is for very
     * specific use cases where a secondary task needs to access an
     * object during its creation/deletion.
     *
     * For all typical modes the ID in the record should be equal
     * to the token ID.
     */
    if (token->lock_mode == OS_LOCK_MODE_RESERVED)
    {
        expected_id = OS_OBJECT_ID_RESERVED;
        OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "reserved_mode_adjust_expected_id");
    }

    while (true)
    {
        /* Log each loop entry */
        OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "loop_top");

        /* Validate the integrity of the ID.  As the "active_id" is a single
         * integer, we can do this check regardless of whether global is locked or not. */
        if (OS_ObjectIdEqual(obj->active_id, expected_id))
        {
            /*
             * Got an ID match...
             */
            if (token->lock_mode == OS_LOCK_MODE_EXCLUSIVE)
            {
                /*
                 * For EXCLUSIVE mode, overwrite the ID to be RESERVED now -- this
                 * makes any future ID checks or lock attempts in other tasks fail to match.
                 */
                if (!OS_ObjectIdEqual(expected_id, OS_OBJECT_ID_RESERVED))
                {
                    expected_id    = OS_OBJECT_ID_RESERVED;
                    obj->active_id = expected_id;

                    OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "exclusive_overwrite_to_reserved");
                }

                /*
                 * Also confirm that reference count is zero
                 * If not zero, will need to wait for other tasks to release.
                 */
                if (obj->refcount == 0)
                {
                    return_code = OS_SUCCESS;
                    OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "exclusive_refcount_zero_success");
                    break;
                }
                else
                {
                    OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "exclusive_refcount_nonzero_wait");
                }
            }
            else
            {
                /*
                 * Nothing else to test for this lock type
                 */
                return_code = OS_SUCCESS;
                OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "nonexclusive_match_success");
                break;
            }
        }
        else if (token->lock_mode == OS_LOCK_MODE_NONE || !OS_ObjectIdEqual(obj->active_id, OS_OBJECT_ID_RESERVED))
        {
            /* Not an ID match and not RESERVED - fail out */
            return_code = OS_ERR_INVALID_ID;
            OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "id_mismatch_fail");
            break;
        }

        /*
         * If we get this far, it means there is contention for access to the object.
         *  a) we want to some type of lock but the ID is currently RESERVED
         *  b) the refcount is too high - need to wait for release
         *
         * In this case we will UNLOCK the global object again so that the holder
         * can relinquish it.  We'll try again a few times before giving up hope.
         */
        ++attempts;
        if (attempts >= 5)
        {
            return_code = OS_ERR_OBJECT_IN_USE;
            OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "attempts_exhausted");
            break;
        }

        OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "waiting_for_state_change_before_block");

        /*
         * Call the impl layer to wait for some sort of change to occur.
         */
        OS_WaitForStateChange(token, attempts);

        OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "after_wait_for_state_change");
    }

    /*
     * Determine if the global table needs to be unlocked now.
     *
     * If lock_mode is OS_LOCK_MODE_NONE, then the table was never locked
     * to begin with, and therefore never needs to be unlocked.
     */
    if (token->lock_mode != OS_LOCK_MODE_NONE)
    {
        if (return_code == OS_SUCCESS)
        {
            /* always increment the refcount, which means a task is actively
             * using or modifying this record. */
            ++obj->refcount;

            OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "success_increment_refcount_before_unlock_decision");

            /*
             * On a successful operation, the global is unlocked if it is
             * a REFCOUNT or EXCLUSIVE lock.  Note for EXCLUSIVE, because the ID
             * was overwritten to OS_OBJECT_ID_RESERVED, other tasks will not be
             * able to access the object because the ID will not match, so the
             * table can be unlocked while the remainder of the create/delete process
             * continues.
             *
             * For OS_LOCK_MODE_GLOBAL the global lock should be maintained and
             * returned to the caller.
             */
            if (token->lock_mode == OS_LOCK_MODE_REFCOUNT || token->lock_mode == OS_LOCK_MODE_EXCLUSIVE)
            {
                OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "calling_OS_Unlock_Global");
                OS_Unlock_Global(token);
                OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "after_OS_Unlock_Global");
            }
        }
        else if (token->lock_mode == OS_LOCK_MODE_EXCLUSIVE && OS_ObjectIdEqual(expected_id, OS_OBJECT_ID_RESERVED))
        {
            /*
             * On failure, if the active_id was overwritten, then set
             * it back to the original value which is in the token.
             * (note it had to match initially before overwrite)
             */
            obj->active_id = OS_ObjectIdFromToken(token);
            OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "restore_active_id_on_failure");
        }
    }

    OS_ObjectIdConvertToken_Log(token, obj, expected_id, return_code, attempts, "exit");

    return return_code;
}
