#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe helper - append logs to /tmp/logs/1290.txt */
static void os_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1290.txt", "a");
    if (!f)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_OpenCreate(osal_id_t *filedes, const char *path, int32 flags, int32 access_mode)
{
    int32                        return_code;
    char                         local_path[OS_MAX_LOCAL_PATH_LEN];
    OS_object_token_t            token;
    OS_stream_internal_record_t *stream;

    /* Check parameters */
    OS_CHECK_POINTER(filedes);

    /* Initialize file descriptor */
    *filedes = OS_OBJECT_ID_UNDEFINED;

    /* Log entry and initial state */
    os_log_probe("OS_OpenCreate ENTRY: filedes_ptr=%p, initial_filedes=%d, path=\"%s\", flags=%d, access_mode=%d",
                 (void *)filedes, (int)*filedes, path ? path : "(null)", (int)flags, (int)access_mode);

    /*
    ** Check for a valid access mode
    */
    if (access_mode != OS_WRITE_ONLY && access_mode != OS_READ_ONLY && access_mode != OS_READ_WRITE)
    {
        os_log_probe("OS_OpenCreate: invalid access_mode=%d -> returning OS_ERROR", (int)access_mode);
        return OS_ERROR;
    }

    /*
     * Translate the path
     */
    os_log_probe("OS_OpenCreate: calling OS_TranslatePath with path=\"%s\"", path ? path : "(null)");
    return_code = OS_TranslatePath(path, (char *)local_path);
    os_log_probe("OS_OpenCreate: OS_TranslatePath returned %d, local_path=\"%s\"",
                 (int)return_code, local_path[0] ? local_path : "(empty)");

    if (return_code == OS_SUCCESS)
    {
        /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
        os_log_probe("OS_OpenCreate: calling OS_ObjectIdAllocateNew");
        return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, NULL, &token);
        os_log_probe("OS_OpenCreate: OS_ObjectIdAllocateNew returned %d, token=%d", (int)return_code, (int)token);
        if (return_code == OS_SUCCESS)
        {
            stream = OS_OBJECT_TABLE_GET(OS_stream_table, token);
            os_log_probe("OS_OpenCreate: stream_ptr=%p (token=%d)", (void *)stream, (int)token);

            /* Reset the table entry and save the name */
            OS_OBJECT_INIT(token, stream, stream_name, path);
            os_log_probe("OS_OpenCreate: OS_OBJECT_INIT called for token=%d, stream_name=\"%s\"",
                         (int)token, path ? path : "(null)");

            /* Now call the OS-specific implementation.  */
            os_log_probe("OS_OpenCreate: calling OS_FileOpen_Impl with local_path=\"%s\", flags=%d, access_mode=%d",
                         local_path[0] ? local_path : "(empty)", (int)flags, (int)access_mode);
            return_code = OS_FileOpen_Impl(&token, local_path, flags, access_mode);
            os_log_probe("OS_OpenCreate: OS_FileOpen_Impl returned %d", (int)return_code);

            /* Check result, finalize record, and unlock global table. */
            return_code = OS_ObjectIdFinalizeNew(return_code, &token, filedes);
            os_log_probe("OS_OpenCreate: OS_ObjectIdFinalizeNew returned %d, final_filedes=%d",
                         (int)return_code, filedes ? (int)*filedes : -1);
        }
        else
        {
            os_log_probe("OS_OpenCreate: OS_ObjectIdAllocateNew failed with %d", (int)return_code);
        }
    }
    else
    {
        os_log_probe("OS_OpenCreate: OS_TranslatePath failed with %d - skipping allocation/open", (int)return_code);
    }

    os_log_probe("OS_OpenCreate EXIT: return_code=%d", (int)return_code);

    return return_code;
}
