#include <stdio.h>
#include <stdarg.h>
#include <sys/stat.h>
#include <errno.h>

/*
 * Probe helper: append logs to /tmp/logs/461.txt. Attempts to create the
 * directory /tmp/logs if it does not exist. This function is safe if the
 * directory already exists.
 */
static void probe_log(const char *fmt, ...)
{
    va_list ap;
    FILE *fp;

    /* Try to create the logs directory if needed. Ignore errors. */
    (void)mkdir("/tmp/logs", 0777);

    fp = fopen("/tmp/logs/461.txt", "a");
    if (fp == NULL)
    {
        return; /* If logging fails, do not disturb original behavior. */
    }

    va_start(ap, fmt);
    vfprintf(fp, fmt, ap);
    va_end(ap);

    fprintf(fp, "\n");
    fclose(fp);
}

int32 OS_Posix_InternalTaskCreate_Impl(pthread_t *pthr, osal_priority_t priority, size_t stacksz,
                                       PthreadFuncPtr_t entry, void *entry_arg)
{
    int                return_code = 0;
    pthread_attr_t     custom_attr;
    struct sched_param priority_holder;

    /* Log function entry and initial parameter state. */
    probe_log("ENTER: OS_Posix_InternalTaskCreate_Impl: initial stacksz=%zu, priority=%d, pthr=%p, entry=%p, entry_arg=%p",
              stacksz, (int)priority, (void *)pthr, (void *)entry, entry_arg);

    /*
     ** Initialize the pthread_attr structure.
     ** The structure is used to set the stack and priority
     */
    memset(&custom_attr, 0, sizeof(custom_attr));
    return_code = pthread_attr_init(&custom_attr);
    if (return_code != 0)
    {
        probe_log("ERROR: pthread_attr_init failed: %s (errno=%d)", strerror(return_code), return_code);
        OS_DEBUG("pthread_attr_init error in OS_TaskCreate: %s\n", strerror(return_code));
        return OS_ERROR;
    }

    /*
     * Adjust the stack size parameter.
     *
     * POSIX has additional restrictions/limitations on the stack size of tasks that
     * other RTOS environments may not have.  Specifically POSIX says that the stack
     * size must be at least PTHREAD_STACK_MIN and may also need to be a multiple of the
     * system page size.
     *
     * Rounding up means the user might get a bigger stack than they requested, but
     * that should not break anything aside from consuming extra memory.
     */
    probe_log("STATE: before adjustment stacksz=%zu, PTHREAD_STACK_MIN=%zu, PageSize=%zu",
              stacksz, (size_t)PTHREAD_STACK_MIN, (size_t)POSIX_GlobalVars.PageSize);

    if (stacksz < PTHREAD_STACK_MIN)
    {
        probe_log("PATH: stacksz < PTHREAD_STACK_MIN (was %zu), setting to PTHREAD_STACK_MIN=%zu",
                  stacksz, (size_t)PTHREAD_STACK_MIN);
        stacksz = PTHREAD_STACK_MIN;
        probe_log("STATE: after enforcing min stacksz=%zu", stacksz);
    }

    probe_log("ACTION: adding PageSize-1: stacksz (before)=%zu, add=%zu",
              stacksz, (size_t)(POSIX_GlobalVars.PageSize - 1));
    stacksz += POSIX_GlobalVars.PageSize - 1;
    probe_log("STATE: after +PageSize-1 stacksz=%zu", stacksz);

    probe_log("ACTION: rounding down to page multiple: stacksz (before)=%zu, PageSize=%zu",
              stacksz, (size_t)POSIX_GlobalVars.PageSize);
    stacksz -= stacksz % POSIX_GlobalVars.PageSize;
    probe_log("STATE: after rounding stacksz=%zu", stacksz);

    /*
    ** Set the Stack Size
    */
    probe_log("CALL: pthread_attr_setstacksize with stacksz=%zu", stacksz);
    return_code = pthread_attr_setstacksize(&custom_attr, stacksz);
    if (return_code != 0)
    {
        probe_log("ERROR: pthread_attr_setstacksize failed: %s (errno=%d)", strerror(return_code), return_code);
        OS_DEBUG("pthread_attr_setstacksize error in OS_TaskCreate: %s\n", strerror(return_code));
        return OS_ERROR;
    }

    /*
    ** Set the thread to be joinable by default
    */
    return_code = pthread_attr_setdetachstate(&custom_attr, PTHREAD_CREATE_JOINABLE);
    if (return_code != 0)
    {
        probe_log("ERROR: pthread_attr_setdetachstate failed: %s (errno=%d)", strerror(return_code), return_code);
        OS_DEBUG("pthread_attr_setdetachstate error in OS_TaskCreate: %s\n", strerror(return_code));
        return OS_ERROR;
    }

    /*
    ** Test to see if the original main task scheduling priority worked.
    ** If so, then also set the attributes for this task.  Otherwise attributes
    ** are left at default.
    */
    if (POSIX_GlobalVars.EnableTaskPriorities)
    {
        /*
        ** Set the scheduling inherit attribute to EXPLICIT
        */
        return_code = pthread_attr_setinheritsched(&custom_attr, PTHREAD_EXPLICIT_SCHED);
        if (return_code != 0)
        {
            probe_log("ERROR: pthread_attr_setinheritsched failed: %s (errno=%d)", strerror(return_code), return_code);
            OS_DEBUG("pthread_attr_setinheritsched error in OS_TaskCreate, errno = %s\n", strerror(return_code));
            return OS_ERROR;
        }

        /*
        ** Set the scheduling policy
        ** The best policy is determined during initialization
        */
        return_code = pthread_attr_setschedpolicy(&custom_attr, POSIX_GlobalVars.SelectedRtScheduler);
        if (return_code != 0)
        {
            probe_log("ERROR: pthread_attr_setschedpolicy failed: %s (errno=%d)", strerror(return_code), return_code);
            OS_DEBUG("pthread_attr_setschedpolity error in OS_TaskCreate: %s\n", strerror(return_code));
            return OS_ERROR;
        }

        /*
        ** Set priority
        */
        return_code = pthread_attr_getschedparam(&custom_attr, &priority_holder);
        if (return_code != 0)
        {
            probe_log("ERROR: pthread_attr_getschedparam failed: %s (errno=%d)", strerror(return_code), return_code);
            OS_DEBUG("pthread_attr_getschedparam error in OS_TaskCreate: %s\n", strerror(return_code));
            return OS_ERROR;
        }

        priority_holder.sched_priority = OS_PriorityRemap(priority);
        probe_log("STATE: remapped priority to sched_priority=%d", priority_holder.sched_priority);
        return_code                    = pthread_attr_setschedparam(&custom_attr, &priority_holder);
        if (return_code != 0)
        {
            probe_log("ERROR: pthread_attr_setschedparam failed: %s (errno=%d)", strerror(return_code), return_code);
            OS_DEBUG("pthread_attr_setschedparam error in OS_TaskCreate: %s\n", strerror(return_code));
            return OS_ERROR;
        }

    } /* End if user is root */

    /*
     ** Create thread
     */
    probe_log("CALL: pthread_create (creating thread)");
    return_code = pthread_create(pthr, &custom_attr, entry, entry_arg);
    if (return_code != 0)
    {
        probe_log("ERROR: pthread_create failed: %s (errno=%d)", strerror(return_code), return_code);
        OS_DEBUG("pthread_create error in OS_TaskCreate: %s\n", strerror(return_code));
        return OS_ERROR;
    }

    probe_log("INFO: pthread_create succeeded, thread handle @ %p", (void *)pthr);

    /*
     ** Free the resources that are no longer needed
     ** Since the task is now running - pthread_create() was successful -
     ** Do not treat anything bad that happens after this point as fatal.
     ** The task is running, after all - better to leave well enough alone.
     */
    return_code = pthread_attr_destroy(&custom_attr);
    if (return_code != 0)
    {
        probe_log("WARN: pthread_attr_destroy returned error: %s (errno=%d)", strerror(return_code), return_code);
        OS_DEBUG("pthread_attr_destroy error in OS_TaskCreate: %s\n", strerror(return_code));
    }

    probe_log("EXIT: OS_Posix_InternalTaskCreate_Impl returning OS_SUCCESS");

    return OS_SUCCESS;
}
