int32 OS_Posix_InternalTaskCreate_Impl(pthread_t *pthr, osal_priority_t priority, size_t stacksz,
                                       PthreadFuncPtr_t entry, void *entry_arg)
{
    int                return_code = 0;
    pthread_attr_t     custom_attr;
    struct sched_param priority_holder;

    /* Logging probe variables */
    FILE *logf = fopen("/tmp/logs/458.txt", "a");
    int rc_get_detach = 0;
    int current_detach_state = -1;

    if (logf)
    {
        fprintf(logf, "OS_Posix_InternalTaskCreate_Impl: ENTRY pid=%ld priority=%d stacksz=%zu pthr=%p entry=%p entry_arg=%p\n",
                (long)getpid(), (int)priority, stacksz, (void *)pthr, (void *)entry, entry_arg);
        fflush(logf);
    }

    /*
     ** Initialize the pthread_attr structure.
     ** The structure is used to set the stack and priority
     */
    memset(&custom_attr, 0, sizeof(custom_attr));
    return_code = pthread_attr_init(&custom_attr);
    if (return_code != 0)
    {
        OS_DEBUG("pthread_attr_init error in OS_TaskCreate: %s\n", strerror(return_code));
        if (logf)
        {
            fprintf(logf, "pthread_attr_init FAILED: rc=%d (%s)\n", return_code, strerror(return_code));
            fflush(logf);
            fclose(logf);
        }
        return OS_ERROR;
    }

    /* Probe: read default detach state after init to show default value */
    rc_get_detach = pthread_attr_getdetachstate(&custom_attr, &current_detach_state);
    if (logf)
    {
        if (rc_get_detach == 0)
        {
            fprintf(logf, "After pthread_attr_init: detachstate=%d\n", current_detach_state);
        }
        else
        {
            fprintf(logf, "After pthread_attr_init: pthread_attr_getdetachstate FAILED rc=%d (%s)\n", rc_get_detach, strerror(rc_get_detach));
        }
        fflush(logf);
    }

    /*
     * Adjust the stack size parameter.
     *
     * POSIX has additional restrictions/limitations on the stack size of tasks that
     * other RTOS environments may not have.  Specifically POSIX says that the stack
     * size must be at least PTHREAD_STACK_MIN and may also need to be a multiple of the
     * system page size.
     *
     * Rounding up means the user might get a bigger stack than they requested, but
     * that should not break anything aside from consuming extra memory.
     */
    if (stacksz < PTHREAD_STACK_MIN)
    {
        stacksz = PTHREAD_STACK_MIN;
    }

    stacksz += POSIX_GlobalVars.PageSize - 1;
    stacksz -= stacksz % POSIX_GlobalVars.PageSize;

    if (logf)
    {
        fprintf(logf, "Adjusted stacksz=%zu (pagesize=%zu)\n", stacksz, (size_t)POSIX_GlobalVars.PageSize);
        fflush(logf);
    }

    /*
    ** Set the Stack Size
    */
    return_code = pthread_attr_setstacksize(&custom_attr, stacksz);
    if (return_code != 0)
    {
        OS_DEBUG("pthread_attr_setstacksize error in OS_TaskCreate: %s\n", strerror(return_code));
        if (logf)
        {
            fprintf(logf, "pthread_attr_setstacksize FAILED: rc=%d (%s)\n", return_code, strerror(return_code));
            fflush(logf);
            fclose(logf);
        }
        return OS_ERROR;
    }

    /*
    ** Set the thread to be joinable by default
    */
    if (logf)
    {
        fprintf(logf, "About to call pthread_attr_setdetachstate(&custom_attr, PTHREAD_CREATE_JOINABLE)\n");
        fflush(logf);
    }

    return_code = pthread_attr_setdetachstate(&custom_attr, PTHREAD_CREATE_JOINABLE);
    if (return_code != 0)
    {
        OS_DEBUG("pthread_attr_setdetachstate error in OS_TaskCreate: %s\n", strerror(return_code));
        if (logf)
        {
            fprintf(logf, "pthread_attr_setdetachstate FAILED: rc=%d (%s)\n", return_code, strerror(return_code));
            fflush(logf);
            fclose(logf);
        }
        return OS_ERROR;
    }

    /* Probe: read detach state after setting and log whether the call changed anything */
    rc_get_detach = pthread_attr_getdetachstate(&custom_attr, &current_detach_state);
    if (logf)
    {
        if (rc_get_detach == 0)
        {
            fprintf(logf, "After pthread_attr_setdetachstate: detachstate=%d\n", current_detach_state);
            if (current_detach_state == PTHREAD_CREATE_JOINABLE)
            {
                fprintf(logf, "Note: detachstate is PTHREAD_CREATE_JOINABLE after set (may have been default)\n");
            }
        }
        else
        {
            fprintf(logf, "After pthread_attr_setdetachstate: pthread_attr_getdetachstate FAILED rc=%d (%s)\n", rc_get_detach, strerror(rc_get_detach));
        }
        fflush(logf);
    }

    /*
    ** Test to see if the original main task scheduling priority worked.
    ** If so, then also set the attributes for this task.  Otherwise attributes
    ** are left at default.
    */
    if (POSIX_GlobalVars.EnableTaskPriorities)
    {
        if (logf)
        {
            fprintf(logf, "EnableTaskPriorities is true - configuring scheduling attributes\n");
            fflush(logf);
        }

        /*
        ** Set the scheduling inherit attribute to EXPLICIT
        */
        return_code = pthread_attr_setinheritsched(&custom_attr, PTHREAD_EXPLICIT_SCHED);
        if (return_code != 0)
        {
            OS_DEBUG("pthread_attr_setinheritsched error in OS_TaskCreate, errno = %s\n", strerror(return_code));
            if (logf)
            {
                fprintf(logf, "pthread_attr_setinheritsched FAILED: rc=%d (%s)\n", return_code, strerror(return_code));
                fflush(logf);
                fclose(logf);
            }
            return OS_ERROR;
        }

        /*
        ** Set the scheduling policy
        ** The best policy is determined during initialization
        */
        return_code = pthread_attr_setschedpolicy(&custom_attr, POSIX_GlobalVars.SelectedRtScheduler);
        if (return_code != 0)
        {
            OS_DEBUG("pthread_attr_setschedpolity error in OS_TaskCreate: %s\n", strerror(return_code));
            if (logf)
            {
                fprintf(logf, "pthread_attr_setschedpolicy FAILED: rc=%d (%s)\n", return_code, strerror(return_code));
                fflush(logf);
                fclose(logf);
            }
            return OS_ERROR;
        }

        if (logf)
        {
            fprintf(logf, "Requested scheduling policy=%d\n", POSIX_GlobalVars.SelectedRtScheduler);
            fflush(logf);
        }

        /*
        ** Set priority
        */
        return_code = pthread_attr_getschedparam(&custom_attr, &priority_holder);
        if (return_code != 0)
        {
            OS_DEBUG("pthread_attr_getschedparam error in OS_TaskCreate: %s\n", strerror(return_code));
            if (logf)
            {
                fprintf(logf, "pthread_attr_getschedparam FAILED: rc=%d (%s)\n", return_code, strerror(return_code));
                fflush(logf);
                fclose(logf);
            }
            return OS_ERROR;
        }

        priority_holder.sched_priority = OS_PriorityRemap(priority);
        if (logf)
        {
            fprintf(logf, "Remapped priority to %d\n", priority_holder.sched_priority);
            fflush(logf);
        }

        return_code                    = pthread_attr_setschedparam(&custom_attr, &priority_holder);
        if (return_code != 0)
        {
            OS_DEBUG("pthread_attr_setschedparam error in OS_TaskCreate: %s\n", strerror(return_code));
            if (logf)
            {
                fprintf(logf, "pthread_attr_setschedparam FAILED: rc=%d (%s)\n", return_code, strerror(return_code));
                fflush(logf);
                fclose(logf);
            }
            return OS_ERROR;
        }

    } /* End if user is root */

    /*
     ** Create thread
     */
    if (logf)
    {
        fprintf(logf, "Calling pthread_create with attr object\n");
        fflush(logf);
    }

    return_code = pthread_create(pthr, &custom_attr, entry, entry_arg);
    if (return_code != 0)
    {
        OS_DEBUG("pthread_create error in OS_TaskCreate: %s\n", strerror(return_code));
        if (logf)
        {
            fprintf(logf, "pthread_create FAILED: rc=%d (%s)\n", return_code, strerror(return_code));
            fflush(logf);
            fclose(logf);
        }
        return OS_ERROR;
    }

    if (logf)
    {
        fprintf(logf, "pthread_create succeeded: thread id stored at %p\n", (void *)pthr);
        fflush(logf);
    }

    /*
     ** Free the resources that are no longer needed
     ** Since the task is now running - pthread_create() was successful -
     ** Do not treat anything bad that happens after this point as fatal.
     ** The task is running, after all - better to leave well enough alone.
     */
    return_code = pthread_attr_destroy(&custom_attr);
    if (return_code != 0)
    {
        OS_DEBUG("pthread_attr_destroy error in OS_TaskCreate: %s\n", strerror(return_code));
        if (logf)
        {
            fprintf(logf, "pthread_attr_destroy returned rc=%d (%s)\n", return_code, strerror(return_code));
            fflush(logf);
        }
    }

    if (logf)
    {
        fprintf(logf, "OS_Posix_InternalTaskCreate_Impl: EXIT success\n");
        fflush(logf);
        fclose(logf);
    }

    return OS_SUCCESS;
}
