#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <time.h>
#include <errno.h>

/*
 * Probe helpers: write controlled runtime information to /tmp/logs/458.txt
 * These functions are non-intrusive (only open/append/close) and do not
 * change the behavior of the original function. They are intended to help
 * reproduce/observe the scenario where OS_DEBUG may be defined empty and
 * calls such as strerror(return_code) inside it are never executed.
 */
static void probe_logf(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/458.txt", "a");
    if (f == NULL)
    {
        return;
    }

    /* Timestamp prefix for easier correlation */
    time_t t = time(NULL);
    if (t != (time_t)-1)
    {
        struct tm tm_buf;
        if (localtime_r(&t, &tm_buf) != NULL)
        {
            char ts[64];
            if (strftime(ts, sizeof(ts), "%Y-%m-%d %H:%M:%S", &tm_buf) > 0)
            {
                fprintf(f, "%s ", ts);
            }
        }
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static void probe_err(const char *context, int return_code)
{
    /* Capture both numeric code and strerror() message to reproduce the scenario
     * where the original call to strerror() inside an empty OS_DEBUG macro would
     * not be executed. Calling strerror here guarantees the message is created
     * and logged for evidence. */
    const char *msg = "(null)";
    if (return_code != 0)
    {
        msg = strerror(return_code);
        if (msg == NULL)
        {
            msg = "(strerror returned NULL)";
        }
    }
    probe_logf("PROBE ERROR: %s: return_code=%d, strerror=\"%s\"", context, return_code, msg);
}

int32 OS_Posix_InternalTaskCreate_Impl(pthread_t *pthr, osal_priority_t priority, size_t stacksz,
                                       PthreadFuncPtr_t entry, void *entry_arg)
{
    int                return_code = 0;
    pthread_attr_t     custom_attr;
    struct sched_param priority_holder;

    probe_logf("ENTER: OS_Posix_InternalTaskCreate_Impl pthr=%p priority=%d stacksz=%zu entry=%p entry_arg=%p",
               (void *)pthr, (int)priority, (size_t)stacksz, (void *)entry, entry_arg);

    /*
     ** Initialize the pthread_attr structure.
     ** The structure is used to set the stack and priority
     */
    memset(&custom_attr, 0, sizeof(custom_attr));
    return_code = pthread_attr_init(&custom_attr);
    if (return_code != 0)
    {
        /* Probe and preserve original behavior */
        probe_err("pthread_attr_init", return_code);
        OS_DEBUG("pthread_attr_init error in OS_TaskCreate: %s\n", strerror(return_code));
        return OS_ERROR;
    }

    /*
     * Adjust the stack size parameter.
     *
     * POSIX has additional restrictions/limitations on the stack size of tasks that
     * other RTOS environments may not have.  Specifically POSIX says that the stack
     * size must be at least PTHREAD_STACK_MIN and may also need to be a multiple of the
     * system page size.
     *
     * Rounding up means the user might get a bigger stack than they requested, but
     * that should not break anything aside from consuming extra memory.
     */
    probe_logf("STACK ADJUST: original stacksz=%zu", stacksz);
    if (stacksz < PTHREAD_STACK_MIN)
    {
        stacksz = PTHREAD_STACK_MIN;
    }

    stacksz += POSIX_GlobalVars.PageSize - 1;
    stacksz -= stacksz % POSIX_GlobalVars.PageSize;
    probe_logf("STACK ADJUSTED: adjusted stacksz=%zu (PageSize=%zu)", stacksz, (size_t)POSIX_GlobalVars.PageSize);

    /*
    ** Set the Stack Size
    */
    return_code = pthread_attr_setstacksize(&custom_attr, stacksz);
    if (return_code != 0)
    {
        probe_err("pthread_attr_setstacksize", return_code);
        OS_DEBUG("pthread_attr_setstacksize error in OS_TaskCreate: %s\n", strerror(return_code));
        return OS_ERROR;
    }

    /*
    ** Set the thread to be joinable by default
    */
    return_code = pthread_attr_setdetachstate(&custom_attr, PTHREAD_CREATE_JOINABLE);
    if (return_code != 0)
    {
        probe_err("pthread_attr_setdetachstate", return_code);
        OS_DEBUG("pthread_attr_setdetachstate error in OS_TaskCreate: %s\n", strerror(return_code));
        return OS_ERROR;
    }

    /*
    ** Test to see if the original main task scheduling priority worked.
    ** If so, then also set the attributes for this task.  Otherwise attributes
    ** are left at default.
    */
    probe_logf("PRIORITY PATH: EnableTaskPriorities=%d SelectedRtScheduler=%d",
               (int)POSIX_GlobalVars.EnableTaskPriorities,
               (int)POSIX_GlobalVars.SelectedRtScheduler);

    if (POSIX_GlobalVars.EnableTaskPriorities)
    {
        /*
        ** Set the scheduling inherit attribute to EXPLICIT
        */
        return_code = pthread_attr_setinheritsched(&custom_attr, PTHREAD_EXPLICIT_SCHED);
        if (return_code != 0)
        {
            probe_err("pthread_attr_setinheritsched", return_code);
            OS_DEBUG("pthread_attr_setinheritsched error in OS_TaskCreate, errno = %s\n", strerror(return_code));
            return OS_ERROR;
        }

        /*
        ** Set the scheduling policy
        ** The best policy is determined during initialization
        */
        return_code = pthread_attr_setschedpolicy(&custom_attr, POSIX_GlobalVars.SelectedRtScheduler);
        if (return_code != 0)
        {
            probe_err("pthread_attr_setschedpolicy", return_code);
            OS_DEBUG("pthread_attr_setschedpolity error in OS_TaskCreate: %s\n", strerror(return_code));
            return OS_ERROR;
        }

        /*
        ** Set priority
        */
        return_code = pthread_attr_getschedparam(&custom_attr, &priority_holder);
        if (return_code != 0)
        {
            probe_err("pthread_attr_getschedparam", return_code);
            OS_DEBUG("pthread_attr_getschedparam error in OS_TaskCreate: %s\n", strerror(return_code));
            return OS_ERROR;
        }

        priority_holder.sched_priority = OS_PriorityRemap(priority);
        probe_logf("PRIORITY MAP: requested=%d remapped=%d", (int)priority, (int)priority_holder.sched_priority);
        return_code                    = pthread_attr_setschedparam(&custom_attr, &priority_holder);
        if (return_code != 0)
        {
            probe_err("pthread_attr_setschedparam", return_code);
            OS_DEBUG("pthread_attr_setschedparam error in OS_TaskCreate: %s\n", strerror(return_code));
            return OS_ERROR;
        }

    } /* End if user is root */

    /*
     ** Create thread
     */
    probe_logf("CALL pthread_create: pthr=%p attr=%p entry=%p entry_arg=%p", (void *)pthr, (void *)&custom_attr, (void *)entry, entry_arg);
    return_code = pthread_create(pthr, &custom_attr, entry, entry_arg);
    if (return_code != 0)
    {
        probe_err("pthread_create", return_code);
        OS_DEBUG("pthread_create error in OS_TaskCreate: %s\n", strerror(return_code));
        return OS_ERROR;
    }

    /*
     ** Free the resources that are no longer needed
     ** Since the task is now running - pthread_create() was successful -
     ** Do not treat anything bad that happens after this point as fatal.
     ** The task is running, after all - better to leave well enough alone.
     */
    return_code = pthread_attr_destroy(&custom_attr);
    if (return_code != 0)
    {
        /* Non-fatal: log for diagnostics */
        probe_err("pthread_attr_destroy", return_code);
        OS_DEBUG("pthread_attr_destroy error in OS_TaskCreate: %s\n", strerror(return_code));
    }

    probe_logf("EXIT: OS_Posix_InternalTaskCreate_Impl result=%d", OS_SUCCESS);
    return OS_SUCCESS;
}
