#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <string.h>

static void OS_Posix_LogProbe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/354.txt", "a");
    if (f == NULL)
    {
        return;
    }

    time_t t = time(NULL);
    struct tm tm_buf;
#if defined(_POSIX_VERSION)
    localtime_r(&t, &tm_buf);
#else
    {
        struct tm *tmp = localtime(&t);
        if (tmp != NULL)
        {
            tm_buf = *tmp;
        }
        else
        {
            memset(&tm_buf, 0, sizeof(tm_buf));
        }
    }
#endif

    char timestr[64];
    strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", &tm_buf);

    fprintf(f, "%s - ", timestr);

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_Posix_TableMutex_Init(osal_objtype_t idtype)
{
    int                     ret;
    int32                   return_code = OS_SUCCESS;
    pthread_mutexattr_t     mutex_attr;
    OS_impl_objtype_lock_t *impl;

    impl = OS_impl_objtype_lock_table[idtype];

    /* Log entry and state */
    OS_Posix_LogProbe("Enter OS_Posix_TableMutex_Init: idtype=%d, impl_ptr=%p", (int)idtype, (void *)impl);

    if (impl == NULL)
    {
        OS_Posix_LogProbe("impl is NULL for idtype=%d; returning OS_SUCCESS", (int)idtype);
        return OS_SUCCESS;
    }

    do
    {
        /*
         * initialize the pthread mutex attribute structure with default values
         */
        ret = pthread_mutexattr_init(&mutex_attr);
        OS_Posix_LogProbe("pthread_mutexattr_init returned %d (%s)", ret, (ret == 0) ? "OK" : strerror(ret));
        if (ret != 0)
        {
            OS_DEBUG("Error: pthread_mutexattr_init failed: %s\n", strerror(ret));
            return_code = OS_ERROR;
            break;
        }

        /*
         * Allow the mutex to use priority inheritance
         */
        ret = pthread_mutexattr_setprotocol(&mutex_attr, PTHREAD_PRIO_INHERIT);
        OS_Posix_LogProbe("pthread_mutexattr_setprotocol returned %d (%s)", ret, (ret == 0) ? "OK" : strerror(ret));
        if (ret != 0)
        {
            OS_DEBUG("Error: pthread_mutexattr_setprotocol failed: %s\n", strerror(ret));
            return_code = OS_ERROR;
            break;
        }

        /*
         * Use normal (faster/non-recursive) mutex implementation
         * There should not be any instances of OSAL locking its own table more than once.
         */
        ret = pthread_mutexattr_settype(&mutex_attr, PTHREAD_MUTEX_NORMAL);
        OS_Posix_LogProbe("pthread_mutexattr_settype returned %d (%s)", ret, (ret == 0) ? "OK" : strerror(ret));
        if (ret != 0)
        {
            OS_DEBUG("Error: pthread_mutexattr_settype failed: %s\n", strerror(ret));
            return_code = OS_ERROR;
            break;
        }

        ret = pthread_mutex_init(&impl->mutex, &mutex_attr);
        OS_Posix_LogProbe("pthread_mutex_init returned %d (%s) for impl=%p", ret, (ret == 0) ? "OK" : strerror(ret), (void *)impl);
        if (ret != 0)
        {
            OS_DEBUG("Error: pthread_mutex_init failed: %s\n", strerror(ret));
            return_code = OS_ERROR;
            break;
        }

        /* create a condition variable with default attributes.
         * This will be broadcast every time the object table changes */
        ret = pthread_cond_init(&impl->cond, NULL);
        OS_Posix_LogProbe("pthread_cond_init returned %d (%s) for impl=%p", ret, (ret == 0) ? "OK" : strerror(ret), (void *)impl);
        if (ret != 0)
        {
            OS_DEBUG("Error: pthread_cond_init failed: %s\n", strerror(ret));
            return_code = OS_ERROR;
            break;
        }

        OS_Posix_LogProbe("Initialization sequence completed for idtype=%d, impl=%p, return_code=%d", (int)idtype, (void *)impl, (int)return_code);
    } while (0);

    OS_Posix_LogProbe("Exit OS_Posix_TableMutex_Init: idtype=%d, return_code=%d", (int)idtype, (int)return_code);

    return return_code;
}
