#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <pthread.h>

/* Helper probe function to capture control flow and state to a log file.
 * Appends messages to /tmp/logs/354.txt. Keeps original logic unchanged.
 */
static void osal_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/354.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

int32 OS_Posix_TableMutex_Init(osal_objtype_t idtype)
{
    int                     ret;
    int32                   return_code = OS_SUCCESS;
    pthread_mutexattr_t     mutex_attr;
    OS_impl_objtype_lock_t *impl;

    impl = OS_impl_objtype_lock_table[idtype];

    /* Log entry and current table pointer for the given idtype */
    osal_probe_log("OS_Posix_TableMutex_Init enter: idtype=%d impl=%p", (int)idtype, (void *)impl);

    if (impl == NULL)
    {
        osal_probe_log("OS_Posix_TableMutex_Init: impl is NULL for idtype=%d, returning OS_SUCCESS", (int)idtype);
        return OS_SUCCESS;
    }

    do
    {
        /*
         * initialize the pthread mutex attribute structure with default values
         */
        ret = pthread_mutexattr_init(&mutex_attr);
        if (ret != 0)
        {
            /* Probe: record the failing call, return code and strerror for reproduction */
            osal_probe_log("Error: pthread_mutexattr_init failed: ret=%d err=%s", ret, strerror(ret));
            return_code = OS_ERROR;
            break;
        }

        /*
         * Allow the mutex to use priority inheritance
         */
        ret = pthread_mutexattr_setprotocol(&mutex_attr, PTHREAD_PRIO_INHERIT);
        if (ret != 0)
        {
            osal_probe_log("Error: pthread_mutexattr_setprotocol failed: ret=%d err=%s", ret, strerror(ret));
            return_code = OS_ERROR;
            break;
        }

        /*
         * Use normal (faster/non-recursive) mutex implementation
         * There should not be any instances of OSAL locking its own table more than once.
         */
        ret = pthread_mutexattr_settype(&mutex_attr, PTHREAD_MUTEX_NORMAL);
        if (ret != 0)
        {
            osal_probe_log("Error: pthread_mutexattr_settype failed: ret=%d err=%s", ret, strerror(ret));
            return_code = OS_ERROR;
            break;
        }

        ret = pthread_mutex_init(&impl->mutex, &mutex_attr);
        if (ret != 0)
        {
            osal_probe_log("Error: pthread_mutex_init failed: ret=%d err=%s", ret, strerror(ret));
            return_code = OS_ERROR;
            break;
        }

        /* create a condition variable with default attributes.
         * This will be broadcast every time the object table changes */
        ret = pthread_cond_init(&impl->cond, NULL);
        if (ret != 0)
        {
            osal_probe_log("Error: pthread_cond_init failed: ret=%d err=%s", ret, strerror(ret));
            return_code = OS_ERROR;
            break;
        }

    } while (0);

    if (return_code == OS_SUCCESS)
    {
        osal_probe_log("OS_Posix_TableMutex_Init exit: success for idtype=%d", (int)idtype);
    }
    else
    {
        osal_probe_log("OS_Posix_TableMutex_Init exit: failure for idtype=%d return_code=%d", (int)idtype, (int)return_code);
    }

    return return_code;
}
