#include <stdio.h>
#include <string.h>
#include <errno.h>
#include <unistd.h>
#include <mqueue.h>

/* Probe logging helper - appends state information to /tmp/logs/419.txt */
static void OS_QueueCreate_Impl_LogProbe(const char *stage,
                                         const OS_object_token_t *token,
                                         OS_impl_queue_internal_record_t *impl,
                                         OS_queue_internal_record_t *queue,
                                         const struct mq_attr *attr,
                                         const char *name,
                                         mqd_t queueDesc)
{
    FILE *f = fopen("/tmp/logs/419.txt", "a");
    if (f == NULL)
    {
        return;
    }

    fprintf(f, "--- OS_QueueCreate_Impl probe: %s ---\n", stage);
    fprintf(f, "token ptr=%p\n", (const void *)token);
    fprintf(f, "impl ptr=%p\n", (void *)impl);
    fprintf(f, "queue ptr=%p\n", (void *)queue);

    if (queue != NULL)
    {
        /* Print read-only fields which demonstrate the pointer is only used for reads */
        fprintf(f, "queue->max_depth=%lu\n", (unsigned long)queue->max_depth);
        fprintf(f, "queue->max_size=%lu\n", (unsigned long)queue->max_size);
        fprintf(f, "queue->queue_name=%s\n", (queue->queue_name != NULL) ? queue->queue_name : "(null)");
    }

    if (attr != NULL)
    {
        fprintf(f, "attr->mq_maxmsg=%ld\n", (long)attr->mq_maxmsg);
        fprintf(f, "attr->mq_msgsize=%ld\n", (long)attr->mq_msgsize);
    }

    fprintf(f, "POSIX_GlobalVars.TruncateQueueDepth=%ld\n", (long)POSIX_GlobalVars.TruncateQueueDepth);

    if (name != NULL)
    {
        fprintf(f, "generated name=%s\n", name);
    }

    if (queueDesc != (mqd_t)(-1))
    {
        fprintf(f, "mq_open returned descriptor=%ld\n", (long)queueDesc);
    }
    else
    {
        fprintf(f, "mq_open failed, errno=%d (%s)\n", errno, strerror(errno));
    }

    fprintf(f, "--- end probe: %s ---\n\n", stage);
    fclose(f);
}

int32 OS_QueueCreate_Impl(const OS_object_token_t *token, uint32 flags)
{
    int                              return_code;
    mqd_t                            queueDesc;
    struct mq_attr                   queueAttr;
    char                             name[OS_MAX_API_NAME * 2];
    OS_impl_queue_internal_record_t *impl;
    OS_queue_internal_record_t *     queue;

    impl  = OS_OBJECT_TABLE_GET(OS_impl_queue_table, *token);
    queue = OS_OBJECT_TABLE_GET(OS_queue_table, *token);

    /* Log state right after retrieving table entries */
    OS_QueueCreate_Impl_LogProbe("after_table_lookup", token, impl, queue, NULL, NULL, (mqd_t)(-1));

    /* set queue attributes */
    memset(&queueAttr, 0, sizeof(queueAttr));
    queueAttr.mq_maxmsg  = queue->max_depth;
    queueAttr.mq_msgsize = queue->max_size;

    /* Log attributes after initial set */
    OS_QueueCreate_Impl_LogProbe("after_attr_set", token, impl, queue, &queueAttr, NULL, (mqd_t)(-1));

    /*
     * The "TruncateQueueDepth" indicates a soft limit to the size of a queue.
     * If nonzero, anything larger than this will be silently truncated
     * (Supports running applications as non-root)
     */
    if (POSIX_GlobalVars.TruncateQueueDepth > 0 && POSIX_GlobalVars.TruncateQueueDepth < queueAttr.mq_maxmsg)
    {
        queueAttr.mq_maxmsg = POSIX_GlobalVars.TruncateQueueDepth;
    }

    /* Log attributes after possible truncation */
    OS_QueueCreate_Impl_LogProbe("after_truncate_check", token, impl, queue, &queueAttr, NULL, (mqd_t)(-1));

    /*
    ** Construct the queue name:
    ** The name will consist of "/<process_id>.queue_name"
    */
    snprintf(name, sizeof(name), "/%d.%s", (int)getpid(), queue->queue_name);

    /* Log the generated name */
    OS_QueueCreate_Impl_LogProbe("after_name_constructed", token, impl, queue, &queueAttr, name, (mqd_t)(-1));

    /*
     ** create message queue
     */
    queueDesc = mq_open(name, O_CREAT | O_RDWR, 0666, &queueAttr);

    /* Log result of mq_open */
    OS_QueueCreate_Impl_LogProbe("after_mq_open", token, impl, queue, &queueAttr, name, queueDesc);

    if (queueDesc == (mqd_t)(-1))
    {
        OS_DEBUG("OS_QueueCreate Error. errno = %d (%s)\n", errno, strerror(errno));
        if (errno == EINVAL)
        {
            OS_DEBUG("Your queue depth may be too large for the\n");
            OS_DEBUG("OS to handle. Please check the msg_max\n");
            OS_DEBUG("parameter located in /proc/sys/fs/mqueue/msg_max\n");
            OS_DEBUG("on your Linux file system and raise it if you\n");
            OS_DEBUG(" need to or run as root\n");
        }
        return_code = OS_ERROR;
    }
    else
    {
        impl->id    = queueDesc;
        return_code = OS_SUCCESS;

        /*
         * Unlink the queue right now --
         * queues have kernel persistence and if we do a lot of restarts (i.e. during debugging)
         * a lot of stale message queues will collect in the system.  It is OK to unlink right now
         * as this only affects the ability of another process to open the same queue, but we do
         * not need that to happen anyway.
         */
        if (mq_unlink(name) != 0)
        {
            OS_DEBUG("OS_QueueDelete Error during mq_unlink(). errno = %d (%s)\n", errno, strerror(errno));
            /* Note - since the queue is already closed, we cannot really handle this failure gracefully,
             * The queue is no longer usable so we can't return an error and go back to the way things were.
             * In this case we need to return OS_SUCCESS so the rest of the entry will be cleaned up.
             */
        }
    }

    /* Final state log before return */
    OS_QueueCreate_Impl_LogProbe("final", token, impl, queue, &queueAttr, name, queueDesc);

    return return_code;
}
