#include <stdio.h>
#include <errno.h>
#include <string.h>
#include <stdint.h>
#include <mqueue.h>

int32 OS_QueueDelete_Impl(const OS_object_token_t *token)
{
    int32                            return_code;
    OS_impl_queue_internal_record_t *impl;
    FILE *logf = fopen("/tmp/logs/427.txt", "a");

    if (logf != NULL)
    {
        fprintf(logf, "ENTER OS_QueueDelete_Impl: token=%p\n", (const void *)token);
        fflush(logf);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_queue_table, *token);

    if (logf != NULL)
    {
        fprintf(logf, "impl pointer = %p\n", (void *)impl);
        fflush(logf);
    }

    /* Try to delete and unlink the queue */
    if (impl != NULL)
    {
        /* Log the id value in a portable way using uintptr_t */
        if (logf != NULL)
        {
            fprintf(logf, "impl->id = %llu\n", (unsigned long long)(uintptr_t)impl->id);
            fflush(logf);
        }
    }
    else
    {
        if (logf != NULL)
        {
            fprintf(logf, "Warning: impl is NULL before mq_close()\n");
            fflush(logf);
        }
    }

    {
        int mqret = mq_close(impl->id);

        if (logf != NULL)
        {
            fprintf(logf, "mq_close returned %d, errno = %d (%s)\n", mqret, errno, strerror(errno));
            fflush(logf);
        }

        if (mqret != 0)
        {
            OS_DEBUG("OS_QueueDelete Error during mq_close(). errno = %d (%s)\n", errno, strerror(errno));
            return_code = OS_ERROR;
        }
        else
        {
            return_code = OS_SUCCESS;
        }
    }

    if (logf != NULL)
    {
        fprintf(logf, "EXIT OS_QueueDelete_Impl: return_code = %ld\n", (long)return_code);
        fclose(logf);
    }

    return return_code;
}
