#include <stdio.h>
#include <stdarg.h>

static void os_select_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/658.txt", "a");
    if (!f) return;

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);

    fclose(f);
}

int32 OS_SelectSingle_Impl(const OS_object_token_t *token, uint32 *SelectFlags, int32 msecs)
{
    int32                           return_code;
    fd_set                          wr_set;
    fd_set                          rd_set;
    OS_impl_file_internal_record_t *impl;

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    os_select_log("enter: token=%p, impl=%p, SelectFlags_in=0x%08x, msecs=%d",
                  (void *)token, (void *)impl, (unsigned int)(*SelectFlags), (int)msecs);

    /*
     * If called on a stream_id which does not support this
     * operation, return immediately and do not invoke the system call
     */
    if (!impl->selectable)
    {
        os_select_log("path: not selectable -> return OS_ERR_OPERATION_NOT_SUPPORTED");
        return OS_ERR_OPERATION_NOT_SUPPORTED;
    }

    if (impl->fd >= FD_SETSIZE)
    {
        /* out of range of select() implementation */
        os_select_log("path: fd out of range (fd=%d, FD_SETSIZE=%d) -> return OS_ERR_OPERATION_NOT_SUPPORTED",
                      (int)impl->fd, (int)FD_SETSIZE);
        return OS_ERR_OPERATION_NOT_SUPPORTED;
    }

    if (*SelectFlags != 0)
    {
        int want_read = (*SelectFlags & OS_STREAM_STATE_READABLE) != 0;
        int want_write = (*SelectFlags & OS_STREAM_STATE_WRITABLE) != 0;

        os_select_log("path: SelectFlags != 0; want_read=%d, want_write=%d", want_read, want_write);

        FD_ZERO(&wr_set);
        FD_ZERO(&rd_set);
        if (*SelectFlags & OS_STREAM_STATE_READABLE)
        {
            FD_SET(impl->fd, &rd_set);
            os_select_log("FD_SET: fd=%d added to rd_set", (int)impl->fd);
        }
        if (*SelectFlags & OS_STREAM_STATE_WRITABLE)
        {
            FD_SET(impl->fd, &wr_set);
            os_select_log("FD_SET: fd=%d added to wr_set", (int)impl->fd);
        }

        return_code = OS_DoSelect(impl->fd, &rd_set, &wr_set, msecs);
        os_select_log("OS_DoSelect returned %d for fd=%d, msecs=%d", (int)return_code, (int)impl->fd, (int)msecs);

        if (return_code == OS_SUCCESS)
        {
            int is_readable = FD_ISSET(impl->fd, &rd_set) ? 1 : 0;
            int is_writable = FD_ISSET(impl->fd, &wr_set) ? 1 : 0;

            os_select_log("select result: fd=%d readable=%d writable=%d", (int)impl->fd, is_readable, is_writable);

            if (!is_readable)
            {
                *SelectFlags &= ~OS_STREAM_STATE_READABLE;
                os_select_log("cleared READABLE flag; SelectFlags_now=0x%08x", (unsigned int)(*SelectFlags));
            }
            if (!is_writable)
            {
                *SelectFlags &= ~OS_STREAM_STATE_WRITABLE;
                os_select_log("cleared WRITABLE flag; SelectFlags_now=0x%08x", (unsigned int)(*SelectFlags));
            }
        }
        else
        {
            *SelectFlags = 0;
            os_select_log("select failed -> SelectFlags set to 0");
        }
    }
    else
    {
        /* Nothing to check for, return immediately. */
        return_code = OS_SUCCESS;
        os_select_log("path: SelectFlags == 0 -> return OS_SUCCESS");
    }

    os_select_log("exit: return_code=%d, SelectFlags_out=0x%08x", (int)return_code, (unsigned int)(*SelectFlags));

    return return_code;
}
