#include <stdio.h>
#include <errno.h>
#include <string.h>

/* Probe logging helper for OS_SocketAccept_Impl
 * Appends human-readable state and control-flow info to /tmp/logs/819.txt
 */
static void OS_SocketAccept_LogState(const char *stage,
                                    const OS_impl_file_internal_record_t *sock_impl,
                                    const OS_impl_file_internal_record_t *conn_impl,
                                    const OS_SockAddr_t *Addr,
                                    int32 timeout,
                                    uint32 operation,
                                    int32 return_code,
                                    socklen_t addrlen)
{
    FILE *f = fopen("/tmp/logs/819.txt", "a");
    if (f == NULL)
    {
        return; /* Best-effort logging only */
    }

    fprintf(f, "[OS_SocketAccept] stage=%s\n", stage);
    fprintf(f, "  timeout=%ld, operation=0x%08lx, return_code=%ld\n",
            (long)timeout, (unsigned long)operation, (long)return_code);

    if (sock_impl != NULL)
    {
        /* sock_impl contents are only read here for logging */
        fprintf(f, "  sock_impl=%p, selectable=%d, fd=%d\n",
                (void *)sock_impl,
                (int)sock_impl->selectable,
                (int)sock_impl->fd);
    }
    else
    {
        fprintf(f, "  sock_impl=NULL\n");
    }

    if (conn_impl != NULL)
    {
        fprintf(f, "  conn_impl=%p, fd(before)=%d\n",
                (void *)conn_impl,
                (int)conn_impl->fd);
    }
    else
    {
        fprintf(f, "  conn_impl=NULL\n");
    }

    if (Addr != NULL)
    {
        fprintf(f, "  Addr=%p, Addr->ActualLength=%lu, addrlen(param)=%lu\n",
                (void *)Addr,
                (unsigned long)Addr->ActualLength,
                (unsigned long)addrlen);
    }
    else
    {
        fprintf(f, "  Addr=NULL\n");
    }

    if (return_code != OS_SUCCESS && errno != 0)
    {
        fprintf(f, "  errno=%d (%s)\n", errno, strerror(errno));
    }

    fprintf(f, "---\n");
    fclose(f);
}

int32 OS_SocketAccept_Impl(const OS_object_token_t *sock_token, const OS_object_token_t *conn_token,
                           OS_SockAddr_t *Addr, int32 timeout)
{
    int32                           return_code;
    uint32                          operation;
    socklen_t                       addrlen;
    OS_impl_file_internal_record_t *sock_impl;
    OS_impl_file_internal_record_t *conn_impl;

    /* Log entry into the function with tokens and timeout */
    {
        FILE *f = fopen("/tmp/logs/819.txt", "a");
        if (f != NULL)
        {
            fprintf(f, "Entering OS_SocketAccept_Impl: sock_token=%p, conn_token=%p, timeout=%ld\n",
                    (void *)sock_token, (void *)conn_token, (long)timeout);
            fclose(f);
        }
    }

    sock_impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *sock_token);
    conn_impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *conn_token);

    /* Log state after resolving implementation records */
    OS_SocketAccept_LogState("after_lookup", sock_impl, conn_impl, Addr, timeout, 0u, -1, 0u);

    operation = OS_STREAM_STATE_READABLE;
    if (sock_impl->selectable)
    {
        /* Log before calling select implementation */
        OS_SocketAccept_LogState("before_select", sock_impl, conn_impl, Addr, timeout, operation, -1, 0u);

        return_code = OS_SelectSingle_Impl(sock_token, &operation, timeout);

        /* Log after select returns */
        OS_SocketAccept_LogState("after_select", sock_impl, conn_impl, Addr, timeout, operation, return_code, 0u);
    }
    else
    {
        return_code = OS_SUCCESS;

        /* Log branch where socket is not selectable */
        OS_SocketAccept_LogState("not_selectable", sock_impl, conn_impl, Addr, timeout, operation, return_code, 0u);
    }

    if (return_code == OS_SUCCESS)
    {
        if ((operation & OS_STREAM_STATE_READABLE) == 0)
        {
            return_code = OS_ERROR_TIMEOUT;

            /* Log timeout outcome */
            OS_SocketAccept_LogState("timeout_path", sock_impl, conn_impl, Addr, timeout, operation, return_code, 0u);
        }
        else
        {
            addrlen       = Addr->ActualLength;

            /* Log just before accept to capture fd and addrlen */
            OS_SocketAccept_LogState("before_accept", sock_impl, conn_impl, Addr, timeout, operation, return_code, addrlen);

            conn_impl->fd = accept(sock_impl->fd, (struct sockaddr *)&Addr->AddrData, &addrlen);

            /* Log result of accept including errno if any */
            OS_SocketAccept_LogState("after_accept", sock_impl, conn_impl, Addr, timeout, operation, return_code, addrlen);

            if (conn_impl->fd < 0)
            {
                return_code = OS_ERROR;

                /* Log error branch */
                OS_SocketAccept_LogState("accept_failed", sock_impl, conn_impl, Addr, timeout, operation, return_code, addrlen);
            }
            else
            {
                Addr->ActualLength = addrlen;

                /* Log success before setting socket flags */
                OS_SocketAccept_LogState("accept_succeeded_before_flags", sock_impl, conn_impl, Addr, timeout, operation, return_code, addrlen);

                OS_IMPL_SET_SOCKET_FLAGS(conn_token);

                /* Log after setting flags */
                OS_SocketAccept_LogState("accept_succeeded_after_flags", sock_impl, conn_impl, Addr, timeout, operation, return_code, addrlen);
            }
        }
    }

    /* Final log and return */
    OS_SocketAccept_LogState("return", sock_impl, conn_impl, Addr, timeout, operation, return_code, 0u);

    return return_code;
}
