#include <stdio.h>
#include <stdarg.h>
#include <time.h>
#include <string.h>
#include <errno.h>

/* Probe logging helper: appends messages to /tmp/logs/867.txt */
static void OS_SocketBindAddress_Impl_Log(const char *fmt, ...)
{
    FILE *fp = fopen("/tmp/logs/867.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    time_t t = time(NULL);
    struct tm tm_buf;
#if defined(_POSIX_VERSION)
    localtime_r(&t, &tm_buf);
    char timestr[64];
    strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", &tm_buf);
#else
    char *timestr = ctime(&t);
#endif

    fprintf(fp, "[%s] ", timestr);

    va_list ap;
    va_start(ap, fmt);
    vfprintf(fp, fmt, ap);
    va_end(ap);

    fprintf(fp, "\n");
    fclose(fp);
}

int32 OS_SocketBindAddress_Impl(const OS_object_token_t *token, const OS_SockAddr_t *Addr)
{
    int                             os_result;
    socklen_t                       addrlen;
    const struct sockaddr *         sa;
    OS_impl_file_internal_record_t *impl;

    /* Probe: record entry and token pointer */
    OS_SocketBindAddress_Impl_Log("ENTRY: token=%p, Addr=%p", (const void *)token, (const void *)Addr);

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    /* Probe: record impl pointer */
    OS_SocketBindAddress_Impl_Log("LOOKUP: impl=%p", (const void *)impl);

    sa = (const struct sockaddr *)&Addr->AddrData;

    /* Probe: record address family */
    if (sa != NULL)
    {
        OS_SocketBindAddress_Impl_Log("ADDR: sa=%p, sa_family=%d", (const void *)sa, (int)sa->sa_family);
    }
    else
    {
        OS_SocketBindAddress_Impl_Log("ADDR: sa=NULL");
    }

    switch (sa->sa_family)
    {
        case AF_INET:
            addrlen = sizeof(struct sockaddr_in);
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            addrlen = sizeof(struct sockaddr_in6);
            break;
#endif
        default:
            addrlen = 0;
            break;
    }

    /* Probe: record computed addrlen */
    OS_SocketBindAddress_Impl_Log("ADDRLEN: %u", (unsigned)addrlen);

    if (addrlen == 0)
    {
        OS_SocketBindAddress_Impl_Log("EXIT: Bad address (addrlen=0)");
        return OS_ERR_BAD_ADDRESS;
    }

    /* Probe: record FD before bind (impl may be NULL in erroneous situations) */
    if (impl != NULL)
    {
        OS_SocketBindAddress_Impl_Log("PRE_BIND: impl=%p, fd=%d", (const void *)impl, impl->fd);
    }
    else
    {
        OS_SocketBindAddress_Impl_Log("PRE_BIND: impl=NULL (will attempt bind and likely crash)");
    }

    os_result = bind(impl->fd, sa, addrlen);

    /* Probe: record result of bind and errno/string on failure */
    if (os_result < 0)
    {
        int err = errno;
        OS_SocketBindAddress_Impl_Log("BIND_FAIL: result=%d, errno=%d, strerror=%s", os_result, err, strerror(err));
        OS_DEBUG("bind: %s\n", strerror(errno));
        OS_SocketBindAddress_Impl_Log("EXIT: OS_ERROR");
        return OS_ERROR;
    }

    OS_SocketBindAddress_Impl_Log("BIND_OK: result=%d", os_result);
    OS_SocketBindAddress_Impl_Log("EXIT: OS_SUCCESS");

    return OS_SUCCESS;
}
