int32 OS_SocketConnect_Impl(const OS_object_token_t *token, const OS_SockAddr_t *Addr, int32 timeout)
{
    int32                           return_code;
    int                             os_status;
    int                             sockopt;
    socklen_t                       slen;
    uint32                          operation;
    const struct sockaddr *         sa;
    OS_impl_file_internal_record_t *impl;

    /* Open log file for probes; if opening fails, proceed without logging */
    FILE *log = fopen("/tmp/logs/875.txt", "a");
    if (log)
    {
        fprintf(log, "ENTER OS_SocketConnect_Impl: token=%p Addr=%p timeout=%ld\n",
                (const void *)token, (const void *)Addr, (long)timeout);
    }

    impl = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);

    if (log)
    {
        fprintf(log, "impl lookup: impl=%p\n", (void *)impl);
        if (impl != NULL)
        {
            fprintf(log, "impl->fd=%d impl->selectable=%d\n", impl->fd, impl->selectable);
        }
    }

    sa = (const struct sockaddr *)&Addr->AddrData;
    switch (sa->sa_family)
    {
        case AF_INET:
            slen = sizeof(struct sockaddr_in);
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case AF_INET6:
            slen = sizeof(struct sockaddr_in6);
            break;
#endif
        default:
            slen = 0;
            break;
    }

    if (log)
    {
        fprintf(log, "sa_family=%d computed_slen=%u Addr->ActualLength=%u\n",
                (int)sa->sa_family, (unsigned)slen, (unsigned)Addr->ActualLength);
    }

    if (slen != Addr->ActualLength)
    {
        if (log)
        {
            fprintf(log, "BAD ADDRESS: expected_len=%u actual_len=%u\n",
                    (unsigned)slen, (unsigned)Addr->ActualLength);
        }
        return_code = OS_ERR_BAD_ADDRESS;
    }
    else
    {
        return_code = OS_SUCCESS;
        os_status   = connect(impl->fd, sa, slen);

        if (log)
        {
            fprintf(log, "connect returned os_status=%d\n", os_status);
        }

        if (os_status < 0)
        {
            /* save errno immediately to avoid changes by logging functions */
            int saved_errno = errno;

            if (saved_errno != EINPROGRESS)
            {
                if (log)
                {
                    fprintf(log, "connect error: errno=%d (%s)\n", saved_errno, strerror(saved_errno));
                }
                OS_DEBUG("connect: %s\n", strerror(saved_errno));
                return_code = OS_ERROR;
            }
            else
            {
                /* EINPROGRESS branch - non-blocking connect in progress */
                if (log)
                {
                    fprintf(log, "connect returned EINPROGRESS (non-blocking)\n");
                }

                operation = OS_STREAM_STATE_WRITABLE;
                if (impl->selectable)
                {
                    if (log)
                    {
                        fprintf(log, "calling OS_SelectSingle_Impl token=%p operation=0x%08x timeout=%ld\n",
                                (const void *)token, (unsigned)operation, (long)timeout);
                    }

                    return_code = OS_SelectSingle_Impl(token, &operation, timeout);

                    if (log)
                    {
                        fprintf(log, "OS_SelectSingle_Impl returned %ld operation=0x%08x\n",
                                (long)return_code, (unsigned)operation);
                    }
                }
                if (return_code == OS_SUCCESS)
                {
                    if ((operation & OS_STREAM_STATE_WRITABLE) == 0)
                    {
                        if (log)
                        {
                            fprintf(log, "select indicated timeout/not writable: operation=0x%08x\n",
                                    (unsigned)operation);
                        }
                        return_code = OS_ERROR_TIMEOUT;
                    }
                    else
                    {
                        /* Check SO_ERROR on the socket to ensure connect succeeded */
                        sockopt   = 0;
                        slen      = sizeof(sockopt);
                        os_status = getsockopt(impl->fd, SOL_SOCKET, SO_ERROR, &sockopt, &slen);

                        if (log)
                        {
                            fprintf(log, "getsockopt returned os_status=%d sockopt=%d slen=%u\n",
                                    os_status, sockopt, (unsigned)slen);
                        }

                        if (os_status < 0 || sockopt != 0)
                        {
                            return_code = OS_ERROR;
                        }
                    }
                }
            }
        }
    }

    if (log)
    {
        fprintf(log, "EXIT OS_SocketConnect_Impl: return_code=%ld\n", (long)return_code);
        fclose(log);
    }

    return return_code;
}
