#include <stdio.h>
#include <stdarg.h>

/* Probe helper: append diagnostic messages to /tmp/logs/890.txt.
 * This function is intentionally lightweight and preserves original logic.
 */
static void os_socketopen_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/890.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

int32 OS_SocketOpen_Impl(const OS_object_token_t *token)
{
    int                             os_domain;
    int                             os_type;
    int                             os_proto;
    int                             os_flags;
    OS_impl_file_internal_record_t *impl;
    OS_stream_internal_record_t *   stream;

    impl   = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);
    stream = OS_OBJECT_TABLE_GET(OS_stream_table, *token);

    /* Log entry and pointers */
    os_socketopen_log("OS_SocketOpen_Impl ENTRY: token=%p, impl=%p, stream=%p", (const void *)token, (const void *)impl, (const void *)stream);

    os_proto = 0;
    os_socketopen_log("Initial os_proto set to %d", os_proto);

    switch (stream->socket_type)
    {
        case OS_SocketType_DATAGRAM:
            os_socketopen_log("socket_type: DATAGRAM (case)");
            os_type  = SOCK_DGRAM;
            os_proto = IPPROTO_UDP;
            os_socketopen_log("After DATAGRAM: os_type=%d, os_proto=%d", os_type, os_proto);
            break;

        case OS_SocketType_STREAM:
            os_socketopen_log("socket_type: STREAM (case)");
            os_type  = SOCK_STREAM;
            os_proto = IPPROTO_TCP;
            os_socketopen_log("After STREAM: os_type=%d, os_proto=%d", os_type, os_proto);
            break;

        default:
            os_socketopen_log("socket_type: DEFAULT - not implemented, returning OS_ERR_NOT_IMPLEMENTED");
            return OS_ERR_NOT_IMPLEMENTED;
    }

    switch (stream->socket_domain)
    {
        case OS_SocketDomain_INET:
            os_socketopen_log("socket_domain: INET (case)");
            os_domain = AF_INET;
            os_socketopen_log("After INET: os_domain=%d", os_domain);
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case OS_SocketDomain_INET6:
            os_socketopen_log("socket_domain: INET6 (case)");
            os_domain = AF_INET6;
            os_socketopen_log("After INET6: os_domain=%d", os_domain);
            break;
#endif
        default:
            os_socketopen_log("socket_domain: DEFAULT - not implemented, returning OS_ERR_NOT_IMPLEMENTED");
            return OS_ERR_NOT_IMPLEMENTED;
    }

    os_socketopen_log("About to call socket() with os_domain=%d, os_type=%d, os_proto=%d", os_domain, os_type, os_proto);
    impl->fd = socket(os_domain, os_type, os_proto);
    if (impl->fd < 0)
    {
        os_socketopen_log("socket() failed, fd=%d, returning OS_ERROR", impl->fd);
        return OS_ERROR;
    }

    os_socketopen_log("socket() succeeded, fd=%d", impl->fd);

    /*
     * Setting the REUSEADDR flag helps during debugging when there might be frequent
     * code restarts.  However if setting the option fails then it is not worth bailing out over.
     */
    os_flags = 1;
    os_socketopen_log("Setting SO_REUSEADDR on fd=%d (os_flags=%d)", impl->fd, os_flags);
    setsockopt(impl->fd, SOL_SOCKET, SO_REUSEADDR, &os_flags, sizeof(os_flags));

    /*
     * Set the standard options on the filehandle by default --
     * this may set it to non-blocking mode if the implementation supports it.
     * any blocking would be done explicitly via the select() wrappers
     *
     * NOTE: The implementation still generally works without this flag set, but
     * nonblock mode does improve robustness in the event that multiple tasks
     * attempt to accept new connections from the same server socket at the same time.
     */
    os_socketopen_log("Calling OS_IMPL_SET_SOCKET_FLAGS for token=%p", (const void *)token);
    OS_IMPL_SET_SOCKET_FLAGS(token);
    os_socketopen_log("OS_IMPL_SET_SOCKET_FLAGS returned for token=%p", (const void *)token);

    os_socketopen_log("OS_SocketOpen_Impl EXIT: returning OS_SUCCESS");
    return OS_SUCCESS;
}
