#include <stdio.h>
#include <errno.h>
#include <string.h>

/* Probe/log helper to capture control flow and state for OS_SocketOpen_Impl.
 * Appends logs to /tmp/logs/891.txt.
 */
static void OS_SocketOpen_LogState(const OS_object_token_t *token,
                                  const OS_stream_internal_record_t *stream,
                                  OS_impl_file_internal_record_t *impl,
                                  const char *stage,
                                  int os_domain,
                                  int os_type,
                                  int os_proto)
{
    FILE *f = fopen("/tmp/logs/891.txt", "a");
    if (f == NULL)
    {
        return;
    }

    fprintf(f, "Stage: %s\n", stage ? stage : "(null)");
    fprintf(f, "token: %p\n", (const void *)token);

    if (stream != NULL)
    {
        /* log the two fields reported in the violation scenario */
        fprintf(f, "stream: %p, socket_type: %d, socket_domain: %d\n",
                (const void *)stream,
                (int)stream->socket_type,
                (int)stream->socket_domain);
    }
    else
    {
        fprintf(f, "stream: NULL\n");
    }

    if (impl != NULL)
    {
        fprintf(f, "impl: %p, fd: %d\n", (void *)impl, impl->fd);
    }
    else
    {
        fprintf(f, "impl: NULL\n");
    }

    if (os_domain >= 0 || os_type >= 0 || os_proto >= 0)
    {
        fprintf(f, "os_domain: %d, os_type: %d, os_proto: %d\n",
                os_domain, os_type, os_proto);
    }

    fprintf(f, "errno: %d (%s)\n", errno, strerror(errno));
    fprintf(f, "----\n");
    fclose(f);
}

int32 OS_SocketOpen_Impl(const OS_object_token_t *token)
{
    int                             os_domain;
    int                             os_type;
    int                             os_proto;
    int                             os_flags;
    OS_impl_file_internal_record_t *impl;
    OS_stream_internal_record_t *   stream;

    impl   = OS_OBJECT_TABLE_GET(OS_impl_filehandle_table, *token);
    stream = OS_OBJECT_TABLE_GET(OS_stream_table, *token);

    /* Log state after object table lookups */
    OS_SocketOpen_LogState(token, stream, impl, "after_lookup", -1, -1, -1);

    os_proto = 0;

    switch (stream->socket_type)
    {
        case OS_SocketType_DATAGRAM:
            os_type  = SOCK_DGRAM;
            os_proto = IPPROTO_UDP;
            break;

        case OS_SocketType_STREAM:
            os_type  = SOCK_STREAM;
            os_proto = IPPROTO_TCP;
            break;

        default:
            /* Log unsupported socket type and return */
            OS_SocketOpen_LogState(token, stream, impl, "unsupported_socket_type", -1, -1, -1);
            return OS_ERR_NOT_IMPLEMENTED;
    }

    switch (stream->socket_domain)
    {
        case OS_SocketDomain_INET:
            os_domain = AF_INET;
            break;
#ifdef OS_NETWORK_SUPPORTS_IPV6
        case OS_SocketDomain_INET6:
            os_domain = AF_INET6;
            break;
#endif
        default:
            /* Log unsupported socket domain and return */
            OS_SocketOpen_LogState(token, stream, impl, "unsupported_socket_domain", -1, -1, -1);
            return OS_ERR_NOT_IMPLEMENTED;
    }

    /* Log parameters selected for socket creation */
    OS_SocketOpen_LogState(token, stream, impl, "before_socket", os_domain, os_type, os_proto);

    impl->fd = socket(os_domain, os_type, os_proto);
    if (impl->fd < 0)
    {
        /* Log socket failure including errno */
        OS_SocketOpen_LogState(token, stream, impl, "socket_failed", os_domain, os_type, os_proto);
        return OS_ERROR;
    }

    /* Log successful socket creation */
    OS_SocketOpen_LogState(token, stream, impl, "socket_created", os_domain, os_type, os_proto);

    /*
     * Setting the REUSEADDR flag helps during debugging when there might be frequent
     * code restarts.  However if setting the option fails then it is not worth bailing out over.
     */
    os_flags = 1;
    setsockopt(impl->fd, SOL_SOCKET, SO_REUSEADDR, &os_flags, sizeof(os_flags));

    /*
     * Set the standard options on the filehandle by default --
     * this may set it to non-blocking mode if the implementation supports it.
     * any blocking would be done explicitly via the select() wrappers
     *
     * NOTE: The implementation still generally works without this flag set, but
     * nonblock mode does improve robustness in the event that multiple tasks
     * attempt to accept new connections from the same server socket at the same time.
     */
    OS_IMPL_SET_SOCKET_FLAGS(token);

    /* Final state before successful return */
    OS_SocketOpen_LogState(token, stream, impl, "return_success", os_domain, os_type, os_proto);

    return OS_SUCCESS;
}
