int32 OS_TaskCreate(osal_id_t *task_id, const char *task_name, osal_task_entry function_pointer,
                    osal_stackptr_t stack_pointer, size_t stack_size, osal_priority_t priority, uint32 flags)
{
    int32                      return_code;
    OS_object_token_t          token;
    OS_task_internal_record_t *task;

    FILE *logf = NULL;

    /* Open the probe log (best-effort). */
    logf = fopen("/tmp/logs/2026.txt", "a");
    if (logf != NULL)
    {
        fprintf(logf, "OS_TaskCreate ENTRY: task_name=\"%s\", function_pointer=%p, stack_pointer=%p, stack_size=%zu, priority=%u, flags=0x%08x\n",
                task_name ? task_name : "(null)", (void *)function_pointer, (void *)stack_pointer, stack_size, (unsigned)priority, (unsigned)flags);
        fflush(logf);
    }

    /*
     * Check parameters
     *
     * Note "stack_pointer" is not checked, because in certain configurations it can be validly null.
     */
    OS_CHECK_POINTER(task_id);
    OS_CHECK_POINTER(function_pointer);
    OS_CHECK_APINAME(task_name);
    OS_CHECK_SIZE(stack_size);

    /* Note - the common ObjectIdAllocate routine will lock the object type and leave it locked. */
    return_code = OS_ObjectIdAllocateNew(LOCAL_OBJID_TYPE, task_name, &token);

    if (logf != NULL)
    {
        fprintf(logf, "OS_TaskCreate after OS_ObjectIdAllocateNew: return_code=%d, token=%u\n", (int)return_code, (unsigned)token);
        fflush(logf);
    }

    if (return_code == OS_SUCCESS)
    {
        task = OS_OBJECT_TABLE_GET(OS_task_table, token);

        /* Reset the table entry and save the name */
        OS_OBJECT_INIT(token, task, task_name, task_name);

        task->stack_size             = stack_size;
        task->priority               = priority;
        task->entry_function_pointer = function_pointer;
        task->stack_pointer          = stack_pointer;

        /* Add default flags */
        if (logf != NULL)
        {
            fprintf(logf, "OS_TaskCreate before flags OR: flags_in=0x%08x, OS_ADD_TASK_FLAGS=0x%08x\n", (unsigned)flags, (unsigned)OS_ADD_TASK_FLAGS);
            fflush(logf);
        }

        flags |= OS_ADD_TASK_FLAGS;

        if (logf != NULL)
        {
            fprintf(logf, "OS_TaskCreate after flags OR: flags_out=0x%08x\n", (unsigned)flags);
            fflush(logf);
        }

        /* Now call the OS-specific implementation.  This reads info from the task table. */
        return_code = OS_TaskCreate_Impl(&token, flags);

        if (logf != NULL)
        {
            fprintf(logf, "OS_TaskCreate after OS_TaskCreate_Impl: return_code=%d\n", (int)return_code);
            fflush(logf);
        }

        /* Check result, finalize record, and unlock global table. */
        return_code = OS_ObjectIdFinalizeNew(return_code, &token, task_id);

        if (logf != NULL)
        {
            fprintf(logf, "OS_TaskCreate after OS_ObjectIdFinalizeNew: final_return_code=%d, allocated_task_id=%p\n",
                    (int)return_code, (void *)task_id);
            fflush(logf);
        }
    }

    if (logf != NULL)
    {
        fprintf(logf, "OS_TaskCreate EXIT: return_code=%d\n\n", (int)return_code);
        fclose(logf);
    }

    return return_code;
}
