#include <stdio.h>
#include <string.h>
#include <stddef.h>

int32 OS_TranslatePath(const char *VirtualPath, char *LocalPath)
{
    OS_object_token_t             token;
    int32                         return_code;
    const char *                  name_ptr;
    OS_filesys_internal_record_t *filesys;
    size_t                        SysMountPointLen;
    size_t                        VirtPathLen;
    size_t                        VirtPathBegin;

    /*
    ** Check to see if the path pointers are NULL
    */
    /* Check parameters */
    OS_CHECK_POINTER(VirtualPath);
    OS_CHECK_POINTER(LocalPath);

    /*
    ** Check length
    */
    VirtPathLen = OS_strnlen(VirtualPath, OS_MAX_PATH_LEN);
    if (VirtPathLen >= OS_MAX_PATH_LEN)
    {
        return OS_FS_ERR_PATH_TOO_LONG;
    }

    /* checks to see if there is a '/' somewhere in the path */
    name_ptr = strrchr(VirtualPath, '/');
    if (name_ptr == NULL)
    {
        return OS_FS_ERR_PATH_INVALID;
    }

    /* strrchr returns a pointer to the last '/' char, so we advance one char */
    name_ptr = name_ptr + 1;

    /*
     * Probe/Logging: record state before memchr that may read beyond the
     * end of the VirtualPath allocation. This writes to /tmp/logs/1431.txt
     * so other agents can reproduce and gather evidence.
     */
    {
        FILE *log = fopen("/tmp/logs/1431.txt", "a");
        if (log != NULL)
        {
            size_t name_index = 0u;
            size_t remaining_bytes = 0u;

            if (name_ptr >= VirtualPath)
            {
                name_index = (size_t)(name_ptr - VirtualPath);
                if (VirtPathLen >= name_index)
                {
                    /* include the NUL terminator in remaining_bytes */
                    remaining_bytes = VirtPathLen - name_index + 1u;
                }
                else
                {
                    remaining_bytes = 0u; /* defensive: should not normally happen */
                }
            }

            fprintf(log, "OS_TranslatePath ENTRY: VirtualPath=%p, LocalPath=%p, VirtPathLen=%zu\n",
                    (const void *)VirtualPath, (void *)LocalPath, VirtPathLen);
            fprintf(log, "name_ptr after strrchr: name_ptr=%p, name_index=%zu, remaining_bytes=%zu, OS_MAX_FILE_NAME=%zu\n",
                    (const void *)name_ptr, name_index, remaining_bytes, (size_t)OS_MAX_FILE_NAME);

            if (remaining_bytes < (size_t)OS_MAX_FILE_NAME)
            {
                fprintf(log, "WARNING: memchr(name_ptr, 0, OS_MAX_FILE_NAME) may read past the end of the VirtualPath buffer: remaining_bytes=%zu < OS_MAX_FILE_NAME=%zu\n",
                        remaining_bytes, (size_t)OS_MAX_FILE_NAME);
            }
            else
            {
                fprintf(log, "INFO: memchr will search within computed remaining bytes: remaining_bytes=%zu >= OS_MAX_FILE_NAME=%zu\n",
                        remaining_bytes, (size_t)OS_MAX_FILE_NAME);
            }

            fclose(log);
        }
    }

    /* Preserve original memchr usage but capture the result so it can be logged */
    {
        void *memchr_result = memchr(name_ptr, 0, (size_t)OS_MAX_FILE_NAME);

        /* log the memchr outcome */
        FILE *log = fopen("/tmp/logs/1431.txt", "a");
        if (log != NULL)
        {
            fprintf(log, "memchr(name_ptr, 0, OS_MAX_FILE_NAME) -> %p\n", memchr_result);
            if (memchr_result == NULL)
            {
                fprintf(log, "memchr returned NULL -> returning OS_FS_ERR_NAME_TOO_LONG\n");
            }
            fclose(log);
        }

        if (memchr_result == NULL)
        {
            return OS_FS_ERR_NAME_TOO_LONG;
        }
    }

    SysMountPointLen = 0;
    VirtPathBegin    = VirtPathLen;

    /*
    ** All valid Virtual paths must start with a '/' character
    */
    if (VirtualPath[0] != '/')
    {
        return OS_FS_ERR_PATH_INVALID;
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)VirtualPath, &token);

    if (return_code != OS_SUCCESS)
    {
        return_code = OS_FS_ERR_PATH_INVALID;
    }
    else
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM) != 0)
        {
            SysMountPointLen = OS_strnlen(filesys->system_mountpt, sizeof(filesys->system_mountpt));
            VirtPathBegin    = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));
            if (SysMountPointLen < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(LocalPath, filesys->system_mountpt, SysMountPointLen);
            }
        }
        else
        {
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }

        OS_ObjectIdRelease(&token);
    }

    if (return_code == OS_SUCCESS)
    {
        if (VirtPathLen < VirtPathBegin)
        {
            return_code = OS_FS_ERR_PATH_INVALID;
        }
        else
        {
            VirtPathLen -= VirtPathBegin;
            if ((SysMountPointLen + VirtPathLen) < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(&LocalPath[SysMountPointLen], &VirtualPath[VirtPathBegin], VirtPathLen);
                LocalPath[SysMountPointLen + VirtPathLen] = 0;
            }
            else
            {
                return_code = OS_FS_ERR_PATH_TOO_LONG;
            }
        }
    }

    return return_code;
}
