#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <string.h>

/* Probe helper: append a formatted log line to /tmp/logs/1427.txt */
static void OS_TranslatePath_Log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1427.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);

    fclose(f);
}

int32 OS_TranslatePath(const char *VirtualPath, char *LocalPath)
{
    OS_object_token_t             token;
    int32                         return_code;
    const char *                  name_ptr;
    OS_filesys_internal_record_t *filesys;
    size_t                        SysMountPointLen;
    size_t                        VirtPathLen;
    size_t                        VirtPathBegin;

    /* Entry probe: pointers and initial snippet of the virtual path (safe check) */
    OS_TranslatePath_Log("ENTRY: VirtualPath=%p LocalPath=%p", (const void *)VirtualPath, (void *)LocalPath);
    if (VirtualPath != NULL)
    {
        size_t _snip = strnlen(VirtualPath, 256);
        char _buf[257] = {0};
        if (_snip > 0)
        {
            memcpy(_buf, VirtualPath, _snip);
            _buf[_snip] = '\0';
        }
        OS_TranslatePath_Log("ENTRY: VirtualPath (snippet, len=%zu) = '%s'", _snip, _buf);
    }

    /*
    ** Check to see if the path pointers are NULL
    */
    /* Check parameters */
    OS_CHECK_POINTER(VirtualPath);
    OS_CHECK_POINTER(LocalPath);

    /*
    ** Check length
    */
    VirtPathLen = OS_strnlen(VirtualPath, OS_MAX_PATH_LEN);
    OS_TranslatePath_Log("After len check: VirtPathLen=%zu", VirtPathLen);
    if (VirtPathLen >= OS_MAX_PATH_LEN)
    {
        OS_TranslatePath_Log("RETURN: OS_FS_ERR_PATH_TOO_LONG (VirtPathLen >= OS_MAX_PATH_LEN)");
        return OS_FS_ERR_PATH_TOO_LONG;
    }

    /* checks to see if there is a '/' somewhere in the path */
    name_ptr = strrchr(VirtualPath, '/');
    OS_TranslatePath_Log("strrchr on VirtualPath returned name_ptr=%p", (const void *)name_ptr);
    if (name_ptr == NULL)
    {
        OS_TranslatePath_Log("RETURN: OS_FS_ERR_PATH_INVALID (no '/')");
        return OS_FS_ERR_PATH_INVALID;
    }

    /* strrchr returns a pointer to the last '/' char, so we advance one char */
    name_ptr = name_ptr + 1;
    if (memchr(name_ptr, 0, OS_MAX_FILE_NAME) == NULL)
    {
        OS_TranslatePath_Log("RETURN: OS_FS_ERR_NAME_TOO_LONG (no NUL within OS_MAX_FILE_NAME)");
        return OS_FS_ERR_NAME_TOO_LONG;
    }

    SysMountPointLen = 0;
    VirtPathBegin    = VirtPathLen;

    /*
    ** All valid Virtual paths must start with a '/' character
    */
    if (VirtualPath[0] != '/')
    {
        OS_TranslatePath_Log("RETURN: OS_FS_ERR_PATH_INVALID (VirtualPath[0] != '/') char=%c", VirtualPath[0]);
        return OS_FS_ERR_PATH_INVALID;
    }

    /* Get a reference lock, as a filesystem check could take some time. */
    OS_TranslatePath_Log("Calling OS_ObjectIdGetBySearch with search_func=OS_FileSys_FindVirtMountPoint and ref=(void*)VirtualPath ptr=%p", (const void *)VirtualPath);

    /* NOTE: This is the site of the reported const-discarding cast: (void *)VirtualPath */
    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)VirtualPath, &token);

    OS_TranslatePath_Log("OS_ObjectIdGetBySearch returned code=%d", return_code);

    if (return_code != OS_SUCCESS)
    {
        OS_TranslatePath_Log("OS_ObjectIdGetBySearch failed -> returning OS_FS_ERR_PATH_INVALID");
        return_code = OS_FS_ERR_PATH_INVALID;
    }
    else
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        OS_TranslatePath_Log("Got filesys pointer=%p filesys->flags=0x%08X", (void *)filesys, (unsigned int)filesys->flags);

        if ((filesys->flags & OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM) != 0)
        {
            SysMountPointLen = OS_strnlen(filesys->system_mountpt, sizeof(filesys->system_mountpt));
            VirtPathBegin    = OS_strnlen(filesys->virtual_mountpt, sizeof(filesys->virtual_mountpt));

            OS_TranslatePath_Log("Mounted system: system_mountpt='%.*s' (len=%zu) virtual_mountpt='%.*s' (len=%zu)",
                                 (int)SysMountPointLen, filesys->system_mountpt, SysMountPointLen,
                                 (int)VirtPathBegin, filesys->virtual_mountpt, VirtPathBegin);

            if (SysMountPointLen < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(LocalPath, filesys->system_mountpt, SysMountPointLen);
                OS_TranslatePath_Log("Copied system_mountpt into LocalPath (len=%zu)", SysMountPointLen);
            }
        }
        else
        {
            OS_TranslatePath_Log("filesys not mounted system -> returning OS_ERR_INCORRECT_OBJ_STATE");
            return_code = OS_ERR_INCORRECT_OBJ_STATE;
        }

        OS_ObjectIdRelease(&token);
    }

    if (return_code == OS_SUCCESS)
    {
        if (VirtPathLen < VirtPathBegin)
        {
            OS_TranslatePath_Log("RETURN: OS_FS_ERR_PATH_INVALID (VirtPathLen < VirtPathBegin): VirtPathLen=%zu VirtPathBegin=%zu", VirtPathLen, VirtPathBegin);
            return_code = OS_FS_ERR_PATH_INVALID;
        }
        else
        {
            VirtPathLen -= VirtPathBegin;
            OS_TranslatePath_Log("After subtract: SysMountPointLen=%zu VirtPathLen=%zu (remaining)" , SysMountPointLen, VirtPathLen);
            if ((SysMountPointLen + VirtPathLen) < OS_MAX_LOCAL_PATH_LEN)
            {
                memcpy(&LocalPath[SysMountPointLen], &VirtualPath[VirtPathBegin], VirtPathLen);
                LocalPath[SysMountPointLen + VirtPathLen] = 0;
                OS_TranslatePath_Log("Final LocalPath written at %p: '%s'", (void *)LocalPath,
                                     (LocalPath != NULL) ? LocalPath : "(null)");
            }
            else
            {
                OS_TranslatePath_Log("RETURN: OS_FS_ERR_PATH_TOO_LONG (SysMountPointLen + VirtPathLen >= OS_MAX_LOCAL_PATH_LEN) computed=%zu", SysMountPointLen + VirtPathLen);
                return_code = OS_FS_ERR_PATH_TOO_LONG;
            }
        }
    }

    OS_TranslatePath_Log("EXIT: returning %d", return_code);
    return return_code;
}
