#include <stdio.h>
#include <time.h>
#include <stdint.h>

/* Probe helper: logs timespec state and control points to /tmp/logs/594.txt */
static void OS_LogTimespecState(const char *label, uint32 usecs, const struct timespec *time_spec)
{
    FILE *f = fopen("/tmp/logs/594.txt", "a");
    if (f != NULL)
    {
        /* Print pointer address and numeric fields to help reproduce the scenario */
        fprintf(f, "%s: usecs=%lu, time_spec=%p, tv_sec=%ld, tv_nsec=%ld\n",
                label,
                (unsigned long)usecs,
                (void *)time_spec,
                (long)time_spec->tv_sec,
                (long)time_spec->tv_nsec);
        fclose(f);
    }
}

static void OS_UsecToTimespec(uint32 usecs, struct timespec *time_spec)
{
    /* Log entry state (caller may have zeroed the struct prior to call) */
    OS_LogTimespecState("entry", usecs, time_spec);

    if (usecs < 1000000)
    {
        OS_LogTimespecState("branch_if_before_assign", usecs, time_spec);

        time_spec->tv_nsec = (usecs * 1000);
        time_spec->tv_sec  = 0;

        OS_LogTimespecState("branch_if_after_assign", usecs, time_spec);
    }
    else
    {
        OS_LogTimespecState("branch_else_before_assign", usecs, time_spec);

        time_spec->tv_sec  = usecs / 1000000;
        time_spec->tv_nsec = (usecs % 1000000) * 1000;

        OS_LogTimespecState("branch_else_after_assign", usecs, time_spec);
    }

    /* Log exit state */
    OS_LogTimespecState("exit", usecs, time_spec);
}
