#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe logging helper - appends messages to /tmp/logs/1362.txt */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1362.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

int32 OS_rename(const char *old, const char *new)
{
    OS_object_iter_t             iter;
    OS_stream_internal_record_t *stream;
    int32                        return_code;
    char                         old_path[OS_MAX_LOCAL_PATH_LEN];
    char                         new_path[OS_MAX_LOCAL_PATH_LEN];

    probe_log("OS_rename entry: old=" "%p" ", new=" "%p", called", (const void *)old, (const void *)new);
    if (old != NULL)
    {
        probe_log("OS_rename: old literal='%s'", old);
    }
    if (new != NULL)
    {
        probe_log("OS_rename: new literal='%s'", new);
    }

    return_code = OS_TranslatePath(old, old_path);
    probe_log("OS_TranslatePath(old) returned %d", (int)return_code);
    if (return_code == OS_SUCCESS)
    {
        probe_log("OS_TranslatePath(old) -> '%s'", old_path);
        return_code = OS_TranslatePath(new, new_path);
        probe_log("OS_TranslatePath(new) returned %d", (int)return_code);
        if (return_code == OS_SUCCESS)
        {
            probe_log("OS_TranslatePath(new) -> '%s'", new_path);
        }
    }

    if (return_code == OS_SUCCESS)
    {
        return_code = OS_FileRename_Impl(old_path, new_path);
        probe_log("OS_FileRename_Impl('%s','%s') returned %d", old_path, new_path, (int)return_code);
    }

    if (return_code == OS_SUCCESS)
    {
        probe_log("Starting object table iteration for active local objects");
        OS_ObjectIdIterateActive(LOCAL_OBJID_TYPE, &iter);

        while (OS_ObjectIdIteratorGetNext(&iter))
        {
            /* Log the iterator token value to help reproduce control flow */
            probe_log("Iterator token: %llu", (unsigned long long)iter.token);

            stream = OS_OBJECT_TABLE_GET(OS_stream_table, iter.token);
            if (stream == NULL)
            {
                probe_log("Stream record for token %llu is NULL", (unsigned long long)iter.token);
                continue;
            }

            /* Log stream fields that affect the branching */
            probe_log("Stream token=%llu: socket_domain=%d, stream_name='%s'",
                      (unsigned long long)iter.token,
                      (int)stream->socket_domain,
                      stream->stream_name);

            if (stream->socket_domain == OS_SocketDomain_INVALID && strcmp(stream->stream_name, old) == 0)
            {
                probe_log("Match found (socket_domain invalid and name equals old). Performing rename of stream token=%llu", (unsigned long long)iter.token);

                /* Preserve original value for logging before update */
                char before_name[sizeof(stream->stream_name)];
                strncpy(before_name, stream->stream_name, sizeof(before_name) - 1);
                before_name[sizeof(before_name) - 1] = '\0';
                probe_log("Before rename: '%s'", before_name);

                /* Actual rename as in original code */
                strncpy(stream->stream_name, new, sizeof(stream->stream_name) - 1);
                stream->stream_name[sizeof(stream->stream_name) - 1] = 0;

                probe_log("After rename: '%s'", stream->stream_name);
            }
            else
            {
                probe_log("No match for token=%llu (either socket_domain not invalid or name != old)", (unsigned long long)iter.token);
            }
        }

        OS_ObjectIdIteratorDestroy(&iter);
        probe_log("Finished object table iteration");
    }

    probe_log("OS_rename exit: return_code=%d", (int)return_code);
    return return_code;
}
