#include <stdio.h>
#include <sys/stat.h>
#include <sys/types.h>

/* Probe logging for OS_unmount. Writes human-readable traces to /tmp/logs/1474.txt
 * This helper preserves the original logic of OS_unmount and only emits observational
 * logs. It attempts to create the /tmp/logs directory but ignores errors doing so.
 */
static void OS_unmount_log(const char *stage, const char *mountpoint, int32 return_code,
                           const OS_filesys_internal_record_t *filesys, const OS_object_token_t *token)
{
    FILE *f = NULL;

    /* Best-effort create the logs directory; ignore errors so behavior is unchanged */
    (void)mkdir("/tmp/logs", 0755);

    f = fopen("/tmp/logs/1474.txt", "a");
    if (f == NULL)
    {
        return;
    }

    fprintf(f, "[OS_unmount] stage=%s mountpoint=\"%s\" return_code=%d\n",
            stage != NULL ? stage : "(null)", mountpoint != NULL ? mountpoint : "(null)", (int)return_code);

    if (filesys != NULL)
    {
        /* Log the flags value which is central to the decision logic in the function */
        fprintf(f, "[OS_unmount] filesys->flags=0x%08lX\n", (unsigned long)filesys->flags);
    }

    /* Log presence of token pointer; content/layout of token is implementation-defined so avoid deref */
    fprintf(f, "[OS_unmount] token_ptr=%p\n", (const void *)token);

    fclose(f);
}

int32 OS_unmount(const char *mountpoint)
{
    int32                         return_code;
    OS_object_token_t             token;
    OS_filesys_internal_record_t *filesys;

    /* Entry log */
    OS_unmount_log("entry", mountpoint, 0, NULL, NULL);

    /* Check parameters */
    OS_CHECK_STRING(mountpoint, sizeof(filesys->virtual_mountpt), OS_FS_ERR_PATH_TOO_LONG);

    return_code = OS_ObjectIdGetBySearch(OS_LOCK_MODE_GLOBAL, LOCAL_OBJID_TYPE, OS_FileSys_FindVirtMountPoint,
                                         (void *)mountpoint, &token);

    /* Log result of lookup */
    OS_unmount_log("after_ObjectIdGetBySearch", mountpoint, return_code, NULL, &token);

    if (return_code == OS_SUCCESS)
    {
        filesys = OS_OBJECT_TABLE_GET(OS_filesys_table, token);

        /* Log filesys state prior to flags check */
        OS_unmount_log("before_flags_check", mountpoint, return_code, filesys, &token);

        /*
         * FIXED flag should always be unset (these don't support mount/unmount at all)
         * READY flag should be set (mkfs/initfs must have been called on this FS)
         * MOUNTED SYSTEM/VIRTUAL should always be unset.
         *
         * The FIXED flag is not enforced to support abstraction.
         */
        if ((filesys->flags & ~OS_FILESYS_FLAG_IS_FIXED) !=
            (OS_FILESYS_FLAG_IS_READY | OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL))
        {
            /* unmount() cannot be used on this file system at this time */
            return_code = OS_ERR_INCORRECT_OBJ_STATE;

            /* Log incorrect state outcome */
            OS_unmount_log("incorrect_state", mountpoint, return_code, filesys, &token);
        }
        else
        {
            /* Call the implementation and log before/after in order to capture whether it is a no-op */
            OS_unmount_log("before_impl_call", mountpoint, return_code, filesys, &token);

            return_code = OS_FileSysUnmountVolume_Impl(&token);

            OS_unmount_log("after_impl_call", mountpoint, return_code, filesys, &token);
        }

        if (return_code == OS_SUCCESS)
        {
            /* mark as mounted in the local table.
             * For now this does both sides (system and virtual) */
            filesys->flags &= ~(OS_FILESYS_FLAG_IS_MOUNTED_SYSTEM | OS_FILESYS_FLAG_IS_MOUNTED_VIRTUAL);

            /* Log flags after clearing mount bits */
            OS_unmount_log("after_flag_clear", mountpoint, return_code, filesys, &token);
        }

        OS_ObjectIdRelease(&token);

        /* Log after release */
        OS_unmount_log("after_release", mountpoint, return_code, filesys, NULL);
    }

    if (return_code != OS_SUCCESS)
    {
        return_code = OS_ERR_NAME_NOT_FOUND;

        /* Log final error mapping */
        OS_unmount_log("final_error_map", mountpoint, return_code, NULL, NULL);
    }

    /* Exit log */
    OS_unmount_log("exit", mountpoint, return_code, NULL, NULL);

    return return_code;
}
