#include <stdio.h>
#include <stddef.h>

/* Probe helpers: write concise logs about control flow and state to /tmp/logs/2711.txt.
 * These helpers avoid reading the contents of OutputMessage to not introduce new
 * out-of-bounds reads beyond what the original code already does. They only log
 * pointer values and sizes reported by the original logic. */
static void UT_BSP_LogProbe_Pre(uint8 MessageType, const char *OutputMessage)
{
    FILE *fp = fopen("/tmp/logs/2711.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    fprintf(fp, "[UT_BSP_DoText PRE] MessageType=%u OutputMessage=%p\n",
            (unsigned)MessageType, (const void *)OutputMessage);
    fclose(fp);
}

static void UT_BSP_LogProbe_Post(const char *OutputMessage, size_t MsgLen)
{
    FILE *fp = fopen("/tmp/logs/2711.txt", "a");
    if (fp == NULL)
    {
        return;
    }

    if (OutputMessage == NULL)
    {
        fprintf(fp, "[UT_BSP_DoText POST] OutputMessage=NULL MsgLen=%zu\n", MsgLen);
    }
    else
    {
        /* Do NOT read from OutputMessage other than what the original logic does.
         * Log pointer and length; if length > 0, log the last character value
         * as seen by the original logic (this accesses OutputMessage[MsgLen-1]
         * which mirrors the original code's behavior). */
        if (MsgLen > 0)
        {
            unsigned char last = (unsigned char)OutputMessage[MsgLen - 1];
            fprintf(fp, "[UT_BSP_DoText POST] OutputMessage=%p MsgLen=%zu LastChar=0x%02x\n",
                    (const void *)OutputMessage, MsgLen, (unsigned)last);
        }
        else
        {
            fprintf(fp, "[UT_BSP_DoText POST] OutputMessage=%p MsgLen=0\n",
                    (const void *)OutputMessage);
        }
    }

    fclose(fp);
}

void UT_BSP_DoText(uint8 MessageType, const char *OutputMessage)
{
    char   Buffer[16];
    size_t MsgLen;
    uint32 TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
    uint32 MsgEnabled   = BSP_UT_Global.CurrVerbosity >> MessageType;

    if (MsgEnabled & 1)
    {
        /* Log pre-call state so that crashes during strlen/str* can be diagnosed. */
        UT_BSP_LogProbe_Pre(MessageType, OutputMessage);

        UT_BSP_Lock();

        /* Determine if the message type warrants special treatment (color/highlight/etc). */
        switch (MessageType)
        {
            case UTASSERT_CASETYPE_ABORT:
            case UTASSERT_CASETYPE_FAILURE:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED;
                break;
            case UTASSERT_CASETYPE_MIR:
            case UTASSERT_CASETYPE_WARN:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_GREEN;
                break;
            case UTASSERT_CASETYPE_TSF:
            case UTASSERT_CASETYPE_TTF:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_RED | OS_BSP_CONSOLEMODE_BLUE;
                break;
            case UTASSERT_CASETYPE_BEGIN:
                OS_BSP_ConsoleOutput_Impl("\n", 1); /* add a bit of extra whitespace between tests */
                break;
            case UTASSERT_CASETYPE_PASS:
                TermModeBits = OS_BSP_CONSOLEMODE_HIGHLIGHT | OS_BSP_CONSOLEMODE_GREEN;
                break;
            default:
                break;
        }

        if (MsgEnabled <= 1)
        {
            TermModeBits = OS_BSP_CONSOLEMODE_NORMAL;
        }

        snprintf(Buffer, sizeof(Buffer), "[%5s]", UtAssert_GetCaseTypeAbbrev(MessageType));

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(TermModeBits);
        }

        OS_BSP_ConsoleOutput_Impl(Buffer, strlen(Buffer));

        if (TermModeBits != OS_BSP_CONSOLEMODE_NORMAL)
        {
            OS_BSP_ConsoleSetMode_Impl(OS_BSP_CONSOLEMODE_NORMAL);
        }

        OS_BSP_ConsoleOutput_Impl(" ", 1);

        /* Compute message length (this may read through OutputMessage until a '\0').
         * We preserve the original behavior but log the result immediately after. */
        MsgLen = strlen(OutputMessage);

        /* Log post-call state including the reported length and last character as
         * observed by the original logic. This provides evidence if strlen read beyond
         * the intended object bounds (e.g., enormous length or unexpected last char). */
        UT_BSP_LogProbe_Post(OutputMessage, MsgLen);

        OS_BSP_ConsoleOutput_Impl(OutputMessage, MsgLen);
        if (MsgLen == 0 || OutputMessage[MsgLen - 1] != '\n')
        {
            OS_BSP_ConsoleOutput_Impl("\n", 1);
        }

        UT_BSP_Unlock();
    }

    /*
     * If any ABORT (major failure) message is thrown,
     * then call a BSP-provided routine to stop the test and possibly dump a core
     */
    if (MessageType == UTASSERT_CASETYPE_ABORT)
    {
        OS_BSP_Shutdown_Impl();
    }
}
