#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdbool.h>
#include <stdint.h>

/* Probe logging helper - append-only to avoid altering program state */
static void UT_ProbeLog(const char *fmt, ...)
{
    va_list ap;
    FILE *f = fopen("/tmp/logs/2411.txt", "a");
    if (!f)
    {
        return;
    }
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

int32 UT_DefaultStubImplWithArgs(const char *FunctionName, UT_EntryKey_t FuncKey, int32 DefaultRc, va_list ArgList)
{
    const char *         RetcodeString;
    UT_StubTableEntry_t *StubPtr;
    UT_StubTableEntry_t *ContextTblPtr;
    UT_StubTableEntry_t *RvcPtr;
    UT_StubContext_t     LocalContext;
    uint32               Counter;
    va_list              ArgListCopy;
    bool                 GotInt32StatusFromHook;

    /* Log entry into function with key state */
    UT_ProbeLog("ENTRY: UT_DefaultStubImplWithArgs FunctionName=%p (%s), FuncKey=%p, DefaultRc=%ld",
                (void *)FunctionName, (FunctionName ? FunctionName : "(null)"), (void *)((uintptr_t)FuncKey), (long)DefaultRc);

    ContextTblPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_CALLBACK_CONTEXT);
    if (ContextTblPtr != NULL)
    {
        LocalContext = ContextTblPtr->Data.Context;

        /* Always clear the context entry -- the next call will have a different one */
        UT_ClearStubEntry(ContextTblPtr);

        UT_ProbeLog("Context: found existing context at %p; Int32StatusIsSet=%d, Int32StatusCode=%ld",
                    (void *)ContextTblPtr, (int)LocalContext.Int32StatusIsSet, (long)LocalContext.Int32StatusCode);
    }
    else
    {
        memset(&LocalContext, 0, sizeof(LocalContext));
        UT_ProbeLog("Context: no existing context, zero-initialized LocalContext");
    }

    RvcPtr = UT_Stub_FindRetvalConfig(FuncKey);
    UT_ProbeLog("RvcPtr lookup result: %p", (void *)RvcPtr);

    /* For legacy compatibility, determine the int32 status code (this may or may not be relevent) */
    if (RvcPtr != NULL && UT_Stub_IsValueCompatible(&RvcPtr->Data.Rvc, "int32", UT_ValueGenre_INTEGER))
    {
        /* Log details about the buffer and the intended call to UT_LoadRetval */
        UT_ProbeLog("About to load int32 retval: size=%zu, RvcPtr=%p, &RvcPtr->Data.Rvc.Buf=%p, Genre=%d",
                    sizeof(LocalContext.Int32StatusCode), (void *)RvcPtr, (void *)&RvcPtr->Data.Rvc.Buf,
                    (int)RvcPtr->Data.Rvc.Genre);

        /* Call UT_LoadRetval and capture its result to allow logging */
        bool load_retval_result = UT_LoadRetval(&LocalContext.Int32StatusCode, sizeof(LocalContext.Int32StatusCode), &RvcPtr->Data.Rvc.Buf,
                                                UT_ValueGenre_INTEGER);

        LocalContext.Int32StatusIsSet = load_retval_result;

        UT_ProbeLog("UT_LoadRetval(int32) returned=%d, Int32StatusCode=%ld", (int)load_retval_result,
                    (long)LocalContext.Int32StatusCode);
    }

    if (!LocalContext.Int32StatusIsSet)
    {
        LocalContext.Int32StatusCode = DefaultRc;
    }

    if (FunctionName != NULL)
    {
        if (!LocalContext.Int32StatusIsSet)
        {
            RetcodeString = "DEFAULT";
        }
        else
        {
            /* Indicate that this invocation got a non-default return code */
            RetcodeString = "*SPECIAL*";
        }

        UtDebug("%s called (%s,%ld)", FunctionName, RetcodeString, (long)LocalContext.Int32StatusCode);
        UT_ProbeLog("Debug: %s called (%s,%ld)", FunctionName, RetcodeString, (long)LocalContext.Int32StatusCode);
    }

    Counter = 0;
    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_COUNTER);
    if (StubPtr == NULL)
    {
        /* Creating counter entry - repeat search and grab any unused slot */
        StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_UNUSED);
    }

    if (StubPtr == NULL)
    {
        UtAssert_Abort("Cannot set counter - UT_MAX_FUNC_STUBS too low?");
    }
    else
    {
        StubPtr->EntryType = UT_ENTRYTYPE_COUNTER;
        StubPtr->FuncKey   = FuncKey;
        Counter            = StubPtr->Data.Rc.Count;
        ++StubPtr->Data.Rc.Count;
        StubPtr->Data.Rc.Value = LocalContext.Int32StatusCode;

        UT_ProbeLog("Counter updated: StubPtr=%p, CounterIndex=%u, NewCount=%u, StoredValue=%ld",
                    (void *)StubPtr, (unsigned)Counter, (unsigned)StubPtr->Data.Rc.Count,
                    (long)StubPtr->Data.Rc.Value);
    }

    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_CALLBACK_HOOK);
    if (StubPtr != NULL)
    {
        if (StubPtr->Data.Cb.IsVarg)
        {
            va_copy(ArgListCopy, ArgList);
            LocalContext.Int32StatusCode = StubPtr->Data.Cb.Ptr.VaHook(
                StubPtr->Data.Cb.CallbackArg, LocalContext.Int32StatusCode, Counter, &LocalContext, ArgListCopy);
            va_end(ArgListCopy);
        }
        else
        {
            LocalContext.Int32StatusCode = StubPtr->Data.Cb.Ptr.SimpleHook(
                StubPtr->Data.Cb.CallbackArg, LocalContext.Int32StatusCode, Counter, &LocalContext);
        }

        GotInt32StatusFromHook        = true;
        LocalContext.Int32StatusIsSet = true;

        UT_ProbeLog("Hook invoked: HookPtr=%p, GotInt32StatusFromHook=%d, Int32StatusCode=%ld",
                    (void *)StubPtr->Data.Cb.Ptr.SimpleHook, (int)GotInt32StatusFromHook,
                    (long)LocalContext.Int32StatusCode);
    }
    else
    {
        GotInt32StatusFromHook = false;
    }

    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_FINAL_HANDLER);
    if (StubPtr != NULL)
    {
        if (StubPtr->Data.Cb.IsVarg)
        {
            StubPtr->Data.Cb.Ptr.VaHandler(StubPtr->Data.Cb.CallbackArg, FuncKey, &LocalContext, ArgList);
            UT_ProbeLog("Final handler (varg) invoked: HandlerPtr=%p", (void *)StubPtr->Data.Cb.Ptr.VaHandler);
        }
        else
        {
            StubPtr->Data.Cb.Ptr.SimpleHandler(StubPtr->Data.Cb.CallbackArg, FuncKey, &LocalContext);
            UT_ProbeLog("Final handler (simple) invoked: HandlerPtr=%p", (void *)StubPtr->Data.Cb.Ptr.SimpleHandler);
        }
    }

    /*
     * Handle propagation of return code.
     */
    StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_RETURN_BUFFER);
    if (StubPtr != NULL && StubPtr->Data.Buff.Position == 0)
    {
        UT_ProbeLog("Return buffer handler: StubPtr=%p, Position=%u, TotalSize=%u, BasePtr=%p",
                    (void *)StubPtr, (unsigned)StubPtr->Data.Buff.Position, (unsigned)StubPtr->Data.Buff.TotalSize,
                    (void *)StubPtr->Data.Buff.BasePtr);

        /* If a hook function was used, it is expected that this will override any configured retval */
        if (GotInt32StatusFromHook && StubPtr->Data.Buff.TotalSize == sizeof(LocalContext.Int32StatusCode))
        {
            memcpy(StubPtr->Data.Buff.BasePtr, &LocalContext.Int32StatusCode, StubPtr->Data.Buff.TotalSize);
            StubPtr->Data.Buff.Position = StubPtr->Data.Buff.TotalSize;
            UT_ProbeLog("Copied int32 from LocalContext into return buffer due to hook: Value=%ld",
                        (long)LocalContext.Int32StatusCode);
        }
        else if (RvcPtr != NULL)
        {
            UT_ProbeLog("RvcPtr non-null when filling return buffer: RvcPtr=%p, RvcBufAddr=%p, RvcGenre=%d",
                        (void *)RvcPtr, (void *)&RvcPtr->Data.Rvc.Buf, (int)RvcPtr->Data.Rvc.Genre);

            if (RvcPtr->Data.Rvc.Genre == UT_ValueGenre_OPAQUE)
            {
                if (StubPtr->Data.Rvc.ActualSz != StubPtr->Data.Buff.TotalSize)
                {
                    UtAssert_Failed("Stub %s opaque return size mismatch, expected %lu, got %lu, type=%s", FunctionName,
                                    (unsigned long)StubPtr->Data.Buff.TotalSize,
                                    (unsigned long)StubPtr->Data.Rvc.ActualSz, StubPtr->Data.Buff.ContentTypeStr);
                }
                else
                {
                    memcpy(StubPtr->Data.Buff.BasePtr, StubPtr->Data.Rvc.Buf.IndirectPtr, StubPtr->Data.Buff.TotalSize);
                    StubPtr->Data.Buff.Position = StubPtr->Data.Buff.TotalSize;
                    UT_ProbeLog("Opaque copy into return buffer from IndirectPtr=%p, TotalSize=%u",
                                (void *)StubPtr->Data.Rvc.Buf.IndirectPtr, (unsigned)StubPtr->Data.Buff.TotalSize);
                }
            }
            else
            {
                /* Log the parameters before calling UT_LoadRetval for the buffer case */
                UT_ProbeLog("About to call UT_LoadRetval for return buffer: Dest=%p, Size=%u, SourceBuf=%p, Genre=%d",
                            (void *)StubPtr->Data.Buff.BasePtr, (unsigned)StubPtr->Data.Buff.TotalSize,
                            (void *)&RvcPtr->Data.Rvc.Buf, (int)RvcPtr->Data.Rvc.Genre);

                bool load_retval_buf = UT_LoadRetval(StubPtr->Data.Buff.BasePtr, StubPtr->Data.Buff.TotalSize, &RvcPtr->Data.Rvc.Buf,
                                                      RvcPtr->Data.Rvc.Genre);

                UT_ProbeLog("UT_LoadRetval(return buffer) returned=%d", (int)load_retval_buf);

                if (load_retval_buf)
                {
                    StubPtr->Data.Buff.Position = StubPtr->Data.Buff.TotalSize;
                }
            }
        }

        if (StubPtr->Data.Buff.Position == 0)
        {
            /* Zero-fill fallback */
            memset(StubPtr->Data.Buff.BasePtr, 0, StubPtr->Data.Buff.TotalSize);
            StubPtr->Data.Buff.Position = StubPtr->Data.Buff.TotalSize;
            UT_ProbeLog("Return buffer was empty; zero-filled %u bytes at %p", (unsigned)StubPtr->Data.Buff.TotalSize,
                        (void *)StubPtr->Data.Buff.BasePtr);
        }
    }

    /*
     * Deferred retval configs should be expunged after they are used
     */
    if (RvcPtr != NULL && RvcPtr->EntryType == UT_ENTRYTYPE_RETVAL_CONFIG_DEFERRED)
    {
        UT_ProbeLog("Clearing deferred RvcPtr at %p (EntryType=%d)", (void *)RvcPtr, (int)RvcPtr->EntryType);
        UT_ClearStubEntry(RvcPtr);
    }

    UT_ProbeLog("EXIT: returning Int32StatusCode=%ld", (long)LocalContext.Int32StatusCode);

    return LocalContext.Int32StatusCode;
}
