static void UT_DoSetHookFunction(UT_EntryKey_t FuncKey, UT_EntryType_t EntryType, UT_HookFuncPtr_t Value, void *UserObj,
                                 bool IsVarg)
{
    UT_StubTableEntry_t *StubPtr;
    FILE *log = fopen("/tmp/logs/2423.txt", "a");

    if (log != NULL)
    {
        /* Log entry and parameter state. Avoid dereferencing unknown pointers; only log whether Value.Addr is NULL. */
        fprintf(log, "UT_DoSetHookFunction ENTER: FuncKey=%u EntryType=%u Value.Addr_is_null=%d UserObj=%p IsVarg=%d\n",
                (unsigned)FuncKey,
                (unsigned)EntryType,
                (Value.Addr == NULL) ? 1 : 0,
                UserObj,
                IsVarg ? 1 : 0);
        fflush(log);
    }

    /*
     * First find an existing hook entry for the function.
     * In case one is already set we do not duplicate
     */
    StubPtr = UT_GetStubEntry(FuncKey, EntryType);

    if (log != NULL)
    {
        fprintf(log, "After first UT_GetStubEntry: StubPtr=%p\n", (void *)StubPtr);
        fflush(log);
    }

    if (StubPtr == NULL && Value.Addr != NULL)
    {
        /* Creating force fail entry - repeat search and grab any unused slot */
        if (log != NULL)
        {
            fprintf(log, "No existing stub found and a non-NULL hook provided - attempting to grab UNUSED slot\n");
            fflush(log);
        }

        StubPtr = UT_GetStubEntry(FuncKey, UT_ENTRYTYPE_UNUSED);

        if (log != NULL)
        {
            fprintf(log, "After second UT_GetStubEntry(UNUSED): StubPtr=%p\n", (void *)StubPtr);
            fflush(log);
        }

        if (StubPtr == NULL)
        {
            if (log != NULL)
            {
                fprintf(log, "Cannot set hook function - no unused slots available, about to call UtAssert_Abort\n");
                fflush(log);
                fclose(log);
                log = NULL;
            }

            /* Preserve original behavior */
            UtAssert_Abort("Cannot set hook function - UT_MAX_FUNC_STUBS too low?");
        }
    }

    if (Value.Addr == NULL && StubPtr != NULL)
    {
        /* Caller wants to delete the entry */
        if (log != NULL)
        {
            fprintf(log, "Deleting stub entry at %p (clear)\n", (void *)StubPtr);
            fflush(log);
        }

        UT_ClearStubEntry(StubPtr);
    }
    else if (StubPtr != NULL && Value.Addr != NULL)
    {
        /* Caller wants to set the entry */
        if (log != NULL)
        {
            fprintf(log, "Setting stub entry at %p: FuncKey=%u EntryType=%u UserObj=%p IsVarg=%d\n",
                    (void *)StubPtr,
                    (unsigned)FuncKey,
                    (unsigned)EntryType,
                    UserObj,
                    IsVarg ? 1 : 0);
            fflush(log);
        }

        StubPtr->FuncKey             = FuncKey;
        StubPtr->EntryType           = EntryType;
        StubPtr->Data.Cb.CallbackArg = UserObj;
        StubPtr->Data.Cb.Ptr         = Value;
        StubPtr->Data.Cb.IsVarg      = IsVarg;

        if (log != NULL)
        {
            fprintf(log, "Stub entry set at %p\n", (void *)StubPtr);
            fflush(log);
        }
    }

    if (log != NULL)
    {
        fprintf(log, "UT_DoSetHookFunction EXIT\n");
        fclose(log);
    }
}
