bool UtAssert_GenericIntegerCompare(bool IsUnsigned, UT_IntCheck_t ActualValue, UtAssert_Compare_t CompareType,
                                    UT_IntCheck_t RefValue, const char *File, uint32 Line, UtAssert_Radix_t RadixType,
                                    const char *Typename, const char *ActualText, const char *RefText)
{
    static const char UTASSERT_PREFIX[] = "UTASSERT_";

    char ActualStr[32];
    char RefStr[32];
    char TagStr[32];
    int  TagLen;

    /* Logging probe: capture entry state */
    {
        FILE *fp = fopen("/tmp/logs/2277.txt", "a");
        if (fp != NULL)
        {
            fprintf(fp, "ENTER UtAssert_GenericIntegerCompare\n");
            fprintf(fp, "  IsUnsigned=%d, ActualValue=%lld, CompareType=%d, RefValue=%lld\n",
                    (int)IsUnsigned, (long long)ActualValue, (int)CompareType, (long long)RefValue);
            fprintf(fp, "  File=%s, Line=%lu, RadixType=%d\n",
                    (File != NULL) ? File : "(null)", (unsigned long)Line, (int)RadixType);
            fprintf(fp, "  Typename=%s\n", (Typename != NULL) ? Typename : "(null)");
            fprintf(fp, "  ActualText ptr=%p, ActualText=\"%s\"\n", (void *)ActualText,
                    (ActualText != NULL) ? ActualText : "(null)");
            fprintf(fp, "  RefText ptr=%p, RefText=\"%s\"\n", (void *)RefText,
                    (RefText != NULL) ? RefText : "(null)");
            fclose(fp);
        }
    }

    /* If the radix type was not specified, then check if the typename appears to be a pointer -
     * That is, it contains an asterisk.  This is far from foolproof due to typedefs etc but
     * it should catch most of them (note that "hiding" a pointer via typedef is discouraged by
     * GSFC coding standards, so this shouldn't be too likely)
     */
    if (Typename != NULL && *Typename != 0)
    {
        TagLen = snprintf(TagStr, sizeof(TagStr), "%s", Typename);
        if (TagLen < 0)
        {
            TagLen = 0;
        }
        else if (TagLen > (sizeof(TagStr) - 3))
        {
            TagLen = sizeof(TagStr) - 3;
        }

        while (TagLen > 0 && (isspace((unsigned char)TagStr[TagLen - 1]) || TagStr[TagLen - 1] == ':'))
        {
            --TagLen;
        }

        if (TagLen > 0)
        {
            TagStr[TagLen] = ':';
            ++TagLen;
            TagStr[TagLen] = ' ';
            ++TagLen;
        }

        TagStr[TagLen] = 0;

        if (RadixType == UtAssert_Radix_DEFAULT && strchr(Typename, '*') != NULL)
        {
            /* looks like a pointer type */
            RadixType = UtAssert_Radix_HEX;
        }
    }
    else
    {
        TagStr[0] = 0;
    }

    /* Log TagStr after processing */
    {
        FILE *fp = fopen("/tmp/logs/2277.txt", "a");
        if (fp != NULL)
        {
            fprintf(fp, "After Typename processing: TagStr=\"%s\", TagLen=%d, RadixType=%d\n",
                    TagStr, (int)strlen(TagStr), (int)RadixType);
            fclose(fp);
        }
    }

    /* If either the actual text or the ref text starts with the "UTASSERT_" prefix, then strip it */
    if (strncmp(ActualText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        /* Log before and after pointer adjustment for ActualText */
        {
            FILE *fp = fopen("/tmp/logs/2277.txt", "a");
            if (fp != NULL)
            {
                fprintf(fp, "ActualText has prefix \"%s\" (len=%zu)\n", UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1);
                fprintf(fp, "  before: ActualText ptr=%p, string=\"%s\"\n", (void *)ActualText,
                        (ActualText != NULL) ? ActualText : "(null)");
                fclose(fp);
            }
        }

        ActualText += sizeof(UTASSERT_PREFIX) - 1;

        {
            FILE *fp = fopen("/tmp/logs/2277.txt", "a");
            if (fp != NULL)
            {
                fprintf(fp, "  after:  ActualText ptr=%p, string=\"%s\"\n", (void *)ActualText,
                        (ActualText != NULL) ? ActualText : "(null)");
                fclose(fp);
            }
        }
    }
    if (strncmp(RefText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        /* Log before and after pointer adjustment for RefText */
        {
            FILE *fp = fopen("/tmp/logs/2277.txt", "a");
            if (fp != NULL)
            {
                fprintf(fp, "RefText has prefix \"%s\" (len=%zu)\n", UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1);
                fprintf(fp, "  before: RefText ptr=%p, string=\"%s\"\n", (void *)RefText,
                        (RefText != NULL) ? RefText : "(null)");
                fclose(fp);
            }
        }

        RefText += sizeof(UTASSERT_PREFIX) - 1;

        {
            FILE *fp = fopen("/tmp/logs/2277.txt", "a");
            if (fp != NULL)
            {
                fprintf(fp, "  after:  RefText ptr=%p, string=\"%s\"\n", (void *)RefText,
                        (RefText != NULL) ? RefText : "(null)");
                fclose(fp);
            }
        }
    }

    /* Evaluate compare once so we can log the boolean result without changing semantics */
    bool compareResult = UtAssert_DoCompare(ActualValue, CompareType, RefValue, IsUnsigned);

    /* Log comparison result just before calling UtAssertEx */
    {
        FILE *fp = fopen("/tmp/logs/2277.txt", "a");
        if (fp != NULL)
        {
            fprintf(fp, "Compare result=%d\n", (int)compareResult);
            fprintf(fp, "About to call UtAssertEx with ActualText=\"%s\", RefText=\"%s\"\n",
                    (ActualText != NULL) ? ActualText : "(null)", (RefText != NULL) ? RefText : "(null)");
            fclose(fp);
        }
    }

    return UtAssertEx(compareResult, UTASSERT_CASETYPE_FAILURE,
                      File, Line, "%s%s (%s) %s %s (%s)", TagStr, ActualText,
                      UtAssert_GetValueText(ActualStr, sizeof(ActualStr), ActualValue, IsUnsigned, RadixType),
                      UtAssert_GetOpText(CompareType), RefText,
                      UtAssert_GetValueText(RefStr, sizeof(RefStr), RefValue, IsUnsigned, RadixType));
}
