#include <stdio.h>
#include <string.h>
#include <stdint.h>
#include <stdbool.h>
#include <ctype.h>

/* Probe helper: append diagnostic information to /tmp/logs/2279.txt
 * This logs the label (ActualText/RefText), the pointer value, and details
 * about the prefix that will be checked. It deliberately does not dereference
 * the text pointer to avoid introducing new undefined behavior; it only logs
 * the pointer itself and the intent to call strncmp immediately afterwards
 * (which preserves the original control flow and potential behavior).
 */
static void UtAssert_LogPrefixProbe(const char *label, const char *text, const char *prefix, size_t prefix_len)
{
    FILE *f = fopen("/tmp/logs/2279.txt", "a");
    if (f == NULL)
    {
        return;
    }

    fprintf(f, "--- UtAssert_GenericIntegerCompare prefix probe ---\n");
    fprintf(f, "Label: %s\n", (label != NULL) ? label : "(null)");
    fprintf(f, "Text pointer: %p\n", (const void *)text);
    fprintf(f, "Prefix pointer: %p\n", (const void *)prefix);
    fprintf(f, "Prefix (len=%zu): '%s'\n", prefix_len, prefix);
    fprintf(f, "Note: about to call strncmp(%p, %p, %zu) - this call may read up to %zu bytes from the Text pointer.\n",
            (const void *)text, (const void *)prefix, prefix_len, prefix_len);
    fprintf(f, "--- end probe ---\n\n");

    fclose(f);
}

bool UtAssert_GenericIntegerCompare(bool IsUnsigned, UT_IntCheck_t ActualValue, UtAssert_Compare_t CompareType,
                                    UT_IntCheck_t RefValue, const char *File, uint32 Line, UtAssert_Radix_t RadixType,
                                    const char *Typename, const char *ActualText, const char *RefText)
{
    static const char UTASSERT_PREFIX[] = "UTASSERT_";

    char ActualStr[32];
    char RefStr[32];
    char TagStr[32];
    int  TagLen;

    /* If the radix type was not specified, then check if the typename appears to be a pointer -
     * That is, it contains an asterisk.  This is far from foolproof due to typedefs etc but
     * it should catch most of them (note that "hiding" a pointer via typedef is discouraged by
     * GSFC coding standards, so this shouldn't be too likely)
     */
    if (Typename != NULL && *Typename != 0)
    {
        TagLen = snprintf(TagStr, sizeof(TagStr), "%s", Typename);
        if (TagLen < 0)
        {
            TagLen = 0;
        }
        else if (TagLen > (sizeof(TagStr) - 3))
        {
            TagLen = sizeof(TagStr) - 3;
        }

        while (TagLen > 0 && (isspace((unsigned char)TagStr[TagLen - 1]) || TagStr[TagLen - 1] == ':'))
        {
            --TagLen;
        }

        if (TagLen > 0)
        {
            TagStr[TagLen] = ':';
            ++TagLen;
            TagStr[TagLen] = ' ';
            ++TagLen;
        }

        TagStr[TagLen] = 0;

        if (RadixType == UtAssert_Radix_DEFAULT && strchr(Typename, '*') != NULL)
        {
            /* looks like a pointer type */
            RadixType = UtAssert_Radix_HEX;
        }
    }
    else
    {
        TagStr[0] = 0;
    }

    /* If either the actual text or the ref text starts with the "UTASSERT_" prefix, then strip it */
    /* Insert probes to log the control path and state prior to calling strncmp, so that other agents
     * can reproduce and gather evidence for cases where strncmp may access beyond the bounds of the
     * referenced objects (e.g. when ActualText or RefText is NULL or shorter than the prefix length).
     * The original calls to strncmp are preserved to avoid changing the behavior of the function.
     */
    UtAssert_LogPrefixProbe("ActualText", ActualText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1);
    if (strncmp(ActualText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        ActualText += sizeof(UTASSERT_PREFIX) - 1;
    }

    UtAssert_LogPrefixProbe("RefText", RefText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1);
    if (strncmp(RefText, UTASSERT_PREFIX, sizeof(UTASSERT_PREFIX) - 1) == 0)
    {
        RefText += sizeof(UTASSERT_PREFIX) - 1;
    }

    return UtAssertEx(UtAssert_DoCompare(ActualValue, CompareType, RefValue, IsUnsigned), UTASSERT_CASETYPE_FAILURE,
                      File, Line, "%s%s (%s) %s %s (%s)", TagStr, ActualText,
                      UtAssert_GetValueText(ActualStr, sizeof(ActualStr), ActualValue, IsUnsigned, RadixType),
                      UtAssert_GetOpText(CompareType), RefText,
                      UtAssert_GetValueText(RefStr, sizeof(RefStr), RefValue, IsUnsigned, RadixType));
}
