#include <stdio.h>

/* Probe helper: log the CaseType and chosen abbreviation to a file for later inspection. */
static void UtAssert_LogCase(UtAssert_CaseType_t CaseType, const char *AbbrevStr)
{
    FILE *fp = fopen("/tmp/logs/2287.txt", "a");
    if (fp != NULL)
    {
        /* Cast CaseType to int for safe printing; guard AbbrevStr in case of unexpected NULL. */
        fprintf(fp, "UtAssert_GetCaseTypeAbbrev called: CaseType=%d, AbbrevStr=\"%s\"\n",
                (int)CaseType, (AbbrevStr != NULL) ? AbbrevStr : "(null)");
        fclose(fp);
    }
}

const char *UtAssert_GetCaseTypeAbbrev(UtAssert_CaseType_t CaseType)
{
    const char *AbbrevStr;

    switch (CaseType)
    {
        case UTASSERT_CASETYPE_ABORT:
            AbbrevStr = "ABORT";
            break;
        case UTASSERT_CASETYPE_FAILURE:
            AbbrevStr = "FAIL";
            break;
        case UTASSERT_CASETYPE_MIR:
            AbbrevStr = "MIR";
            break;
        case UTASSERT_CASETYPE_TSF:
            AbbrevStr = "TSF";
            break;
        case UTASSERT_CASETYPE_TTF:
            AbbrevStr = "TTF";
            break;
        case UTASSERT_CASETYPE_WARN:
            AbbrevStr = "WARN";
            break;
        case UTASSERT_CASETYPE_NA:
            AbbrevStr = "N/A";
            break;
        case UTASSERT_CASETYPE_BEGIN:
            AbbrevStr = "BEGIN";
            break;
        case UTASSERT_CASETYPE_END:
            AbbrevStr = "END";
            break;
        case UTASSERT_CASETYPE_PASS:
            AbbrevStr = "PASS";
            break;
        case UTASSERT_CASETYPE_INFO:
            AbbrevStr = "INFO";
            break;
        case UTASSERT_CASETYPE_FLOW:
            AbbrevStr = "FLOW";
            break;
        case UTASSERT_CASETYPE_DEBUG:
            AbbrevStr = "DEBUG";
            break;
        default:
            /* do not return NULL, as the result may be directly passed to C library functions */
            AbbrevStr = "OTHER";
            break;
    }

    /* Log the selected abbreviation and the CaseType for post-mortem analysis. */
    UtAssert_LogCase(CaseType, AbbrevStr);

    return AbbrevStr;
}
