#include <stdio.h>

/* Probe function to log control flow and state for UtAssert_GetCaseTypeAbbrev.
 * Appends a line to /tmp/logs/2287.txt with the numeric CaseType, the string
 * returned, and the pointer value of that string.
 *
 * This function is intended only to collect evidence about which branch
 * was taken and what string was produced; it preserves the original
 * behavior of UtAssert_GetCaseTypeAbbrev.
 */
static void UtAssert_LogCaseAbbrev(UtAssert_CaseType_t CaseType, const char *AbbrevStr)
{
    FILE *fp = fopen("/tmp/logs/2287.txt", "a");
    if (fp != NULL)
    {
        if (AbbrevStr != NULL)
        {
            /* Log CaseType as integer, the returned string, and its pointer */
            fprintf(fp, "UtAssert_GetCaseTypeAbbrev: CaseType=%d, AbbrevStr=\"%s\", AbbrevPtr=%p\n",
                    (int)CaseType, AbbrevStr, (void *)AbbrevStr);
        }
        else
        {
            fprintf(fp, "UtAssert_GetCaseTypeAbbrev: CaseType=%d, AbbrevStr=(null), AbbrevPtr=%p\n",
                    (int)CaseType, (void *)AbbrevStr);
        }
        fclose(fp);
    }
}

const char *UtAssert_GetCaseTypeAbbrev(UtAssert_CaseType_t CaseType)
{
    const char *AbbrevStr;

    switch (CaseType)
    {
        case UTASSERT_CASETYPE_ABORT:
            AbbrevStr = "ABORT";
            break;
        case UTASSERT_CASETYPE_FAILURE:
            AbbrevStr = "FAIL";
            break;
        case UTASSERT_CASETYPE_MIR:
            AbbrevStr = "MIR";
            break;
        case UTASSERT_CASETYPE_TSF:
            AbbrevStr = "TSF";
            break;
        case UTASSERT_CASETYPE_TTF:
            AbbrevStr = "TTF";
            break;
        case UTASSERT_CASETYPE_WARN:
            AbbrevStr = "WARN";
            break;
        case UTASSERT_CASETYPE_NA:
            AbbrevStr = "N/A";
            break;
        case UTASSERT_CASETYPE_BEGIN:
            AbbrevStr = "BEGIN";
            break;
        case UTASSERT_CASETYPE_END:
            AbbrevStr = "END";
            break;
        case UTASSERT_CASETYPE_PASS:
            AbbrevStr = "PASS";
            break;
        case UTASSERT_CASETYPE_INFO:
            AbbrevStr = "INFO";
            break;
        case UTASSERT_CASETYPE_FLOW:
            AbbrevStr = "FLOW";
            break;
        case UTASSERT_CASETYPE_DEBUG:
            AbbrevStr = "DEBUG";
            break;
        default:
            /* do not return NULL, as the result may be directly passed to C library functions */
            AbbrevStr = "OTHER";
            break;
    }

    /* Probe/logging: record which branch was taken and the state for later analysis. */
    UtAssert_LogCaseAbbrev(CaseType, AbbrevStr);

    return AbbrevStr;
}
