#include <stdio.h>

/* Probe function: log the call and the resulting operator text to a file for later analysis.
 * Writes to /tmp/logs/2293.txt. Intentionally minimal to avoid altering original logic.
 */
static void UtAssert_Log_GetOpText(int CompareType, const char *OpText)
{
    FILE *f = fopen("/tmp/logs/2293.txt", "a");
    if (f != NULL)
    {
        /* Log the control path (CompareType) and the resolved OpText.
         * Use integer form of CompareType to avoid depending on enum definition here.
         */
        fprintf(f, "UtAssert_GetOpText: CompareType=%d -> OpText=\"%s\"\n",
                CompareType,
                (OpText != NULL) ? OpText : "(null)");
        fclose(f);
    }
}

const char *UtAssert_GetOpText(UtAssert_Compare_t CompareType)
{
    const char *OpText;

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            OpText = "==";
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            OpText = "!=";
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            OpText = "<";
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            OpText = ">";
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            OpText = "<=";
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            OpText = ">=";
            break;
        case UtAssert_Compare_BITMASK_SET: /* bit(s) in reference are set in actual */
            OpText = "&";
            break;
        case UtAssert_Compare_BITMASK_UNSET: /* bit(s) in reference are not set in actual */
            OpText = "&~";
            break;
        default: /* should never happen */
            OpText = "??";
            break;
    }

    /* Probe: record the call and resolved operator text for later inspection */
    UtAssert_Log_GetOpText((int)CompareType, OpText);

    return OpText;
}
