#include <stdio.h>

/* Probe logging function: append control-flow and state info to /tmp/logs/2293.txt */
static void UtAssert_LogOpText(const char *phase, UtAssert_Compare_t CompareType, const char *OpText)
{
    FILE *fp = fopen("/tmp/logs/2293.txt", "a");
    if (fp != NULL)
    {
        /* Log the phase, numeric compare type and the selected operator text */
        fprintf(fp, "%s: CompareType=%d, OpText=\"%s\"\n", phase, (int)CompareType, (OpText != NULL) ? OpText : "(null)");
        fclose(fp);
    }
}

const char *UtAssert_GetOpText(UtAssert_Compare_t CompareType)
{
    const char *OpText;

    /* Log entry to the function with the input CompareType */
    UtAssert_LogOpText("Enter UtAssert_GetOpText", CompareType, "");

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            OpText = "==";
            UtAssert_LogOpText("Selected", CompareType, OpText);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            OpText = "!=";
            UtAssert_LogOpText("Selected", CompareType, OpText);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            OpText = "<";
            UtAssert_LogOpText("Selected", CompareType, OpText);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            OpText = ">";
            UtAssert_LogOpText("Selected", CompareType, OpText);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            OpText = "<=";
            UtAssert_LogOpText("Selected", CompareType, OpText);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            OpText = ">=";
            UtAssert_LogOpText("Selected", CompareType, OpText);
            break;
        case UtAssert_Compare_BITMASK_SET: /* bit(s) in reference are set in actual */
            OpText = "&";
            UtAssert_LogOpText("Selected", CompareType, OpText);
            break;
        case UtAssert_Compare_BITMASK_UNSET: /* bit(s) in reference are not set in actual */
            OpText = "&~";
            UtAssert_LogOpText("Selected", CompareType, OpText);
            break;
        default: /* should never happen */
            OpText = "??";
            UtAssert_LogOpText("Selected", CompareType, OpText);
            break;
    }

    return OpText;
}
