#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdbool.h>
#include <stdint.h>
#include <stddef.h>
#include <sys/stat.h>

/* Probe logger: append textual trace information to /tmp/logs/2318.txt */
static void UtAssert_Log(const char *format, ...)
{
    /* Ensure the directory exists (ignore errors) */
    (void)mkdir("/tmp/logs", 0777);

    FILE *f = fopen("/tmp/logs/2318.txt", "a");
    if (f == NULL)
    {
        return;
    }

    va_list ap;
    va_start(ap, format);
    vfprintf(f, format, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

bool UtAssert_StringBufCompare(const char *String1, size_t String1Max, const char *String2, size_t String2Max,
                               UtAssert_Compare_t CompareType, const char *File, uint32 Line)
{
    char        ScrubbedString1[256];
    char        ScrubbedString2[256];
    const char *EndPtr1;
    const char *EndPtr2;
    size_t      FormatLen1;
    size_t      FormatLen2;
    bool        Result;
    int         Compare;

    UtAssert_Log("ENTER UtAssert_StringBufCompare: String1=%p String1Max=%zu String2=%p String2Max=%zu CompareType=%d File=%s Line=%u",
                 (const void *)String1, String1Max, (const void *)String2, String2Max, (int)CompareType,
                 (File != NULL) ? File : "(null)", (unsigned)Line);

    /* Locate the actual end of both strings */
    if (String1 == NULL)
    {
        EndPtr1 = NULL;
        UtAssert_Log("String1 is NULL");
    }
    else if (String1Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        /*
         * NOTE: it is technically undefined behavior to pass a size to memchr()
         * that is larger than the actual buffer, even if it is known/guaranteed
         * to find a match within the actual buffer.  Therefore the regular strlen()
         * is used instead.
         */
        EndPtr1 = String1 + strlen(String1);
        UtAssert_Log("String1: used strlen() to determine end: EndPtr1=%p strlen=%zu", (const void *)EndPtr1,
                     (String1 != NULL) ? strlen(String1) : 0);
    }
    else
    {
        EndPtr1 = memchr(String1, 0, String1Max);
        UtAssert_Log("String1: used memchr() to determine end: EndPtr1=%p", (const void *)EndPtr1);
    }

    if (EndPtr1 != NULL)
    {
        FormatLen1 = EndPtr1 - String1;
    }
    else
    {
        FormatLen1 = String1Max;
    }

    if (String2 == NULL)
    {
        EndPtr2 = NULL;
        UtAssert_Log("String2 is NULL");
    }
    else if (String2Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        EndPtr2 = String2 + strlen(String2);
        UtAssert_Log("String2: used strlen() to determine end: EndPtr2=%p strlen=%zu", (const void *)EndPtr2,
                     (String2 != NULL) ? strlen(String2) : 0);
    }
    else
    {
        EndPtr2 = memchr(String2, 0, String2Max);
        UtAssert_Log("String2: used memchr() to determine end: EndPtr2=%p", (const void *)EndPtr2);
    }

    if (EndPtr2 != NULL)
    {
        FormatLen2 = EndPtr2 - String2;
    }
    else
    {
        FormatLen2 = String2Max;
    }

    /* Log computed end information and how null-termination was determined */
    {
        bool NullTermByFlag1 = (String1 != NULL && String1Max == UTASSERT_STRINGBUF_NULL_TERM);
        bool NullTermByMemchr1 = (String1 != NULL && String1Max != UTASSERT_STRINGBUF_NULL_TERM && EndPtr1 != NULL);
        bool NullTerm1 = NullTermByFlag1 || NullTermByMemchr1;

        bool NullTermByFlag2 = (String2 != NULL && String2Max == UTASSERT_STRINGBUF_NULL_TERM);
        bool NullTermByMemchr2 = (String2 != NULL && String2Max != UTASSERT_STRINGBUF_NULL_TERM && EndPtr2 != NULL);
        bool NullTerm2 = NullTermByFlag2 || NullTermByMemchr2;

        UtAssert_Log("Post-end compute: EndPtr1=%p FormatLen1=%zu NullTermByFlag1=%d NullTermByMemchr1=%d", (const void *)EndPtr1,
                     FormatLen1, (int)NullTermByFlag1, (int)NullTermByMemchr1);
        UtAssert_Log("Post-end compute: EndPtr2=%p FormatLen2=%zu NullTermByFlag2=%d NullTermByMemchr2=%d", (const void *)EndPtr2,
                     FormatLen2, (int)NullTermByFlag2, (int)NullTermByMemchr2);

        UtAssert_Log("Null-termination summary: NullTerm1=%d NullTerm2=%d", (int)NullTerm1, (int)NullTerm2);
    }

    if (FormatLen1 == 0 && FormatLen2 == 0)
    {
        /* Two empty strings are considered equal */
        Compare = 0;
        UtAssert_Log("Both FormatLen1 and FormatLen2 are 0 -> Compare set to 0");
    }
    else
    {
        /* Compare actual content based on the shorter of the two strings */
        size_t memcmp_len = (FormatLen1 < FormatLen2) ? FormatLen1 : FormatLen2;

        UtAssert_Log("About to memcmp: String1=%p String2=%p memcmp_len=%zu FormatLen1=%zu FormatLen2=%zu",
                     (const void *)String1, (const void *)String2, memcmp_len, FormatLen1, FormatLen2);

        /* Preserve original behavior: compare up to the shorter length */
        Compare = memcmp(String1, String2, memcmp_len);

        UtAssert_Log("memcmp returned %d", Compare);

        /* If initial content was the same, go by whichever is longer */
        if (Compare == 0)
        {
            /*
             * If String1 is longer, compare should be positive (String1 > String2)
             * If String2 is longer, compare should be negative (String1 < String2)
             * If strings are the same length, compare should be 0.
             */
            Compare = (int)(FormatLen1 - FormatLen2);
            UtAssert_Log("Initial content equal; Compare adjusted by length: FormatLen1=%zu FormatLen2=%zu => Compare=%d",
                         FormatLen1, FormatLen2, Compare);
        }
    }

    UtAssert_Log("CompareType=%d final Compare=%d", (int)CompareType, Compare);

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            Result = (Compare == 0);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            Result = (Compare != 0);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            Result = (Compare < 0);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            Result = (Compare > 0);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            Result = (Compare <= 0);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            Result = (Compare >= 0);
            break;
        default: /* should never happen */
            Result = false;
            break;
    }

    /* Now make "safe" copies of the strings */
    /* Check for a newline within the string, and if present, end the string there instead */
    if (FormatLen1 > 0)
    {
        EndPtr1 = memchr(String1, '\n', FormatLen1);
        if (EndPtr1 != NULL)
        {
            FormatLen1 = EndPtr1 - String1;
            UtAssert_Log("String1 newline found at %p -> new FormatLen1=%zu", (const void *)EndPtr1, FormatLen1);
        }
        memcpy(ScrubbedString1, String1, FormatLen1);
    }
    ScrubbedString1[FormatLen1] = 0;

    if (FormatLen2 > 0)
    {
        EndPtr2 = memchr(String2, '\n', FormatLen2);
        if (EndPtr2 != NULL)
        {
            FormatLen2 = EndPtr2 - String2;
            UtAssert_Log("String2 newline found at %p -> new FormatLen2=%zu", (const void *)EndPtr2, FormatLen2);
        }
        memcpy(ScrubbedString2, String2, FormatLen2);
    }
    ScrubbedString2[FormatLen2] = 0;

    UtAssert_Log("ScrubbedString1='%s' ScrubbedString2='%s' Result=%d", ScrubbedString1, ScrubbedString2, (int)Result);

    return UtAssertEx(Result, UTASSERT_CASETYPE_FAILURE, File, Line, "String: '%s' == '%s'", ScrubbedString1,
                      ScrubbedString2);
}
