#include <stdio.h>
#include <stdint.h>
#include <string.h>
#include <stddef.h>

/* Probe logging helper - appends state information to /tmp/logs/2319.txt */
static void UtAssert_LogState(const char *stage,
                              const char *String1, size_t String1Max,
                              const char *String2, size_t String2Max,
                              const char *EndPtr1, const char *EndPtr2,
                              size_t FormatLen1, size_t FormatLen2,
                              int Compare)
{
    FILE *f = fopen("/tmp/logs/2319.txt", "a");
    if (f == NULL)
    {
        return;
    }

    fprintf(f, "--- UtAssert_StringBufCompare LOG: %s ---\n", stage);
    fprintf(f, "String1 ptr: %p\n", (void *)String1);
    fprintf(f, "String1Max: %zu\n", String1Max);
    fprintf(f, "EndPtr1: %p\n", (void *)EndPtr1);
    fprintf(f, "FormatLen1: %zu\n", FormatLen1);

    /* Note potential unsafe memchr situations without actually doing them */
    if (String1 == NULL && FormatLen1 > 0)
    {
        fprintf(f, "POTENTIAL VIOLATION: memchr(String1, '\\n', FormatLen1) would be called with NULL and non-zero len (%zu)\n", FormatLen1);
    }
    if (String1 != NULL && String1Max > 0 && EndPtr1 == NULL)
    {
        /* We cannot determine actual buffer size here; log that memchr might have read up to String1Max bytes */
        fprintf(f, "NOTE: initial memchr(String1, 0, String1Max) returned NULL; memchr may have scanned up to String1Max (%zu) bytes\n", String1Max);
    }

    fprintf(f, "String2 ptr: %p\n", (void *)String2);
    fprintf(f, "String2Max: %zu\n", String2Max);
    fprintf(f, "EndPtr2: %p\n", (void *)EndPtr2);
    fprintf(f, "FormatLen2: %zu\n", FormatLen2);

    if (String2 == NULL && FormatLen2 > 0)
    {
        fprintf(f, "POTENTIAL VIOLATION: memchr(String2, '\\n', FormatLen2) would be called with NULL and non-zero len (%zu)\n", FormatLen2);
    }

    fprintf(f, "Compare interim value: %d\n", Compare);
    fprintf(f, "--- end log ---\n\n");
    fclose(f);
}

bool UtAssert_StringBufCompare(const char *String1, size_t String1Max, const char *String2, size_t String2Max,
                               UtAssert_Compare_t CompareType, const char *File, uint32 Line)
{
    char        ScrubbedString1[256];
    char        ScrubbedString2[256];
    const char *EndPtr1;
    const char *EndPtr2;
    size_t      FormatLen1;
    size_t      FormatLen2;
    bool        Result;
    int         Compare;

    /* Locate the actual end of both strings */
    if (String1 == NULL)
    {
        EndPtr1 = NULL;
    }
    else if (String1Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        /*
         * NOTE: it is technically undefined behavior to pass a size to memchr()
         * that is larger than the actual buffer, even if it is known/guaranteed
         * to find a match within the actual buffer.  Therefore the regular strlen()
         * is used instead.
         */
        EndPtr1 = String1 + strlen(String1);
    }
    else
    {
        EndPtr1 = memchr(String1, 0, String1Max);
    }

    if (EndPtr1 != NULL)
    {
        FormatLen1 = EndPtr1 - String1;
    }
    else
    {
        FormatLen1 = String1Max;
    }

    /* Log state after determining FormatLen1 */
    UtAssert_LogState("after FormatLen1 computation", String1, String1Max, String2, String2Max, EndPtr1, NULL, FormatLen1, 0);

    if (String2 == NULL)
    {
        EndPtr2 = NULL;
    }
    else if (String2Max == UTASSERT_STRINGBUF_NULL_TERM)
    {
        EndPtr2 = String2 + strlen(String2);
    }
    else
    {
        EndPtr2 = memchr(String2, 0, String2Max);
    }

    if (EndPtr2 != NULL)
    {
        FormatLen2 = EndPtr2 - String2;
    }
    else
    {
        FormatLen2 = String2Max;
    }

    /* Log state after determining FormatLen2 */
    UtAssert_LogState("after FormatLen2 computation", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2);

    if (FormatLen1 == 0 && FormatLen2 == 0)
    {
        /* Two empty strings are considered equal */
        Compare = 0;
    }
    else
    {
        /* Compare actual content based on the shorter of the two strings */
        if (FormatLen1 < FormatLen2)
        {
            Compare = memcmp(String1, String2, FormatLen1);
        }
        else
        {
            Compare = memcmp(String1, String2, FormatLen2);
        }

        /* If initial content was the same, go by whichever is longer */
        if (Compare == 0)
        {
            /*
             * If String1 is longer, compare should be positive (String1 > String2)
             * If String2 is longer, compare should be negative (String1 < String2)
             * If strings are the same length, compare should be 0.
             */
            Compare = FormatLen1 - FormatLen2;
        }
    }

    /* Log compare result before scrubbing/copy */
    UtAssert_LogState("before scrubbing and newline check", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2, Compare);

    switch (CompareType)
    {
        case UtAssert_Compare_EQ: /* actual equals reference value */
            Result = (Compare == 0);
            break;
        case UtAssert_Compare_NEQ: /* actual does not non equal reference value */
            Result = (Compare != 0);
            break;
        case UtAssert_Compare_LT: /* actual less than reference (exclusive) */
            Result = (Compare < 0);
            break;
        case UtAssert_Compare_GT: /* actual greater than reference (exclusive)  */
            Result = (Compare > 0);
            break;
        case UtAssert_Compare_LTEQ: /* actual less than or equal to reference (inclusive) */
            Result = (Compare <= 0);
            break;
        case UtAssert_Compare_GTEQ: /* actual greater than reference (inclusive) */
            Result = (Compare >= 0);
            break;
        default: /* should never happen */
            Result = false;
            break;
    }

    /* Now make "safe" copies of the strings */
    /* Check for a newline within the string, and if present, end the string there instead */
    if (FormatLen1 > 0)
    {
        /* Log right before potential unsafe memchr for newline */
        UtAssert_LogState("before memchr newline check for String1", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2, Compare);

        EndPtr1 = memchr(String1, '\n', FormatLen1);
        if (EndPtr1 != NULL)
        {
            FormatLen1 = EndPtr1 - String1;
        }
        memcpy(ScrubbedString1, String1, FormatLen1);
    }
    ScrubbedString1[FormatLen1] = 0;

    if (FormatLen2 > 0)
    {
        /* Log right before potential unsafe memchr for newline */
        UtAssert_LogState("before memchr newline check for String2", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2, Compare);

        EndPtr2 = memchr(String2, '\n', FormatLen2);
        if (EndPtr2 != NULL)
        {
            FormatLen2 = EndPtr2 - String2;
        }
        memcpy(ScrubbedString2, String2, FormatLen2);
    }
    ScrubbedString2[FormatLen2] = 0;

    /* Final log before returning */
    UtAssert_LogState("final", String1, String1Max, String2, String2Max, EndPtr1, EndPtr2, FormatLen1, FormatLen2, Compare);

    return UtAssertEx(Result, UTASSERT_CASETYPE_FAILURE, File, Line, "String: '%s' == '%s'", ScrubbedString1,
                      ScrubbedString2);
}
