#include <stdio.h>
#include <stdarg.h>
#include <string.h>

/* Probe logging helper: append human-readable trace to /tmp/logs/2684.txt */
static void UtPrintx_ProbeLog(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/2684.txt", "a");
    if (f == NULL) {
        return;
    }
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

void UtPrintx(const void *Memory, uint32 Length)
{
    uint32       i;
    uint32       j;
    const uint8 *Byte_ptr = Memory;
    char         OutputLine[80];
    char *       OutPtr;

    /* Log entry to function with initial Length */
    UtPrintx_ProbeLog("UtPrintx: enter Length=%u", (unsigned)Length);

    i = 0;
    while (i < Length)
    {
        UtPrintx_ProbeLog("While-loop: top i=%u Length=%u", (unsigned)i, (unsigned)Length);

        snprintf(OutputLine, sizeof(OutputLine), "%16lx: ", (unsigned long)&Byte_ptr[i]);
        OutPtr = OutputLine;
        OutPtr += strlen(OutputLine);

        UtPrintx_ProbeLog("About to enter for-loop: initial i=%u", (unsigned)i);

        for (j = 0; j < 16 && i < Length; j++, i++)
        {
            /* Log the condition operands and current counters at the start of each iteration. */
            UtPrintx_ProbeLog("For-iter start: j=%u i=%u (j<16=%d i<Length=%d)",
                              (unsigned)j, (unsigned)i, (int)(j < 16), (int)(i < Length));

            sprintf(OutPtr, "%02X  ", Byte_ptr[i]);
            OutPtr += 3;

            /* Log state at end of body (this is before the for-loop's third-clause increments are applied). */
            UtPrintx_ProbeLog("For-iter end-of-body (before post-increment): j=%u i=%u", (unsigned)j, (unsigned)i);
        }

        /* Log state after the for-loop finishes (shows values after the post-increment effects). */
        UtPrintx_ProbeLog("After for-loop: i=%u j=%u", (unsigned)i, (unsigned)j);

        UT_BSP_DoText(UTASSERT_CASETYPE_INFO, OutputLine);
    }

    UtPrintx_ProbeLog("UtPrintx: exit");
}
