static void *
run_usock (void *cls)
{
  struct MyData *md = cls;
  struct MHD_UpgradeResponseHandle *urh = md->urh;
  char buf[128];
  ssize_t got;

  make_blocking (md->sock);
  /* start by sending extra data MHD may have already read, if any */
  if (0 != md->extra_in_size)
  {
    send_all (md->sock,
              md->extra_in,
              md->extra_in_size);
    free (md->extra_in);
  }
  /* now echo in a loop */
  while (1)
  {
    got = recv (md->sock,
                buf,
                sizeof (buf),
                0);
    if (0 >= got)
      break;
    send_all (md->sock,
              buf,
              (size_t) got);
  }
  free (md);
  MHD_upgrade_action (urh,
                      MHD_UPGRADE_ACTION_CLOSE);
  return NULL;
}
