static enum MHD_Result
can_compress (struct MHD_Connection *con)
{
  const char *ae;
  const char *de;

  ae = MHD_lookup_connection_value (con,
                                    MHD_HEADER_KIND,
                                    MHD_HTTP_HEADER_ACCEPT_ENCODING);
  if (NULL == ae)
    return MHD_NO;
  if (0 == strcmp (ae,
                   "*"))
    return MHD_YES;
  de = strstr (ae,
               "deflate");
  if (NULL == de)
    return MHD_NO;
  if (((de == ae) ||
       (de[-1] == ',') ||
       (de[-1] == ' ')) &&
      ((de[strlen ("deflate")] == '\0') ||
       (de[strlen ("deflate")] == ',') ||
       (de[strlen ("deflate")] == ';')))
    return MHD_YES;
  return MHD_NO;
}
