  enum MHD_Result ret;
  int flush;
  void *tmp_dest;
  *dest = NULL;
  *dest_size = 0;
  do
  {
    if (src_size > CHUNK)
    {
      strm->avail_in = CHUNK;
      src_size -= CHUNK;
      flush = Z_NO_FLUSH;
    }
    else
    {
      strm->avail_in = (uInt) src_size;
      flush = Z_SYNC_FLUSH;
    }
    *offset += strm->avail_in;
    strm->next_in = (const Bytef *) src;
    do
