void metal_default_log_handler(enum metal_log_level level,
                               const char *format, ...)
{
#ifdef DEFAULT_LOGGER_ON
    char msg[1024];
    va_list args;
    static const char * const level_strs[] = {
        "metal: emergency: ",
        "metal: alert:     ",
        "metal: critical:  ",
        "metal: error:     ",
        "metal: warning:   ",
        "metal: notice:    ",
        "metal: info:      ",
        "metal: debug:     ",
    };

    /* Probe: open log file to record control flow and state for analysis */
    FILE *probe = fopen("/tmp/logs/197.txt", "a");
    if (probe != NULL)
    {
        fprintf(probe, "ENTER metal_default_log_handler: level=%d, format=%p\n", (int)level, (const void *)format);
    }

    va_start(args, format);
    vsnprintf(msg, sizeof(msg), format, args);
    va_end(args);

    if (probe != NULL)
    {
        /* Log the formatted message (trimmed by buffer) and its apparent size */
        fprintf(probe, "formatted msg='%s'\n", msg);
    }

    /* Record original value of the parameter so we can detect modification */
    enum metal_log_level orig_level = level;

    if (level <= METAL_LOG_EMERGENCY || level > METAL_LOG_DEBUG)
        level = METAL_LOG_EMERGENCY;

    if (probe != NULL)
    {
        if (level != orig_level)
        {
            fprintf(probe, "level MODIFIED: original=%d -> new=%d\n", (int)orig_level, (int)level);
        }
        else
        {
            fprintf(probe, "level UNCHANGED: %d\n", (int)level);
        }
    }

    fprintf(stderr, "%s%s", level_strs[level], msg);

    if (probe != NULL)
    {
        fprintf(probe, "EXIT metal_default_log_handler\n\n");
        fclose(probe);
    }
#else
    (void)level;
    (void)format;
#endif
}
